css:
@-moz-document regexp(".*redmine.*") {
div#content .context-menu-selection {
    background-color: #a3685a !important;
}
select[multiple="multiple"] {
    background-color: #373b41;
    color: #e0e0e0;
}
#ajax-modal p.buttons input:hover,
.ui-datepicker .ui-datepicker-buttonpane button.ui-state-hover,
input[type="submit"][name="continue"]:hover,
input[type="button"]:hover,
.details input[type="submit"]:hover,
input[type="submit"].small:hover,
input[type="submit"].button-small:hover {
    background: #1d1f21;
    color: #ffffff;
}
#ajax-modal p.buttons input,
.ui-datepicker .ui-datepicker-buttonpane button,
.details input[type="submit"],
input[type="button"],
input[type="submit"],
input[type="submit"][name="continue"],
input[type="submit"].small,
input[type="submit"].button-small {
    background: #373b41;
}
/* Body Text */

body {
    color: #e0e0e0;
}
/* Header */

#content h1,
h2,
h3,
h4 {
    color: #418052;
}
input[type="text"],
input[type="password"],
fieldset#filters td.values input {
    background-color: #373b41;
    border: 1px solid #969896;
    color: #e0e0e0;
}
input[type="text"]:focus,
input[type="password"]:focus,
select:focus,
textarea:focus,
fieldset#filters td.values input:focus {
    border-color: #81a2be;
}
select {
    background-color: #373b41;
    background: none;
    color: #e0e0e0;
    border: 1px solid #969896;
}
select option {
    background-color: #373b41;
    color: #e0e0e0;
}
/* Main Background */

html {
    background-color: #2f2f2f;
}
#wrapper {
    background-color: #2f2f2f;
    background-image: none;
}
#content {
    background-color: #282a2e;
    border-right: none;
}
#content > h2 {
    background-image: none;
    color: #000;
}
a,
a:link,
a:visited {
    color: #de935f;
}
#footer {
    /*background-color: #282a2e;*/
    
    border-top: none;
    display: none;
}
#top-menu a {
    color: #8abeb7;
}
p.other-formats {
    color: #969896;
}
/* Main Header */

#header {
    background-color: #373b41;
    color: #c5c8c6;
}
#main-menu li > a {
    /*color: #b5bd68;*/
    
    color: #000;
}
div#content > h2 {
    background-image: none;
    background-color: #445;
    color: #ababab;
    border: black;
    text-shadow: none;
}
/* Sidebar */

#sidebar h3 {
    color: #418052
}
#sidebar {
    color: #e0e0e0;
    background-color: #282a2e;
    border-color: #464646;
}
#sidebar a.selected {
    background-color: #373b41;
    color: #f0c674;
}
/* Calendar */

table.cal thead th,
table.cal td.week-number {
    background-color: #373b41;
}
table td.odd {
    background-color: #282a2e;
}
table td.even {
    background-color: #373b41;
    background: none;
}
table.cal td.today {
    background-color: #de935f;
}
div.tooltip:hover span.tip {
    background-color: #373b41;
    color: #e0e0e0;
}
table.cal td {
    border-color: #b4b7b4;
}
table.cal {
    border-color: #b4b7b4;
}

/* Settings */

#content {
    border-color: #464646;
    color: #c1baba;
}
#content .tabs ul li {
    background: #282a2e;
}
#content .tabs ul li a.selected {
    background-color: #b294bb;
    color: #FFFFFF;
}
#content .tabs ul li a.selected:hover,
#content .tabs ul li a:hover {
    background-color: #1d1f21;
    color: #ffffff;
}
#content .tabs ul li a {
    background-color: #282a2e;
    color: #e0e0e0;
}
legend {
    color: #c5c8c6;
}
.nodata,
.warning {
    background-color: #de935f;
    border-color: #de935f;
    color: #ffffff;
}
/* Date Picker */

.ui-widget-content,
.ui-datepicker .ui-state-default {
    background: #373b41;
    border-color: #969896;
    color: #e0e0e0;
}
.ui-datepicker .ui-state-default:hover {
    background: #1d1f21;
    color: #ffffff;
}
.ui-datepicker .ui-widget-header {
    background: #282a2e;
    border-color: #969896;
}
.ui-state-hover,
.ui-widget-content .ui-state-hover,
.ui-widget-header .ui-state-hover,
.ui-state-focus,
.ui-widget-content .ui-state-focus,
.ui-widget-header .ui-state-focus {
    background: #1d1f21;
    color: #ffffff;
}
/* Activity */

div#activity dt .time {
    color: #c5c8c6;
}
div#activity dd span.description,
#search-results dd span.description {
    color: #e0e0e0;
}
}

@-moz-document regexp(".*redmine.*") {
/* Wiki */

.wiki h1,
.wiki h2,
.wiki h3 {
    color: #418052;
}
.wiki.wiki-page {
    color: #e0e0e0;
}
.wiki.wiki-page > h2 {
    border-bottom: 1px solid #418052;
}
.wiki.wiki-page > h2 {
    margin-top: 40px;
}
.wiki.wiki-page > h3 {
    margin-top: 30px;
}
.wiki.wiki-page > h4 {
    margin-top: 20px;
}
div.wiki table,
div.wiki td,
div.wiki th {
    border: 1px solid #4e4e4e;
    padding: 8px;
}
div.wiki table {
	background-color: #191919;
    color: #4c8213;
}
div.wiki ul.toc {
    background-color: #9c9c6b;
    border: 1px solid #a09e3b;
    font-size: 1em;
}
div.wiki ul.toc a {
    color: #000000;
}
#content,
#main.nosidebar #content {
    background: #373b41;
}
.jstElements button {
    background-color: #d8d8d8;
}
textarea.wiki-edit {
    background-color: #282a2e;
    border-color: #484c52;
    color: #9e9e9e;
}
code {
    color: #9e9e9e;
    /*font-weight: bold;*/
}
/* Editing */

.box {
    background: #373b41;
    color: #e0e0e0;
}
textarea {
    background: #1d1f21;
    color: #e0e0e0;
}
input[type="text"]:focus,
input[type="password"]:focus,
select:focus,
textarea:focus,
fieldset#filters td.values input:focus {
    border-color: #81a2be;
}
#ajax-modal p.buttons input[type="submit"]:hover,
.ui-datepicker .ui-datepicker-buttonpane button.ui-datepicker-close.ui-state-hover,
input[type="submit"]:hover {
    background: #8abeb7;
}
#ajax-modal p.buttons input[type="submit"],
.ui-datepicker .ui-datepicker-buttonpane button.ui-datepicker-close,
input[type="submit"] {
    background: #81a2be;
}
ul.tagit {
    background: #282a2e !important;
}
ul.tagit li.tagit-new input {
    background: #373b41 !important;
}
#attachments_fields input.filename {
    color: #e0e0e0;
}
#preview fieldset {
    background: inherit;
}
}

@-moz-document regexp(".*redmine.*") {
/* Issue Table */

table.list.issues thead th {
    background-color: #373b41;
    border-top-color: #b4b7b4;
    border-right-color: #b4b7b4;
    border-left-color: #b4b7b4;
    border-bottom-color: #b4b7b4;
}

/*default row font color*/
table.list.issues tbody tr {
	color: #bed0ec;
    border-color:#b4b7b4;
                      
}

/*style for hovered row*/
table.list.issues tbody tr:hover {
	background-color:#483d8b !important;                     
}

table.list.issues tbody td {
	border-color:#b4b7b4;                 
}

/*background color for odd and even rows*/
table.list.issues tr.odd {
    background-color:#4e5663
}
table.list.issues tr.even {
    background-color:#434b56
}

/*colors priority columns*/
table.list.issues tr.priority-3 td.priority {
    /*low*/
    /*color: rgba(181, 20, 20, 0.2);*/
}
table.list.issues tr.priority-4 td.priority {
    /*normal*/
    /*color: rgba(181, 20, 20, 0.4);*/
} 
table.list.issues tr.priority-5 td.priority {
    /*high*/
    color: rgba(255, 16, 16, 1);
} 
table.list.issues tr.priority-6 td.priority {
    /*urgent*/
    color: rgba(255, 16, 16, 1);
    font-weight: bold;
} 
table.list.issues tr.priority-7 td.priority {
    /*immediate*/
    color: black;
    background-color: #ff6666;
}


/*issue id column*/
table.list.issues tbody tr td.id a {
	color: #ffd630
}

/*issue parent task column*/
table.list.issues tbody tr td.parent a {
	color: #000000
}

/*issue subject column*/
table.list.issues tbody tr td.subject a {
	color: #ffd630
}

/*issue assigned_to column*/
table.list.issues tbody tr td.assigned_to a {
	color: #bed0ec
}

/*issue assigned_to column for task assigned to me*/
table.list.issues tbody tr.assigned-to-me td.assigned_to a {
	color: #21009a
}

}

@-moz-document regexp(".*redmine.*") {
/* Issues */

div.issue {
    padding: 10px;
    margin: 5px;
    border-color: #504e4e;
    border-radius: 5px;
    background-color: #2d2b2b;
}

table.list tr.issue a {
    color: #8abeb7;
}

table.list tr.issue .subject a {
	color: #75000e;
}
table.list tr.issue .id a {
	color: #75000e;
}
table.list tr.issue .assigned_to a {
	color: #0c53ff;
}

img.gravatar {
    background: #282a2e;
    border-color: #282a2e;
}
div.flash.notice,
#flash_notice {
    background: #cc6666;
}
div.wiki pre {
    background-color: #282a2e;
    border-color: #373b41;
}
ul.tagit {
    background: #373b41;
}

div#history > div.journal {
    border: 1px solid #504e4e;
    border-radius: 5px;
    padding: 10px;
    margin-bottom: 20px;
    margin-left: 20px;
    background-color: rgb(45, 43, 43);
}
}

@-moz-document regexp(".*redmine.*") {
/*agile board*/

table.list.issues-board tbody tr {
	background-color: #373b41;
}

table.list.issues-board tbody tr:hover {
	background-color: #373b41;
}
table.list.issues-board tbody tr:hover {
	background-color: #373b41;
}

table.list.issues-board th[data-column-id="7"] {
	display:none;
}
table.list.issues-board td.issue-status-col[data-id="7"] {
	display:none;
}

.issue-status-col[data-id="1"] > .issue-card {
    border-left: 10px solid #ffd4d4;
}
.issue-status-col[data-id="2"] > .issue-card {
    border-left: 10px solid rgba(111, 178, 197, 0.62);
}
.issue-status-col[data-id="3"] > .issue-card {
    border-left: 10px solid #7ed861;
}
.issue-status-col[data-id="4"] > .issue-card {
    border-left: 10px solid #b692da;
}
.issue-status-col[data-id="8"] > .issue-card {
    border-left: 10px solid #f7ed7c;
}
.issue-card {
    border-radius: 10px;
}
.issue-status-col[data-id="3"] > .issue-card span.hours {
   display:none;
}
.issue-status-col[data-id="3"] > .issue-card .attributes {
   display:none;
}
.issue-status-col[data-id="3"] > .issue-card em.last_comment {
   display:none;
}
.issue-status-col[data-id="3"] > .issue-card div.quick-comment {
   display:none;
}
.issue-status-col[data-id="3"] > .issue-card table.progress {
   display:none;
}
.issue-status-col[data-id="3"] > .issue-card p.poject {
   display:none;
}
.issue-status-col[data-id="3"] > .issue-card p.assigned-user {
   display:none;
}
.issue-status-col[data-id="3"] > .issue-card:hover span.hours {
	display:block;
}
.issue-status-col[data-id="3"] > .issue-card:hover .attributes {
   display:block;
}
.issue-status-col[data-id="3"] > .issue-card:hover em.last_comment {
   display:block;
}
.issue-status-col[data-id="3"] > .issue-card:hover div.quick-comment {
   display:block;
}
.issue-status-col[data-id="3"] > .issue-card:hover table.progress {
   display:block;
}
.issue-status-col[data-id="3"] > .issue-card:hover p.poject {
   display:block;
}
.issue-status-col[data-id="3"] > .issue-card:hover p.assigned-user {
   display:block;
}
