css:
@-moz-document regexp("https:\\/\\/www.indiehackers.com\\/forum\\/post\\/.*") {
.title-bar, .forum .site-footer {
    background-color: rgb(255, 102, 0);
    border-top-color: transparent !important;
} .logo__glyph {
    display: none;
} .nav__link.ember-view:not(.active) {
    color: black !important;
} .title-bar__auth-button--log-in, .title-bar__auth-button--sign-up, .title-bar__button-label {
	background-color: #f60 !important;
    border-color: #f60 !important;
    background-image: none !important;
    box-shadow: none !important;
    margin: 0 !important;
} .title-bar__auth-button--log-in:hover, .title-bar__auth-button--sign-up:hover {
    background-image: none !important;
} .forum .forum-thread {
    background-color: #f6f6ef !important;
    background-image: none !important;
} .user-avatar {
 	display: none !important;   
} .forum .forum-thread .thread__title, .thread__author-link, .thread__content {
    color: #9a9a9a !important;
} .embedded-comments .comment .comment-voter__icon {
    fill: #9a9a9a;
} .forum .thread-voter.thread-voter--upvoted, .forum .thread-voter:hover:not(.thread-voter--disabled) {
    color: inherit;
    fill: inherit;
} .forum .thread-voter:hover:not(.thread-voter--disabled):not(.thread-voter--upvoted) {
	-webkit-transform: none;
    transform: none;
} .embedded-comments .comment .comment__content a, .embedded-comments .comment .comment__content,  .embedded-comments .comment .footer__action {
	color: #000000;
} .embedded-comments .comment .footer__action {
    color: #828282;
} .embedded-comments .comment-box .comment-box__save-button {
    color: inherit;
    background-image: none;
    border: 1px solid black;
} .embedded-comments .comment-box .comment-box__save-button:hover:not([disabled]) {
    background-image: none !important;
} .embedded-comments .comment-box .comment-box__save-button:focus, .embedded-comments .comment-box .comment-box__textarea:focus {
    -webkit-box-shadow: none !important;
    box-shadow: none !important;
} .forum .forum-thread .thread__content a, .forum .forum-thread .thread__action, .embedded-comments .comment-box .comment-box__cancel-link {
    color: #000;
}
}
