css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.mediapart.fr") {
body {
    background-color: black;
    color: #9c9c9c;
}
.global-wrapper {
    background-color: black;
}
.header {
    color: white;
    background-color: black;
}
a,
.title a {
    color: #d5d2d2;
}
p {
    color: #9c9c9c;
}
.journal-bundle h1,
.journal-bundle h2,
.journal-bundle h3,
.journal-bundle h4,
.journal-bundle h5,
.journal-bundle h6 {
    color: white;
}
.club-bundle .inter-titre-gris,
.club-bundle h2.h4,
.journal-bundle .inter-titre-gris,
.journal-bundle h2.h4 {
    color: #A4AA00;
}
.sub-menu.sub-menu-journal {
    background-color: #A4AA00;
}
.mkt-banner {
    background-color: #A4AA00;
    color: white;
}
.simple-list > .title {
    padding: .5em;
    background-color: #A4AA00;
}
.keywords .title {
    padding: .5em;
    background-color: #A4AA00;
}
.tools-share a {
    color: white;
}
.blackbox {
    background: none;
    background-color: black;
}
.blackbox-inner {
    background: none;
}
.slide-show-feeds [class^="slide-show-body"] a {
    background-color: #1E1E1E;
}
.post-list .keywords .une-block ul,
.une-block .bg-highlight,
.une-block .post-list .keywords ul {
    background-color: black;
}
.multimedia-region {
    background-color: black;
}
.block-reco .head {
    background-color: black;
}
.slide-show-journal-feeds .slide-show-header {
    background-color: #939800;
    padding: .5em
}
.notice {
    background-color: black;
}
.comment.is-recommended {
    background-color: #1A1A1A
}
.read-also {
    background-color: #151515;
    padding: .5em;
}
li > a {
    color: white;
    font-weight: bold;
}
.sub-menu .pager a {
    color: white;
}
.content-comments .manage a {
    color: white;
}
.extra-header {
    background-color: black;
}
.post-event {
 	background-color: black;   
}
.post-event .event-article--cartoon-wrapper {
 	background-color: black;   
}
.post-event .event-thumb-item {
 	background-color: black;
    color: white;
}
.rllt__action-button:visited, #gsr .rllt__action-button:active, ._eMi, #sbsb_f, #sbsb_f:hover, a:link, .w, #prs a:visited, #prs a:active, .q:active, .q:visited, .kl:active, .tbotu, a.iu-card-header, a.iu-card-header:hover, a.iu-card-header:visited {
    color: white;
}
}
