css:
@-moz-document domain("4chan.org") {
    /* Background */
body {
 background: none;
 background-color: #222;
 color: #a5a5a5;
 font-size:90%;
}

/* General links */
a {
 color: #707070;
 text-decoration:none;
}
a:hover {
 color: #828282;
}
a:visited {
 color: #b7b7b7;
}

a.quotelink {
    color: #FFFFFF;
}

/* Board list */
#boardNavDesktop a {
 color: #797!important;
}
#boardNavDesktop a:visited {
 color: #a88!important;
}
#boardNavDesktop a:hover {
 color: #8f8!important;
    }

/* random other links */
.postNum a, a.quotelink, .nameBlock, .nameBlock a {
 color: #a5a5a5!important;
}


span.subject {
    color: #a4a4a4 !important;
}

/* stuff */
hr {
 border-color:#a5a5a5;
}
div.op {
  text-align:left;}
  
div.thread {
    text-align:center;
}

    .fileText {opacity: 0 !important;
        max-height: 0px !important;
        transition: .4s ease-out !important;
    }
    .file:hover .fileText,
    .file.image-expanded .fileText {
        opacity: 1 !important;
        max-height: 22px !important;
    }


/* posts */
div.reply {
  position: relative;
  top: relative;
  width: 100%;
  left: 0;
  right: 0;
  margin-left: auto;
  margin-right: auto;
 background:none!important;
 background-color: #262626!important;
 border: 1px solid #333!important;
 border-radius:1px!important;
 box-shadow:0px 0px 1px black!important;
}


.sideArrows {
 display:none;
}
.postingMode {
 font-family: Monospace;
 color:#888!important;
 background-color:#444844!important;
 border-top:1px solid #484a48!important;
 border-bottom:1px solid #484a48!important;
}
table.postForm > tbody > tr > td:first-child {
 background-color:transparent;
 border:0px;
 color:#aaa;
 vertical-align:top;
}
.postForm textarea, .postForm input, input[type="file"] {
 background-color:#444;
 border:1px solid #555!important;
 border-radius:2px;
 padding:2px;
 color:#aaa;
 box-shadow:0px 0px 6px black;
 margin:1px;
}
.globalMessage {
 color:#FFD24D!important;
}

/* Displays post number */
body{counter-reset:posts;}
div.reply{counter-increment: posts;}

div.reply div.postInfo:after{
float: right;
font-size: 10px !important;
color: white;
opacity: 0.4;
margin-left: 4px;
padding: 2px;
content: "#"counter(posts);
}

  div.reply div.reply div.postInfo:after, div.op > div.postInfo:after{display:none;}

/*Makes the comment box bigger*/
textarea{height:10em !important;width:30em !important;}

/*removes the logo*/
img.title,div.boardSubtitle{display:none !important;}

/*removes the rules and info underneath the post box*/
ul.rules{display:none !important;}

    
    .boardBanner {    /*   banner */
     position: relative;
     top: relative;
     width: 100%;
     left: 0;
     right: 0;
     margin-left: auto;
     margin-right: auto;
    box-shadow: none !important;
  }
    
    div.center,
  h1,
  hr,
  .boardTitle,
/*   .boardBanner,   
     */
  .sideArrows,
.hide-thread-button,
  .fa-plus-square-o::before,
.hide-reply-button.stub a::before,
  .hide-reply-button,
  .postingMode,
  .stylechanger,
  .webm-title,
  .txt-no,
	.txt-ctrl,hreadHideButton
{
    display: none !important;
    }
    

    #custom-board-list {
    display: inline !important;
    white-space: normal !important;
    position: relative !important;
    top: 0px !important;
    left: 106px !important;
    z-index: 0 !important;
  }
    
  #header-bar {
      background: #222 !important;
    display: inline-block;
    font-family: 'PT Sans',sans-serif;
    font-size: 14px;
    font-weight: 400;
    line-height: 1.2em;
     position: relative;
     top: relative;
     width: 100%;
     left: 0;
     right: 0;
     margin-left: auto;
     margin-right: auto;
    box-shadow: none !important;
    font-family: "Roboto", "Source Han Sans JP Regular", "Meiryo" !important;
    }
    
      #board-list a {
    padding-right: 50px !important;
    white-space: pre;
          color: #a4a4a4 !important;
    }
  

   #options.reply.dialog {
    background: #222 !important;
    color: #717171 !important;
    border: none !important;
    z-index: 5 !important;
  }

  #options.reply.dialog label {
    text-decoration: none !important;
    font-style: italic !important;
    color: #a4a4a4 !important;
  }

  #optionsbar {
    border-bottom: 1px solid #eceef0 !important;
    margin: -5px !important;
    padding: 5px !important;
  }

  .suboption-list > div:last-of-type {
    background-color: #222 !important;
  }

  #fourchanx-settings.dialog {
    background: #222 !important;
  }

  .dialog {
    background: none !important;
    color: #a4a4a4 !important;
    border: transparent !important;
  }

  .dialog a,
  .dialog label {
    text-decoration: none !important;
  }
    
  #arc-sort{
    border-bottom:1px dotted;
    cursor:pointer;
  }


  #optionsContent > input:checked + div,
  #updater:hover > div,
  #updater .move,
  #qr.dump #replies,
  .hasSubMenu.focused > .subMenu {
    display: block;
  }

  #fourchanx-settings.dialog a {
    color: #a4a4a4 !important;
  }

  #fourchanx-settings.dialog a:hover {
    color: #222 !important;
  }
  
  .yotsuba-b:root #fourchanx-settings fieldset, .yotsuba-b:root .section-main div::before {
    border-color: #a4a4a4 !important;
  }

  
  /*
    
    QUICK REPLY
    
  */

  #qr {
    border-radius: 3px !important;
  }

  #qr > form {
      max-height: calc(100vh - 75px);
      overflow-y: hidden;
      overflow-x: hidden;
    width: 300px;
  }
    
  #qr.dialog { 
    padding: 3px !important;
    position: fixed !important;
    display: inline !important;
    visibility: hidden !important;
    top: -216px !important;
    right: 8px !important;
    left: auto !important;
    bottom: auto !important;
    height: auto !important;
    width: 280px !important;
    background: #a4a4a4 !important;
    box-shadow: none !important;
    z-index: 2 !important;
    transition: all 0.5s ease !important;
    box-shadow: 0 5px 11px 0 rgba(0, 0, 0, 0.18), 0 4px 15px 0 rgba(0, 0, 0, 0.15) !important;
  }

  #qr.dialog.has-captcha:not([Hidden]),
  #qr.dialog:not([Hidden]) {
    visibility: visible !important;
    top: 72px !important;
  }

  #qr.dialog.has-captcha {
    top:-298px !important;
    transition: all 0.9s ease;
  }

    #qr .move {
    position: fixed !important;
    float: right !important;
    left: 0px !important;
    bottom: -2px !important;
    margin-top: 0px !important;
    font-size: 0px !important;
    color: transparent !important;
    background-color: transparent !important;
    height: 25px !important;
    max-width: 15px !important;
    min-width: 15px !important;
    cursor: default !important;
  }

  #qr > div {
    max-width: 600px !important;
    width: 600px !important;
  }

  textarea.field {
    height: 20em !important;
    margin-bottom: 0px !important;
  }

  .field {
    border: none !important;
  }

  .field:hover {
    box-shadow: none !important;
  }
    
  .field:focus,
  input:focus,
  input[type="text"]:focus, 
  input[type="password"]:focus, 
  input:focus:not([type]), 
  textarea:focus{
    color:#a4a4a4;
    box-shadow: none !important;
    border-color: #a4a4a4 !important;
  }

  input:-moz-placeholder,
  textarea:-moz-placeholder {
    color: rgb(153, 153, 153) !important;
  }

  #qr-filename-container {
    font-family: "Roboto", "Source Han Sans JP Regular", "Meiryo" !important;
    box-shadow: none !important;
    border: none !important;
    margin-top: 2px !important;
    overflow: hidden !important;
    max-height: 20px !important;
    min-width: 60.3% !important;
    max-width: 60.3% !important;
  }

  #qr.has-spoiler .has-file #qr-filename-container {
    min-width: 74% !important;
    max-width: 74% !important;
  }

  #qr-spoiler-label {
    max-height: 18px !important;
    min-height: 18px !important;
    margin-top: 1px !important;
    margin-left: -13.1px !important;
    background: #222 !important;
  }

  #qr-spoiler-label input {
    top: 2px !important;
  }

  #qr-filename {
    min-width: 86.4% !important;
    max-width: 86.4% !important;
    text-overflow: ellipsis !important;
  }

  #file-n-submit {
    background-color: #222 !important;
    height: 20px !important;
    margin-top: -1px !important;
    border: none !important;
  }

  #file-n-submit input {
    font-family: "Roboto", "Source Han Sans JP Regular", "Meiryo" !important;
    width: 25.9% !important;
    border-radius: 0px;
  }

  #file-n-submit.has-file input {
    font-family: "Roboto", "Source Han Sans JP Regular", "Meiryo" !important;
    margin-left: -1.5px !important;
    width: 26.4% !important;
  }

  #qr-filerm {
    color: #222 !important;
    bottom: 2px !important;
    right: 10px !important;
  }

  #qr [type=file] [type=text]:focus {
    border: 1px solid grey !important; 
  }

  textarea,
  .field,
  #qr [type=file] [type=text] {
    -moz-appearance: none !important;
    background-color: #222 !important;
    font-size: 12px !important;
    vertical-align: middle !important;
  }

  select,
  textarea:not(:focus),
  .field:not(:focus) {
    box-shadow: none;
    background: #222 !important;
  }

  #qr [type=file] [type=text]:not(:focus) {
    background: #222 !important;
  }

  #qr [type=file] [type=text] {
    float: right !important;
  }

  #dump-button:hover,
  input[type="submit"]:hover,
  input[type="submit"]:active,
  input[type="submit"].active,
  input[type="submit"].disabled,
  input[type="submit"][disabled] {
    background-color: #fafafa !important;
  }

  input[type="submit"] {
    margin-top: 1px !important;
    background: #222 !important;
    text-align: center !important;
    margin-left: -1px !important;
    padding-bottom:1px!important;
    width: 25.2% !important;
    cursor: pointer !important;
    background-repeat: repeat-x !important;
    border: none !important;
    border-top: none !important;
    -moz-border-top-colors: none !important;
    -moz-border-right-colors: none !important;
    -moz-border-bottom-colors: none !important;
    -moz-border-left-colors: none !important;
    border-image: none !important;
    -moz-appearance: none !important;
    box-shadow: none !important;
  }

  input[type="submit"] {
    border-top:none!important;
    border: none !important;
  }

  input[name="name"],
  input[name="email"],
  input[name="sub"] {
    border-top: none !important;
    max-height: 24px !important;
    min-height: 24px !important;
  }

  input[name="sub"] {
    border-right: 1px solid #dedede !important;
  }

  .persona {
    max-height: 20px !important;
    min-height: 20px !important;
    margin-bottom: 4px !important;
  }

  #replies > div {
    border-top: 1px solid rgba(0, 0, 0, .094) !important;
    border-left: 1px solid #dedede !important;
    border-right: 1px solid #dedede !important;
  }

  #replies > div .thumbnail {
    background-color: rgba(160,160,160,.5) !important;
  }

  #dump-list-container {
    margin-bottom: -1px !important;
    background: #222 !important;
    border-left: 1px solid #dedede !important;
    border-right: 1px solid #dedede !important;
    box-shadow: 0px 1px 1px rgba(0, 0, 0, .095) inset;
  }

  #add-post {
    color: #a4a4a4 !important;
  }

  .qr-preview {
    margin-bottom: 1px !important;
  }
  
  #url-button, #paste-area, #dump-button {
    color: #a4a4a4 !important;
  }
  
  #url-button:hover, #paste-area:hover, #dump-button:hover {
    color: var(--accent-color) !important;
  }
  
  .party-hat {
    left: 375px !important;
    z-index: 1 !important;
  }
  
  

  
  
  
   /*
    
      MENU BUTTON
    
  */

  .postContainer .menu-button {
    float: right !important;
    margin-right: 8px !important;
/*     margin-top: 5px !important; */
    font-size: 0 !important;
  }

  .menu-button::after {
    width: 50px !important;
    height: 20px !important;
  }

  .post.op .menu-button,
  .postContainer .menu-button {
    opacity: 0 !important;
  }

  .post.op:hover .menu-button,
  .postContainer:hover .menu-button {
    opacity: 1 !important;
  }

  .postContainer .menu-button:hover::after {
    color: #6482a1 !important;
  } 
   
  .menu-button:hover::after {
    color: #666 !important;
  }
   
   
  #menu.reply.dialog {
    display: block !important;
    margin-left: 0 !important;
    margin-top: -1px !important;
    border: 1px solid #ccc !important;
  }
   
  .subMenu {
    left: -105px !important;
    width: 100px !important;
  }  

  .entry,
  .entry a,
  .subMenu {
    background: #ffffff !important;
    color: #999 !important;
    font-size: 12pt !important;
    border:none!important;
  }

  .entry:hover,
  .entry a:hover {
    color: #000000 !important;
  }
  
  .stub {
    padding: 16px !important;
  } 
  
  .show-reply-button {
    font-size: 12pt !important;
    font-weight: 800 !important;
    margin-left: 6px !important;
  }
  
  .show-reply-button:after {
    font-size: 12pt !important;
    font-weight: 400 !important;
    margin-left: 8px !important;
    color: #a8a8a8 !important;
    content: "post has been hidden" !important;
  }
  
}
