css:
@-moz-document domain("frystcraft.com"), domain("papocraft.com") {
* {
	border-radius: 0 !important;
}

div#wrap > div.jumbotron.no-margin-bottom.no-padding-bottom > div.container.text-center {
    background-color: rgba(0,0,0,0);
}

body#phpbb.notouch.section-index.ltr.hasjs.pace-done, body#phpbb.notouch.section-viewforum.ltr.logged-in.hasjs.pace-done {
	background-color: black;
    background-image: url('http://i.imgur.com/kX89aXK.png');
}

div.page-header > p {
    color: #eee;
}

div.jumbotron.no-margin-bottom.no-padding-bottom, div.jumbotron.no-margin-bottom.no-padding-bottom>div.container.text-center {
    background-color: rgba(0,0,0,0);
}

span.imageset.site_logo {
    margin-top: 22px;
    background-size: 600px;
    padding-top: 183px;
    padding-left: 600px;
}

ol.breadcrumb.no-margin-bottom {
	background-image: linear-gradient(-225deg, #f9f9f9 0%, #d7eeff 100%);
}

div.container-fluid, div#phpbb_announcement {
	background-image: linear-gradient(-225deg, #26abfb 0%, #4da3ea 48%, #2695bf 100%);
}

thead.topiclist {
    background-color: #d4d4d4;
}

tbody.topiclist.forums, tr.t-row.clickable  {
    background-color: #f9f9f9;
}
}
