@-moz-document domain("pixiv.net") {

    /* COLORS */

    /** DESCRIPTION : set the colors **/
	:root {
		--body-bg: #ECEFF1 !important;
		--card-bg: #FFF !important;
		--card-bg-2: #F9F9F9 !important;
		--card-bg-op: rgba(240, 241, 245, .8) !important;
		--border-color: #DBDCE6 !important;
		--primary-color: #333 !important;
		--secondary-color: #7E7E7E !important;
		--invert: invert(0) !important;
	}
	
	/* GENERAL */

	/** DESCRIPTION : set the main font and change others elements for every page **/
	html {
		overflow-x: hidden !important;
	}

	*:not(i) {
		font-family: "Roboto", "ぼくたちのゴシック２ボールド", "BM YEONSUNG" !important;		

		/* font-family: "Roboto", "Microsoft YaHei" !important; [CHINESE] */

		/* font-family: "Roboto", "BM YEONSUNG" !important; [KOREAN] */
	}

	*::-moz-focus-inner {
		border: 0;
	}

	select {
		-webkit-appearance: none;
		-moz-appearance: none;
		text-indent: 0px;
		text-overflow: ellipsis;
		white-space: nowrap;
	}

	textarea::-moz-placeholder {
		color: var(--primary-color) !important;
	}

	:root,
	body {
		background: var(--body-bg) !important;
		color: var(--primary-color);
	}

	#wrapper {

		/*** MAIN CONTAINER ***/
		width: 1050px !important;
		margin-top: 50px !important;
	}

	.bookmark-count {

		/*** BOOKMARK COUNTER BADGE ***/
		font-family: "Roboto" !important;
	}

	#modal-messages {

