	.page-count {

		/*** THUMBNAILS PAGE COUNTER ***/
		background: #3066BE !important;
		border: none !important;
		border-radius: 2px !important;
		box-shadow: none !important;
		font-size: 11pt !important;
		font-stretch: condensed !important;
