css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix(http://tvlistings.zap2it.com) {

/* Un-comment this to remove the top bar */
/*
#zc-topbar{display:none !important;}
*/

#aff_wrapper,
#zc-wrapper,
#zc-wrapper-inner,
#zc-topbar,
#zc-topbar-features,
#zc-shell,
.zc-grid #zc-item table
{width:100% !important;}

.zc-program-detail
{width:80% !important;}

.zc-program-times
{float:left !important;
 margin-left:10% !important;
 width:90% !important;}
.zc-program-times div
{width:15% !important;}

.zc-station
{width:5% !important;}

.zc-episode-detail
{width:50% !important;}

#aff_header_inner,#aff_footer,
#zc-topbar-leaderboard,#zc-footer,
.zc-grid-ad,.zc-adspace,#zc-bar,iframe,
#aff_rightbar
{display:none !important;}
}
