css:
@-moz-document domain("trello.com") {
/* Open card */

.window {
    margin: 2pc 0;
    width: calc(100% - 4pc);
    min-height: calc(100% - 2pc);
    overflow: visible;
    max-width: 1440px;
}
.window-header {
    width: calc(40% - 40px);
}
.window-title {
    padding-bottom: 0.5pc;
}
textarea.mod-card-back-title,
.card-detail-title-assist .js-title-helper {
    font-size: 20px;
    line-height: 26px;
    font-weight: 600;
}
.window-sidebar {
    width: 136px;
}
.window-main-col {
    width: 40%;
    display: block;
}
.card-detail-data {
    width: calc(100% - 40px);
}
.window-main-col .window-module.checklist-list {
    position: fixed;
    display: block;
    width: calc(60% - 260px);
    left: calc(40% + 2pc);
    overflow-y: scroll;
    height: calc(100% - 4pc);
    top: 0;
    padding: 2.8pc 1pc;
    background-color: #fafafa;
    box-shadow: 0 1px 3px rgba(0, 0, 0, .33);
    max-width: 636px;
}
}

@-moz-document domain("trello.com") {
/* —— Board settings —— */

.list {
    background: transparent;
}
.list-cards {
    padding: 0 8px;
}
.list-wrapper {
    margin: 0;
    width: 280px;
}
.board-main-content {
    background-color: #EBF0F2;
}

/* Cards on boards */

.card-label.mod-card-front {
    height: 3px;
    max-width: 24px;
    min-width: 24px;
}
.list-card {
    /*border: 1px solid #F1F2F4;*/
    
    border: none;
    border-radius: 3px;
    /*box-shadow: 0 1px 1px 0 rgba(0, 0, 30, .4);*/
    margin-bottom: 8px;
    transition: box-shadow 0.2s ease-in;
}
.list-card:hover {
    background-color: #fff;
    box-shadow: 0 4px 8px 0 rgba(0, 0, 30, .24);
    transition: box-shadow 0.2s ease-out;
}
.list-card-title {
    font-family: Roboto, -apple-system , sans-serif;
}
.list-card-details {
    padding: 6px 6px 2px 10px;
}
.list-card-labels {
    margin: 3px 0 6px 0;
}
.open-card-composer {
    color: #c8ccd4;
}

.open-card-composer:hover {
    background-color: transparent;
    color: #4d4d4d;
    text-decoration: none;
}

/* ————————  */
#board::-webkit-scrollbar {
    height: 3px;
}
.u-fancy-scrollbar::-webkit-scrollbar {
    left: 3px;
    width: 3px;
}
}
