css:
@-moz-document domain("kissanime.ru") {
:root 
    /*User colors*/
    {
                                            
    --main-color: #75b904;
    --main-background: #000000;
    --second-background: #181818;
    --third-background: #d6d6d6;
    --latest-episode: #f74242;
    --main-text: #3A3A3A;
    --dimer-text: #CCCCCC;
    --highlight: #176fb7;
    --nav-bar: #70b203;
    --new-text: #ffffff;
    --not-visited: #bcff75;
}
/*Changed text highlight*/

::selection {
    background: var(--highlight) !important;
    color: var(-main-text) !important;
}
::-moz-selection {
    background: var(--main-text) !important;
    color: var(--main-text) !important;
}
table.listing,
table.listing td a.episodeVisited,
a#PinnedManagerText,
a#btnBookmarkManager {
    color: var(--main-color) !important;
}
.barContent div p a,
.barContent a.bigChar {
    color: var(--main-color) !important;
}
table.listing th {
    color: var(--new-text) !important;
}
.barContent div p a.dotUnder {
    color: var(--new-text) !important;
}
/*Outline for links*/

a {
    outline-color: var(--main-color) !important;
}
html,
body {
    background: var(--main-background) !important;
    color: var(--main-background) !important;
}
.listing td a {
    color: var(--not-visited) !important;
}
/*Login*/

#topHolderBox {
    color: var(--main-text) !important;
    background: var(--second-background) !important;
}
#topHolderBox > img:nth-child(1) {
    display: none;
}
#topHolderBox > a {
    color: var(--dimer-text) !important;
    opacity: .9;
}
#topHolderBox > a:hover {
    opacity: 1 !important;
}
#head > h3:nth-child(1) > div:nth-child(1) {
    margin: -30px 132px -20px 46px !important;
}
/*Navbar*/

#navbar {
    background: transparent !important;
}
#navcontainer li a#currentTab {
    background: var(--main-color) !important;
}
#navcontainer a:link,
#navcontainer a:visited {
    background: var(--third-background) !important;
}
#navcontainer a:link,
#navcontainer a:visited,
#navsubbar p a {
    color: var(--main-background) !important;
}
#navcontainer li a#currentTab:hover {
    color: var(--dimer-text)
}
#navcontainer li a#currentTab:hover,
#navcontainer a:link:hover,
#navcontainer a:visited:hover,
#navsubbar {
    background: var(--nav-bar) !important;
}
#navbar a:link:hover,
#navbar:hover {
    background-color: var(--main-text)
}
#navsubbar p a:hover {
    color: var(--main-text) !important;
}
#navsubbar {
    margin-bottom: 15px !important;
}
#leftside {
    width: 730px !important;
    text-align: left;
    float: none !important;
}
div.efka-p,
div#malContainer,
div#malContainer > #addRemoveMALContainer > a#addRemoveMAL,
div#malContainer > #findinMALContainer > a {
    color: var(--main-color) !important;
}
/*Big recommended anime*/

.banner,
.barContent,
.bigBarContainer {
    background: var(--second-background) !important;
}
.bigChar,
.dotUnder {
    color: var(--main-color) !important;
    opacity: .9;
}
.bigChar:hover,
.dotUnder:hover .info,
p {
    color: var(--main-text) !important;
}
#divReload,
#divShowGenre > div:nth-child(2) {
    filter: grayscale(100%);
}
/*Latest update, anouncements etc*/

.barTitle,
.rightBox {
    background: var(--main-color) !important;
}
div#rightside {
    display: none !important;
}
.barTitle,
div.navi,
.barContent a {
    color: var(--main-background) !important;
}
.barTitle,
div.scrollable_title,
.barContent a {
    color: var(--main-background) !important;
}
.arrow-general {
    display: none;
}
div.items a {
    color: var(--main-color) !important;
    opacity: .9;
}
div.items a:hover,
.barContent a:hover {
    color: var(--main-color) !important;
}
.textDark,
.barContent .textDark {
    color: var(--new-text) !important;
}
.rightBox,
.barTitle {
    border-color: var(--main-text) !important;
}
.scrollable .items div.div a {
    margin-bottom: 100% !important;
}
.barContent p {
    color: var(--new-text) !important;
}
.arrow-general {
    border: 0px !important;
}
div.items {
    margin: auto !important;
    border: auto !important;
}
div.items > div > a {
    height: 170px
}
div.items > div > a {
    margin: auto 3.5px !important;
}
div.items > div > a > img {
    height: 173px
}
.scrollable {
    position: relative;
    overflow: hidden;
    width: 100%;
    height: 225px;
}
/*New and hot and stuff*/

#tabmenucontainer a:link {
    background: var(--third-background) !important;
}
#tabmenucontainer li a.tabactive {
    background: var(--main-color) !important;
    opacity: .9;
}
#tabmenucontainer li a.tabactive:hover,
#tabmenucontainer a:link:hover {
    background: var(--main-color) !important;
    opacity: .9;
}
#subcontent div a img:hover {
    border-color: var(--main-color) !important;
    opacity: .9;
}
#subcontent span.title,
#tab-trending > div > p > a,
#tab-trending > div > a,
#tab-newest > div > p > a,
#tab-mostview > div > p > a,
#tab-trending > div:nth-child(13) > a:nth-child(1):hover {
    color: var(--main-color) !important;
}
#subcontent span.title:hover,
#tab-trending > div > p > a:hover,
#tab-trending > div > a:hover,
#tab-newest > div > p > a:hover,
#tab-mostview > div > p > a:hover {
    opacity: .9 !important;
}
#tab-trending > div:nth-child(13) > a:nth-child(1) {
    color: #808080 !important;
}
#subcontent div div,
#subcontent {
    background: var(--main-background) !important;
}
#subcontent div div,
#subcontent {
    padding-right: 2px !important
}
#subcontent div div.blue {
    background: var(--main-background) !important;
}
#subcontent div div {
    background-color: transparent !important;
}
#tabmenucontainer a:link {
    color: var(--main-background) !important;
}
#leftside {
    text-align: center !important;
}
/* Pinned Box */

.barTitle,
#PinnedBoxTitle,
.welcome-box-title {
    background: var(--main-color) !important;
}
/*Are you human*/

#formVerify > div > p > span {
    color: var(--main-color) !important;
}
.imgCapSelect {
    border-color: var(--main-color) !important;
}
/*Video*/

.barContent > div:nth-child(1) > div:nth-child(11),
.barContent > div:nth-child(1) > .clear,
.barContent > div:nth-child(1) > .clear2,
#divBookmark > img:nth-child(1) {
    display: none !important;
}
.specialButton {
    background: var(--main-color) !important;
    border-color: var(--dimer-text) !important;
    color: var(--main-background) !important;
    text-shadow: none !important;
}
#containerRoot > div:nth-child(18) {
    height: 50px !important;
}
#containerRoot > div:nth-child(21) > a:nth-child(1) {
    color: var(--main-color) !important;
    opacity: .9;
}
#containerRoot > div:nth-child(21) > a:nth-child(1):hover {
    opacity: 1 !important;
}
select {
    background: #393939 !important;
    border-color: 1px solid #666666 !important;
    color: #ccc !important;
}
#footer {
    background: transparent !important;
}
label.lbl,
div#playerSwitchLightsOffContainer div {
    color: #dadada !important;
}
div#divDownload a {
    color: #d5f406 !important;
}
/*Video player*/

.vjs-default-skin .vjs-play-progress,
.vjs-default-skin .vjs-volume-level {
    background: var(--main-color) !important;
    opacity: 0.8 !important;
}
.vjs-default-skin .vjs-play-progress:hover,
.vjs-default-skin .vjs-volume-level:hover {
    background: var(--main-color) !important;
    opacity: 1 !important;
}
/*Transparency*/

.vjs-default-skin .vjs-control-bar,
.vjs-default-skin .vjs-slider {
    background: rgba(34, 34, 34, .4) !important;
}
.vjs-default-skin .vjs-slider:hover {
    background: rgba(34, 34, 34, .7) !important;
}
/*Aspect ratio change
	.video-js .vjs-tech
	{
		object-fit: fill;
	}
	*/

.vjs-default-skin .vjs-big-play-button {
    background: var(--main-background) !important;
}
.vjs-default-skin .vjs-big-play-button:hover {
    background: var(--main-color) !important;
}
/*Search results*/

.listing tr:hover td {
    color: var(--main-color) !important;
    background-color: var(--main-text) !important;
}
.listing tr.odd {
    color: var(--main-background) !important;
    background-color: var(--main-background) !important;
}
.tooltip {
    background: var(--main-background) !important;
    color: var(--main-text) !important;
    border: 1px solid var(--main-text) !important;
}
.listing tr:hover td > a {
    color: var(--new-text) !important;
}
.listing th,
div.pagination ul.pager li.current,
div.pagination ul.pager li {
    border-color: var(--main-text) !important;
}
.alphabet,
div.pagination ul.pager li.current,
div.pagination ul.pager li {
    background: var(--second-background) !important;
    color: var(--main-text) !important;
}
div.pagination ul.pager li a:hover,
div.pagination ul.pager li a:active {
    background: var(--main-text) !important;
    border-color: var(--main-text) !important;
}
#btnSubmit,
#btnReset {
    filter: grayscale(100%);
}
/*Bookmark*/

#spanBookmark > img:nth-child(1) {
    display: none;
}
/*Comments*/

div.bigBarContainer:nth-child(7) > div:nth-child(2) > div:nth-child(2) > div:nth-child(1) {
    color: var(--main-color) !important;
}
.dark .comment-policy {
    background: var(--main-background) !important;
    border-color: var(--main-text) !important;
}
/*User logged in*/

#topHolderBox > span:nth-child(2) {
    color: var(--dimer-text) !important;
}
.boxDropDown,
#menu_box a,
#menu_box {
    z-index: 99999 !important;
    color: var(--main-text) !important;
    background: var(--main-background) !important;
    border-color: var(--main-text) !important;
}
.aButton {
    background: var(--main-background) !important;
    border-color: var(--main-text) !important;
    filter: grayscale(0%) !important;
}
#menu_box a:hover,
#topHolderBox > a:hover,
.aButton:hover {
    background: var(--main-text) !important;
}
#aDropDown > span:nth-child(1) > small:nth-child(2) {
    color: var(--main-color) !important;
}
#leftside {
    width: 100%;
}

.scrollable .items div:nth-child(5) {
    margin-top: -18px !important;
}
.specialButton {
    border-radius: 0px !important;
    box-shadow: none !important;
    text-shadow: none !important;
    background: none !important;
    font-family: 'Roboto' !important;
    background-color: #42BD41 !important;
    border: none !important;
}
.specialButton:hover {
    background-color: #349433 !important;
}
/* ad/div hider start */
/*Ads*/

iframe#adsIfrme1,
iframe#adsIfrme6,
iframe#adsIfrme7,
iframe#adsIfrme8,
iframe#adsIfrme9,
iframe#adsIfrme10 {
    visibility: hidden;
    height: 0;
}
.barContent > div:only-child > div:nth-child(2),
.barContent > div:only-child > div:nth-child(3),
.barContent > div:only-child > div:nth-child(8),
.barContent > div:only-child > div:nth-child(9),
.barContent > div:only-child > div:nth-child(17),
.barContent > div:only-child > div:nth-child(20),
.barContent > div:only-child > div:nth-child(22),
.barContent > div:only-child > div:nth-child(23) {
    display: none !important;
}
#divFloatLeft,
#tab-trending > div:nth-child(1),
#divAds2,
#divAds1,
#divAds,
#rightside > div:nth-child(5),
#divFloatRight,
#adsIfrme6,
div.divCloseBut,
#adsIfrme0,
#adsIfrme1,
#adsIfrme2,
#adsIfrme3,
#adsIfrme4,
#adsIfrme5,
#adsIfrme6,
#adsIfrme7,
#adsIfrme8,
#adsIfrme10,
#videoAd,
.videoAdClose {
    display: none !important;
}
/* ad/div hider end */
/* Pinned box */

#PinnedList a {
    color: var(--main-color)
}
#pinnedboxcontent a {
    opacity: .9 !important;
}
#PinnedList {
    width: 100%;
}
div#PinnedBox .altPinnedBox {
    color: var(--main-color) !important;
}
.details a img {
    border-color: transparent;
    padding: initial;
}
.details a img:hover {
    border-color: #2BAF2B;
}
div.items img {
    border-color: transparent;
}
div.items img:hover {
    border-color: #A3E9A4;
}
.select#episode {
    background-color: #ffffff !important;
}
/* Cycle alerts */

div#cycleAlerts {
    display: none;
}
/* download font */

div#divDownload {
    color: #dadada !important;
    font-family: Roboto, Helvetica, sans-serif;
    font-size: 15px !important;
    font-weight: normal !important;
    padding-top: 15px !important;
}
/* score dropdown (kiss essentials) */

.KEDropdown {
    border-color: #000000 !important;
    border: 1px solid #666666;
}
.KETextInput {
    border-color: #000000 !important;
    background-color: #000000 !important;
    background: #393939 !important;
}
input#episodesTotalText.KETextInput {
    border-color: #000000 !important;
    background-color: #000000 !important;
    background: #393939 !important;
}
/* link colors for mal */

.bigChar:hover,
.dotUnder:hover .info,
p {
    color: var(--main-background) !important;
}
/* episodes*/

div.barContent.episodeList {
    text-align: left
}
/* Your Profile */

form#formProfile,
form#formProfile > b {
    color: #FFFFFF !important;
    }

}
