css:
@-moz-document domain("userstyles.org") {
body, #nav-view, .navigation, #footer, .see_more, .Discussions > li, #style_pledgie_id, .us-stylecard--long{
    background-color: #2E3E49 !important;
}
#header, .us-stylecard--short  {
    border-radius: 0px 0px 150px 150px/40px;
    box-shadow: 0 3px 12px 0 rgba(0, 0, 0, 0.1), 0 3px 12px 0 rgba(0, 0, 0, 0.1);
    z-index: 999 !important;
    background-color: #2E3E49 !important;
}
 .us-title > a {filter: invert(80%);}
.navigation_title, body, #share_div {
    color: #c0c5c6 !important;
    width: calc(100% - 17px) !important;
}
.navigation {overflow: hidden !important;}
#fourm_link {
right: 2% !important;
position: relative !important;
}
div.right {
    margin-right: 5% !important;
    width: 431px !important;
}
#top_android_button {
right: -25%;
position: relative;
}
#create_new_style{transform: scale(.65) !important; font-size: 25px !important;}
.header-search {padding-top: 1% !important;}
.screenshot_wrapper {
    border-bottom: none !important;
}
.screenshot_img {
    transform: scale(1.06) !important;
    transition: .25s ease-in-out !important;
}
.screenshot_img:hover {
    transform: scale(1.6) !important;
    margin-top: -68px !important;
    z-index: 9999;
    object-fit: contain !important;
    transition: .15s ease-in-out !important;
}
.style_card:hover {
    z-index: 999;
    background-color: rgb(20, 21, 22);
}
.style_card:hover > .style_info > h4 > a {
    filter: brightness(2);
}
.screenshot_img_long {
    transform: scale(1.06) !important;
    transition: .25s ease-in-out !important;
}
.screenshot_img_long:hover {
    transform: scale(2.3) !important;
    z-index: 9999;
    object-fit: contain !important;
    transition: .15s ease-in-out !important;
}
#left-arrow-carousel:hover, #right-adiv.rrow-carousel:hover {
    transform: scale(2) !important;
    margin: -13px !important;
    transition: .1s ease-in-out !important;
}
#ovals-wrapper {
    transform: scale(2, 2);
}
.oval {
    width: 8px;
    height: 8px;
    background-color: rgb(56, 72, 83);
    border-radius: 4px;
    margin-right: 10px;
}
.oval-active {
    width: 9px;
    height: 9px;
}
a, #send_feedback_button, .no-select, .navigation *, .setting_div > .title_setting  {
    color: rgb(170, 190, 230) !important;
    text-shadow: none !important;
}

.linksBox:first-child > a > div > img {
    transform: scale(1.5) !important;
}
.style_card, #send_feedback_button, .css_button, #notes_textarea, .white_button, #overlay-info, #Head {
    background-color: #576b77;
    border: none !important;
    border-bottom-left-radius: 0px;
    border-bottom-right-radius: 0px;
}
.view_details, #top_android_button {
    background-color: #576b77;
}
.view_details > span {
    filter: brightness(1.2) !important;
}
#title_long {
    filter: saturate(0%) brightness(3.6) !important;
}
.style_author_long {
    filter: brightness(2.8) !important;
}
#errorExplanatio {
    color: black !important;
}

.logo {
    transform: scale(0.63) !important;
    display: block !important;
    -moz-box-sizing: border-box !important;
    box-sizing: border-box !important;
    background: url(https://image.ibb.co/nD4Ez5/stylish_logo_2.png) no-repeat !important;
    width: 128px !important;
    height: 128px !important;
    padding-left: 280px;
    padding-bottom: 85px;
    margin-bottom: 0px;
    margin-left: -30px !important;
    margin-right: -30px !important;
    opacity: .95 !important;
}
.linksBox:nth-of-type(1) > a > div > img, .linksBox:nth-of-type(5) > a > div > img {
    filter: grayscale(100%) brightness(4);
}
