.navigation-link {
    box-shadow: none !important;
    margin-top: 4px;
    transition: 1.4s ease !important;
}
.navigation-link:hover, #advancedsettings_area  {
    border-radius: 30px 0px 150px/50px 0px;
    background-color: rgba(30, 30, 30, .7) !important;
    transition: .8s ease !important;
}
.navigation {
    transition: filter 2s ease !important;
    min-width: 190px !important;
    margin-left: 0px !important;
}

#stylish-description, #style_author, #actions_div, #headline, #information_div, #applies_link, .text_style_page, #title_div > div > h1 {
    color: #AABEE6 !important;
}
#information_div {
    border-radius: 2px 2px 90px 6px !important;
}
#information_div, #style_installed_button {
    background-color: rgba(160, 180, 190, 0.9) !important;
    border: 1px solid #606C75;
}
#ownedButtons a div, .customize_button, .customize_button > * {
    background-color: #A0B4BE !important;
    color: #030405 !important;
    border: none !important;
}
#update_style_button * {color: black !important;}
#notes_textarea {
    color: rgba(3, 4, 5, .8) !important;
}
.style_card_long, .line_seperator {
    width: 100% !important;
    box-shadow: inset 0 -1px 0 0 #606C75;
}
#nav-view-responsive, .Item.Participated.Read.ItemDiscussion, #view-responsive, #landing_page_div > *, .coming_soon_div > *, .bottom_landing_page_div *{
    background-color: rgba(50, 62, 74, 1) !important;
    color: white !important;
}
[value=Save], [type=button], #css, .CodeMirror, .CodeMirror-gutters, #style_installed_button {
    color: #3d3d3d !important;
}
#archive_style_button {
    border: solid 1px #3AABF5;
}
#view {
    width: 100% !important;
}
#view_inner, #iframe {
    width: 114% !important;
}
.logo {
    height: 50px !important;
    width: auto;
    filter: drop-shadow(-6px 10px 6px rgba(0, 20, 30, .8));
}
#preview_image_div {
    transform: scale(1.03);
}
.author-styles > tbody > tr, [valign=top], .date-value, .numeric-value {
    width: 20% !important;
}
.author-styles > tbody > tr > .numeric-value {
    font-size: 120% !important;
}
.styles > div {
    border-radius: 5px 5px 5px 5px !important;
}
[href="/post/discussion/userstyles.org"] {
    background: rgb(46, 62, 73);
}
[href="/post/discussion/userstyles.org"]:hover, .NewDiscussion {
    background: rgb(87, 107, 119);
    color: azure !important;
}
input#Form_Search.InputBox, .TextBoxWrapper, .Button.PreviewButton, textarea#style_additional_info, textarea#style_additional_info, .Button.DraftButton, .Button.Primary.CommentButton, textarea#style_long_description, div.form-controls table tbody tr td input {
    filter: invert(80%) sepia(90%) hue-rotate(150deg) !important;
}
textarea#css, .CodeMirror, input#style_short_description, #new-setting > input, #example-url, [name="style[after_screenshot]"], [name="commit"], [name="style[license]"], #style_pledgie_id, #edit-style-settings > li > input:not(.color){
    filter: invert(80%) sepia(90%) hue-rotate(150deg) saturate(95%) !important;
}
.BoxButtons.BoxNewDiscussion * {
    border: none !important;
    filter: invert(80%) sepia(90%) hue-rotate(150deg) !important;
}
.BoxButtons.BoxNewDiscussion:active *:active, .Button.PreviewButton, .Button.DraftButton {
    color: Red !important;
}
#leftFooter * {
    filter: saturate(70%);
}
.adLong, .style_card_long:nth-child(3), .style_card_long:nth-child(13), #rightFooter {
    display: none !important;
}
#account {
    margin-right: -6% !important;
    margin-left: -9% !important;
}
.coming_soon_div {
    position: absolute !important;
    left: 40% !important;
}
.phone_image_div {
    position: absolute !important;
    background: url(https://image.ibb.co/guVVG7/phone_image.png) 0px 0px !important;
    background-size: 102% !important;
    top: 15% !important;
    left: 7% !important;
    transform: scale(1) !important;
}
[src="/ui/images/phone-image.png"] {
    opacity: 0 !important;
}
.settingsIcon {filter: brightness(0) !important;}
.update-icon, .links-box:nth-child(1) > a > .navigation-link > img  {filter: invert(100%) brightness(3) saturate(0) !important;}
}

@-moz-document url-prefix("https://forum.userstyles.org/discussion") {
pre, code {background-color: rgb(40,45,48) !important; color: rgb(250,250,250) !important;}
}
