css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url("http://www.hhuc.edu.cn/") {
    /* i really want this to be global */
    * {
        background-color: transparent!important;
        border: 0 !important;
        font-size: medium !important;
    }
    body {
        background-image: url(http://chuantu.biz/t6/4/1502745486x2728301089.jpg) !important;
        background-repeat: no-repeat !important;
        background-size: 100%, 100%;
        font-family: STXinwei !important;
    }
    table {
        color: black !important;
    }
    /* tr:nth-child(1) {
		visibility:hidden !important;
	    width : 100% !important;
	    display : inline !important;
} */
    tr:nth-child(1)>td>table {
        /* 	visibility:hidden !important; */
        /*     width : 100% !important; */
        /*     display : inline !important; */
        background-color: transparent!important;
        /*     opacity: 0.2; */
        /*     color : black !important; */
    }
    tr:nth-child(2) {
        /* 	visibility:inherit !important; */
        border-color: #0DFF00 !important;
    }
    tr:nth-child(2)>td {
        /* 	visibility:inherit !important; */
        color: #0DFF00 !important;
        background-color: transparent!important;
    }
    tr:nth-child(3) {
        /*     visibility:inherit !important; */
        color: yellow !important
    }
    /* tr:nth-child(3) > td > table > tbody > tr > td:nth-child(1) > table:nth-child(1) > tbody > tr > td > table > tbody > tr:nth-child(1) > td > img
{
	visibility:hidden !important;
    
} */
    tr:nth-child(3)>td>table>tbody>tr>td:nth-child(1)>table:nth-child(1) {
        visibility: hidden !important;
    }
    tr:nth-child(3)>td>table>tbody>tr>td:nth-child(1)>table:nth-child(2) {
        visibility: hidden !important;
    }
    tr:nth-child(3)>td>table>tbody>tr>td:nth-child(2)>table:nth-child(1)>tbody>tr:nth-child(1) {
        visibility: hidden !important;
    }
    tr:nth-child(3)>td>table {
        width: 87% !important;
    }
    tr:nth-child(3)>td>table>tbody>tr>td:nth-child(1) {
        display: None !important;
    }
    tr:nth-child(3)>td>table>tbody>tr>td:nth-child(2) {
        width: 100% !important;
    }
    tr:nth-child(3)>td>table>tbody>tr>td:nth-child(2)>table:nth-child(1)>tbody>tr:nth-child(2)>td>table>tbody>tr:nth-child(3) {
        display: None !important;
    }
    tr:nth-child(3)>td>table>tbody>tr>td:nth-child(3)>table:nth-child(1)>tbody>tr:nth-child(1)>td:nth-child(1) {
        /*     display:None !important; */
        background-image: url('http://jj.cm');
    }
    tr:nth-child(3)>td>table>tbody>tr>td:nth-child(3)>table:nth-child(1)>tbody>tr:nth-child(1)>td:nth-child(2)>table>tbody>tr:nth-child(1) {
        /*     display:None !important; */
        visibility: hidden !important;
    }
    tr:nth-child(3)>td>table>tbody>tr>td:nth-child(3)>table:nth-child(2) {
        display: None !important;
    }
    tr:nth-child(3)>td>table>tbody>tr>td:nth-child(2)>table:nth-child(2) {
        visibility: hidden !important;
    }
    tr:nth-child(4) {
        visibility: hidden !important;
    }
    tr:nth-child(2) td>img {
        visibility: hidden !important;
    }
    #FlashID {
        visibility: hidden !important;
    }
}
