css:
@-moz-document domain("cybre.space") {
.column-header {
    background: #5c3566;
}
.column-header__button,
.column-header__back-button,
.column-back-button {
    background: #5c3566;
    color: #CE76E6;
}
.column-header__button.active,
.column-header__collapsible-inner {
    background: #734280;
}
.column > .scrollable,
.column-link, .detailed-status {
    background: #2E1A33;
}
.detailed-status__action-bar {
    background: #170D1A;
    border-top: 1px solid #5c3566;
    border-bottom: 1px solid #5c3566;
}

.column-link:hover {
    background: #45274D;
}
.column-subheading {
    background: #231326;
    color: #75507B;
}
.getting-started a {
    color: #CE76E6;
}
.column .status {
    border-bottom: 1px solid #5c3566;
    background: url("data:image/png;base64,") bottom left repeat-x;
}

.reply-indicator__content a, .status__content a {
    color: #e5dae8;
}

.status__relative-time,
.status__display-name,
.muted .status__content a,
.muted .status__content p,
.muted .status__display-name strong,
.status__prepend, .status__prepend .status__display-name strong,
.detailed-status__meta,
.account__disclaimer {
    color: #ad7fa8;
}

