.drawer__header {
    background: #207687;
}
.drawer__header a:hover {
    background: #248699;
}
.search__input {
    background: #185966;
}
.drawer__inner {
    background: #185966;
}
.button {
    background: #1C8195;
}
.search__input::placeholder {
    color: #72BFCF;
}
.button:active,
.button:focus,
.button:hover {
    background: #24A6C0;
}

.account__header>div {
    background: #1F364D;
    background-image: url("data:image/png;base64,"), url("data:image/png;base64,");
    background-position: bottom, top;
    background-repeat: repeat-x;
}

.account--action-button .icon-button {
    color: #294866;
}

.account--action-button .icon-button.active {
    color: #5290CC;
}

.account__header .account__header__content {
    color: #DAE1E8;
}

div.compose-form .reply-indicator__content a {
    color: #1C8195;
}

}
