css:
@-moz-document domain("nasdanq.com") {
#mainNav .moreMenu .btn, #mainNav .xNav .btn
  {
    color: #c0c0c0;
    background: #363636;
  }

  body
  {
    background: #3b3b3b;
    color: #c0c0c0;
  }

  .card
  {
    background: #1d1d1d;
  }

  h1, .scard .value h1
  {
    color: #c0c0c0;
  }

  a
  {
    color: #eee;
  }

  .drop
  {
    color: #ff1b1b!important;
  }

  .rise
  {
    color: #53d80b;
  }

  div.functions
  {
    color: #c0c0c0;
  }

  .ncard .newsOverlay, #mainNav .moreMenu, #mainNav .xNav
  {
    background: #1d1d1d;
    box-shadow: 0 0px 8px 3px rgba(0, 0, 0, 0.75);
  }

  #mainNav .moreMenu #logo a, #mainNav .xNav #logo a
  {
    color: #53d80b;
    text-shadow: 0px 0px 20px black;
  }

  .dBorder, .rBorder, .bMedium
  {
    border-color: #1d1d1d;
  }

  input#_query
  {
    background-color: #1d1d1d;
    border: none!important;
  }

  div[contenteditable=true].inputValid, input.inputValid, input[type=password]:focus, input[type=text]:focus, textarea.inputValid, textarea:focus
  {
    box-shadow: none!important;
  }

  h2
  {
    font-size: 1.34999rem;
  }

  .homeWrapper .search #mainSearch .btn
  {
    height: 91%;
    background-color: #0a0a0a;
  }

  a:hover
  {
    color: #00d8ff;
  }

  a, a:hover
  {
    text-transform: uppercase;
  }

  div.lightbox.loading
  {
    background-color: #1d1d1d;
    color: #53d80b;
  }

  .stockList>li .stockTabs
  {
    background: #0a0a0a;
  }

/*									  */

/* Shadow Fix like... 188888888, smdh */

/*									  */

  div.userContent.dShadow
  {
    background-color: #1d1d1d;
  }

  .bShadow
  {
    box-shadow: none;
  }

  .stockList .stockUp:hover
  {
    background: rgba(181, 181, 181, 0.23);
  }

  li.portfolioSelect.idShadow
  {
    box-shadow: none;
  }

    
  .userWrapper .userNav .userMenu li
  {
    color: #cccccc;
    background: #1d1d1d;
  }

  div.userID h2
  {
    font-size: 1.14rem;
  }

  .stockList>li
  {
    background: #131313;
  }

  .comments .moreComments
  {
    font-size: 1.3rem;
    background: #0d0d0d;
    color: #eee;
  }

  .comments .notifNum
  {
    font-size: .9rem;
    color: #53d80b;
    border-color: #53d80b;
  }

  textarea#comment
  {
    resize: none;
    overflow: hidden;
    word-wrap: break-word;
    height: 65.2222px;
    background: #1d1d1d;
    border-color: #1d1d1d;
  }

  .comments .writeComment i
  {
    margin-left: 8pt;
    padding: 8pt;
    border-radius: 3px;
    cursor: pointer;
    box-shadow: none;
  }

  svg a
  {
    color: white;
  }

  text
  {
    color: white;
  }

  div.firmBar h1
  {
    color: #53d80b;
  }

  footer ul li:first-of-type
  {
    color: #53d80b;
  }

  .fa-send:before, .fa-paper-plane:before
  {
    color: beige;
    font-size: 30px;
  }

  .commentBox:hover
  {
    box-shadow: none;
  }

  div.commentHeader, .commentBox .commentHeader .commentAuthor
  {
    color: #53d80b;
  }

  .commentBox
  {
    border-bottom: 2px outset rgba(62, 62, 62, 0.45);
  }

  .leaders .firmLeaders, .leaders .tradeLeaders
  {
    border: none;
    border-bottom-left-radius: 2px;
    border-bottom-right-radius: 2px;
  }

/*			 */

/* scrollbar */

/*			 */


  ::-webkit-scrollbar
  {
    width: 6px;
    height: 6px;
  }

  ::-webkit-scrollbar-track
  {
    background: transparent;
  }

  ::-webkit-scrollbar-thumb
  {
    background: #0e0e0e;
    border-radius: 200000px;
  }

/* box scrollbar */

  div.tradeLeaders ::-webkit-scrollbar-thumb, div.tradeLeaders ::-webkit-scrollbar-track, div.tradeLeaders ::-webkit-scrollbar
  {
    display: none;
  }

  .buyStockModal {
    background: #1d1d1d;
  }
.form.modal {
    background:#1d1d1d }
}
