css:
@-moz-document domain("todoist.com") {
body{background:#141C21 !important;color:#B3B3B3!important;font-family:sans-serif!important;font-size:18px!important;letter-spacing:inherit}
#top_filters .filter.current{color:#4F4F4F!important}
#top_filters li.current{font-size:1.2em!important}
.inspiration a{color:#A5A2A2!important}
li.current table{background:#262F35 !important}
#top_filters .counter.overdue_color{background:#d8422d;color:#FFF!important;font-weight:700!important}
.counter{color:#85807D!important;font-size:95%!important;font-weight:400!important}
.counter_count{color:#85807D!important;font-size:13px!important}
.left_menu {background: #141C21!important;}
.filter:hover,#left_menu .items li:hover{background:#32383b !important}
.filter:hover table,#left_menu .items li:hover table{background:#32383B !important}
.task_item:hover .content,.task_item:hover .assign_user,.task_item:hover .menu,.task_item:hover .project,.on_menu_show{background:#383f42 !important}
.text{color:#EDEDED!important;font-size:15px}
div.heading,.section_header a{color:#4F4F4F!important;font-size:18px!important}
.no_tasks a{color:#50595e!important;font-size:18px!important}
#content,#editor{background:#182128 !important}
#page_background{background:#141b21 !important}
div#search_bar .cmp_search{padding-left:0!important;width:13px!important}
.generic_left_list .arrow{margin-left:-20px;margin-top:11px}
.amimenu .priorities .pri_selected{border:2px solid #848282!important}
.amimenu .priorities a{margin:5px 4px 4px 9px;text-align:center}
.amicheckbox{background:#FFF !important;border-color:#999!important;height:15px;margin-top:-1px!important;width:15px}
a.ex_link{color:#65B6EC!important;cursor:pointer!important;padding-left:22px!important;text-decoration:none}
.task_item span a{color:#65B6EC}
#agenda_view .time span{color:#A94016!important;padding:1px}
#agenda_view .time_overdue span{color:#B01D0C!important;padding:1px}
div#amicomplete_floater{min-height:500px}
.subsection_header{border-bottom-color:#858b8f!important;margin-bottom:10px!important;padding-bottom:1em!important}
.subsection_header a{color:#e6dede!important;font-size:18px!important;font-weight:400!important;text-decoration:none}
.tdb-article__title{color:#e4e3e3}
.tdb-article-slat__title,.tdb-article-slat__title a{color:#ecebeb!important}
h1,.tdb-h1{color:#b7443c!important;font-size:2.125rem}
h2,.tdb-h2{color:#e4e3e3!important;font-size:2.125rem}
h4,.tdb-h4{color:#e4e3e3!important}
span.date{color:#1f2528!important;}
.menu hover{border-bottom:1px solid #32383b;border-top:1px solid #32383b}
.section_header a{color:#EDEDED!important}
#completed_items a{color:#909090}
.gear_menu{visibility:visible!important}
.amimenu{background:#141C21 !important;color:#9d9898!important}
.gb_overlay{background:#242527 !important}
#gb_window.shadow{border:1px solid #141C21!important;border-collapse:separate}
.gb_window .caption,.gb_window .close{background:#141C21 !important;color:#666!important}
#settings_content select{background:#43464A;color:#4DADCA!important;width:200px}
.completed_quick_viz{background:#141C21 !important;width:inherit}
select#select_project{background:#141C21}
.table_explain th{background:#141C21 !important;border:1px solid #CCC}
.entry_title a{color:silver}
.widget li.active a{color:#ECE9E9}
#post{border-top:solid 4px #575B5F}
textarea.text_box.auto_exapnd{background:#141C21 !important;color:#60BBD6}
.note_submit textarea{background:#182127 !important;color:#4DADCA}
div.content .text_box_holder{background:#141C21 !important;border-right:1px solid #666!important}
div#gb_window{background:#141C21 !important;margin-top:47px;padding:1px}
.quick_add_task .select_project a{border:1px solid #666!important;border-top:0;display:block;margin-bottom:3px;padding:8px 4px 6px}
.form_content table{border:1px solid #666!important;border-collapse:collapse;width:100%}
div.completed_quick_viz{background:#141C21 !important;color:#777;min-height:200px;padding:5px 13px!important;width:250px}
body.theme_noir a.action,.theme_noir .note_extra_info a,.theme_noir #item_commander a,.theme_noir .blank_screen a,.theme_noir .premium_only a,.theme_noir .completed_quick_viz a{color:#CCC!important}
.theme_noir a.action,.theme_noir .note_extra_info a,.theme_noir #item_commander a,.theme_noir .blank_screen a,.theme_noir .premium_only a.sel_premium,.theme_noir .completed_quick_viz a{color:#8f8b8b!important}
#amicomplete_floater td{border-bottom:#141c21!important;cursor:pointer}
.dp_cal a span{color:#FFF!important;padding:4px 2px}
.cmp_scheduler_today{margin-top:2.5px!important}
.form_icon_holder span,.form_icon_holder img{margin-right:2px!important}
#settings_content{background:#141c21 !important;border-left:1px solid #4e4747!important;border-right:1px solid #4e4747!important}
#settings_content fieldset,#settings_content section{border-bottom:solid 1px #4e4747!important}
#settings_menu a{color:#bec5c5!important}
.section_social_connect.first{border-bottom:solid 1px #4e4747}
.amiselect{color:#d0cbcb}
div.icon menu:hover .cmp_menu_on:hover gear_menu:hover .gear_menu:hover{border-bottom:1px solid #182127!important}
#editor .cmp_reorder_handler{margin-left:-7px!important;margin-top:-2px!important}
#editor{padding:6px 16px 12px 45px; width: 90% !important}
#agenda_view .subsection_header.warning{color:#B33725!important;font-size:18px!important;font-weight:500!important}
.priority_4 .amicheckbox{background:#d93619 !important;border-color:#d93619!important;border-width:1px!important;height:16px!important;width:16px!important}
.priority_3 .amicheckbox{background:#eec000 !important;border-color:#eec000!important;border-width:1px!important;height:16px!important;width:16px!important}
.priority_2 .amicheckbox{background:#48c9da !important;border-color:#48c9da!important;border-width:1px!important;height:16px!important;width:16px!important}
.priority_1 .amicheckbox{background:#57D452 !important;border-color:#57D452!important;border-width:1px!important;height:16px!important;width:16px!important}
div.no_tasks li{display:none!important}
#top_filters li,#project_list .name,#labels_list .name{font-size:18px!important;font-weight:700;letter-spacing:.5px;position:relative}
#top_filter li,#project_list .item_table td,#labels_list .item_table td{margin:0;padding:5px 0}
#top_filters .counter,#project_list .name .counter_count,#labels_list .name .counter_count{background:#ddd;color:#85807D!important;font-weight:700!important;padding:2px 0;position:absolute;right:2px;text-align:center;width:2rem}
.code_inline,.code_block{background:#eee;font-family:monospace!important;padding:2px 4px}
#content h2 *{font-family:sans-serif}
#content h2 .h2_date{font-style:italic}
#content h2.project_header *{color:#FFEB3B!important;font-size:1.4rem;}
#content h2.project_header{  padding-bottom: 20px !important;}
#content ul.items .task_item .label:nth-child(odd){border:1px solid transparent;font-weight:700;margin-right:.2rem;margin-top:.3rem;padding:1px 5px;text-decoration:none!important}
#content ul.items .task_item:hover .label:nth-child(odd){background:#FFF;border-color:#ddd;cursor:pointer}
#content ul.items .task_item .label_sep{display:none}
#content .items .task_item,#content .items .task_content_item{font-size:13px}
#content .items .task_item:hover .code_inline{background:#ddd}
#content .items .task_item{border-left:.3rem solid #ddd;color:#666}
#content .items .task_item.priority_4{border-left-color:#db4641;color:#073642}
#content .items .task_item.priority_3{border-left-color:#268bd2;color:#485e55}
#content .items .task_item.priority_2{border-left-color:#76C6FF;color:#485e55}
#content .items .task_item .task_content_item{border-bottom:0 none;padding-left:10px!important}
#content .items .task_item table,#content .items .task_item td{border-bottom:1px solid #eee}
#content .items .task_item:last-child table,#content .items .task_item:last-child td{border-bottom:0 none}
#content .items .task_item:hover td{background:#f8f8f8}
#content .items .task_item:hover td.menu{border:0 none}
#content ul.items .task_item table td:first-child{padding-left:1rem}
.arrow{margin-left:-15px!important}
.amicheckbox:hover{background:#fafafa !important}
#content ul.items .task_item .div_due_date span{border:0 none!important;color:#485e55;padding:2px 4px!important; font-size: 13px !important; font-weight: bold !important}
#content ul.items .task_item .div_due_date .date_future{background:#ffecb5}
#content ul.items .task_item .div_due_date .date_overdue{background:#db4641;color:#FFF!important}
#content ul.items .task_item .div_due_date .date_today{background:#fed766}
#content .items .task_item .project .pname{padding-left:0;padding-right:0;width:0}
#content .items .task_item .project:hover .pname{background:#555;color:#eee;margin-top:-35px;overflow:visible;padding:5px;position:absolute;width:auto}
#content .items .task_item .project:hover .pname:before{border-left:5px solid transparent;border-right:5px solid transparent;border-top:5px solid #555;bottom:-5px;content:" ";height:0;position:absolute;width:0}
#content ul.items .task_item .drag_and_drop_handler{height:100%;margin-top:0}
.cmp_reorder_handler{margin-left:0!important;margin-top:-6px!important}
#content .cmp_reorder_handler{margin-left:-8px!important;margin-top:4px!important}
#filters_list .cmp_reorder_handler{margin-left:-8px!important;margin-top:0!important}
#project_history .label{color:#485e55!important}
#project_list .arrow{margin-top:5px!important}
#items_list .arrow{margin-top:10px!important}
#project_list .name .counter_count,#labels_list .name .counter_count{padding:0!important}
li.current table .name,.items .name{font-size:15px!important}
#td_logo,.invisible_bg,.cmp_td_logo_mini,.amimenu .no_hover td,div.gb_window,html,.gb_iframe_html,.text_box_holder,.dp_container,.dp_cal,#settings_content td:first-child,#settings_content td+td,.table_explain td,#top_bar_wrapper,div.gb_window .content,div#quick_add_task manager gb_iframe_html.text_box_holder,.textarea_holder,.note_item .note_extra_info,td.text_box_holder,div.textarea_holder,div.note_item div.note_extra_info{background:#141C21 !important}
.name,.filter,.control{color:#c3cdd4!important;font-size:15px!important}
input,.quick_add_task .text_box{background:#141C21 !important;color:#4DADCA!important}
.task_item .task_content_item,.task_item:hover .menu,.on_menu_show{border-bottom:1px solid #32383b!important}
#left_menu,#content{border-right:1px solid #000!important}
.cmp_open_arrow_down,.cmp_open_arrow_right{margin-bottom:7px!important}
#amicomplete_floater,.dp_cal select,div.amimenu .no_hover td{background:#141C21 !important;color:#B3B3B3!important}
.select_project a,.quick_add_task .input_due_date,div.amimenu span{color:#B3B3B3!important}
.completed_quick_viz h2,.dp_help a,#completed_app .filters select{color:#C3C3C3!important}
#content_title,#page h2,#page p{color:#C8C7C7}
.response p,.response_author,#post_title,.post_author,#post p,.message_form textarea.typing{color:#CDCDCD}
#settings_menu,#settings_popup{background:#141c21 !important}
.section_title,.category h2 a,.section h3 a{color:#dedbdb!important}
#settings_menu a.active,#settings_menu a:hover{background:#333b3e !important}
.priority_4 .amicheckbox:hover,.priority_3 .amicheckbox:hover,.priority_2 .amicheckbox:hover,.priority_1 .amicheckbox:hover{background:#F1EDED !important;border-color:#A5A2A2!important;border-width:1px!important;height:16px!important;width:16px!important}
div#top_bar_inner[style],div#all_holder[style]{margin:0 auto!important; max-width: 1440px;}
#content .items .task_item table, #content .items .task_item td {
border-bottom: 1px solid #182128;
}
}
