css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("cloud.disroot.org") {
	
	/* Nextcloud bar */
	body::before {
	-webkit-box-shadow: none;
	box-shadow: none;
}
	#body-user #header, #body-settings #header, #body-public #header {
	background-color: #2f2f2f;
}
	#header .logo, #header .logo-icon {
	display: none;
}
	#nextcloud {
	padding: 0px;
}
	#appmenu li a {
	padding: 12px 9px;
}
	#expand {
	padding: 7px 2px 6px 1px;
}
	#contactsmenu .menutoggle {
	padding: 14px 9px;
}
	#appmenu li:hover a::before, #appmenu li a.active::before {
	border-bottom-color: #fcfcfc;
}
	
	/* Tooltip */
	#appmenu li span {
	background-color: #fefefe;
	-webkit-border-radius: 2px;
	-moz-border-radius: 2px;
	border-radius: 2px;
	border-top-left-radius: 0;
	border-top-right-radius: 0;
	box-shadow: 0 0.5px 2px rgba(0, 0, 0, 0.27);
}
	
	
	/* Username label */
	.b-system-drop-down .accountPlace {
	text-shadow: none;
	height: 30px;
	padding: 0px 11px;
	border-radius: 2px;
	font-size: 12px;
}
 
	/* XMPP Chat */
#jsxc_roster {
	background-color: #fcfcfc;
}
	#jsxc_roster > .jsxc_bottom {
	background-color: #fcfcfc;
}
	#jsxc_toggleRoster {
	background-color: #fcfcfc;
}
	#jsxc_toggleRoster {
	width: 10px;
}
	#content-wrapper, #filestable > thead {
	padding-right: 2px;
}
	.jsxc_avatar {
	margin: 0px 9px 0px -2px;
}
	#jsxc_buddylist {
	margin-left: -2px;
	margin-top: 2px;
}
	#jsxc_roster > .jsxc_bottom {
	margin-bottom: 2px;
	margin-top: 2px;
}
	.jsxc_online::before {
	background-color: green;
	margin-left: 32px;
	margin-top: 24px;
}
	
	
	
  .thm-body {
	background-color: #fcfcfc;
}
	
	.thm-message-list-top-toolbar, .thm-message-list-bottom-toolbar {
	background-color: #fcfcfc !important;
}
	
	.messageList .b-content .messageListItem {
	background-color: #fcfcfc;
}
	
	.btn {
	background-color: #fcfcfc;
	border-radius: 2px;
}
	
	.btn-group > .btn.single {
	border-radius: 2px !important;
}
	
	.btn-group > .btn.last {
	border-top-right-radius: 2px !important;
	border-bottom-right-radius: 2px !important;
}
	
	.btn-group > .btn.first {
	border-top-left-radius: 2px !important;
	border-bottom-left-radius: 2px !important;
}
	
	.btn.disabled, .btn[disabled] {
	background-color: #f3f3f3;
}
	
	.btn.btn-success {
	background-color: #4c9ed9;
}

	
	.messageList .b-message-list-wrapper {
	-webkit-box-shadow: 0 1px 2px rgba(0,0,0,.2) !important;
	box-shadow: 0 1px 2px rgba(0,0,0,.2) !important;
	border-radius: 2px;
}
  
  .messageList .second-toolbar {
	border-top-right-radius: 2px;
	border-top-left-radius: 2px;
}
  
	.nano.scroller-shadow-bottom::after, .nano.scroller-shadow-top::before {
	-webkit-box-shadow: 0 0 15px rgba(0,0,0,.15);
	box-shadow: 0 0 15px rgba(0, 0, 0, 0.15);
}
 
  
  /* Search bar */
	.input-append .uneditable-input, .input-append input, .input-append select {
	border-radius: 2px;
}
	.input-append .add-on:last-child, .input-append .btn:last-child {
	border-radius: 0 2px 2px 0;
}
  
	
	
	/* Mail preview */
	.messageView .b-content {
	color: #4d4d4d;
	font-size: 13px;
}
	.messageView .b-content .content {
	background-color: #fefefe !important;
}
	.messageView .b-content .messageItem .bodyText .b-text-part div[data-x-div-type="html"] div[data-x-div-type="body"] {
	background-color: #fefefe !important;
}
	.messageView .b-content .messageItemHeader {
	background-color: #fefefe;
	border-bottom: 1px solid #fefefe;
}
	.messageView .b-content .messageItem .bodyText .b-text-part div[data-x-div-type="html"] div[data-x-div-type="body"] {
	color: #2d2d2d !important;
}
	.messageView .b-content {
	border: 1px solid #c2c2c2;
	border-radius: 2px;
}
	
	
	
}
