css:
@-moz-document domain("asoftmurmur.com") {
	#about-link-container,
	#gp-container,
	#site-title,
	#intro-text,
	#action-bar-area > *:not(#global-volume-slider):not(#mute-button) {
		display: none;
	}

	#action-bar {
		text-align: center;
	}

	html,
	body,
	#background-container {
		background: #000;
		min-height: 100vh;
	}

	html {
		height: 100%;
		overflow: hidden;
	}

	body {
		height: 100%;
		overflow: auto;
	}
}
