css:
@-moz-document domain("dtube.video"), domain("d.tube") {
  
  /**
 * Style mod for Dtube.video - The Video Site For Steemit.com,
 *
 * Author: https://steemit.com/@ura-soul
 *
 */
  
  body, body.pushable, .pusher{
   background: hsl(0, 0%, 5.9%)!important;
   color: #eee;
  }
  
  a, .customlink, .channelLink a  {
    color: hsl(0, 61.5%, 54.1%);
  }
  
  a:hover {
    color: hsl(0, 100%, 50%);
  }
  
  .videosnapauthor .customlink{
    color: hsl(0, 43.8%, 63.7%);
  }
  
  input,.ui.form textarea, .ui.form textarea:focus,
  .ui.form input:focus:not([type]), .ui.form input[type="date"]:focus, .ui.form input[type="datetime-local"]:focus, .ui.form input[type="email"]:focus, .ui.form input[type="number"]:focus, .ui.form input[type="password"]:focus, .ui.form input[type="search"]:focus, .ui.form input[type="tel"]:focus, .ui.form input[type="time"]:focus, .ui.form input[type="text"]:focus, .ui.form input[type="file"]:focus, .ui.form input[type="url"]:focus{
    background: hsl(0, 0%, 36.5%)!important;
    color: #eee!important;
    border-color: hsl(0, 0%, 65.9%)!important;
  }
  
  #textlogo {
    color: hsl(0, 82.3%, 60.2%);
  }
  
  .ui.button{
    background: hsl(0, 0%, 38.8%);
    color: hsl(0, 0%, 94.1%); 
  }

  .ui.youtube.button {
    background-color: hsl(0, 44.4%, 50.6%);
  }
  
  .ui.menu{
   background: hsl(0, 0%, 27.8%);
  }
  
  .ui.menu .item {
    color: hsl(0, 0%, 95.3%);
  }
  
  .ui.input input {
    background: grey;
   color: #eee;
   border-color: hsl(0, 0%, 57.3%);
  }
  
  #dsearch::placeholder {
    color: hsl(0, 34.5%, 90%);
  }
  
  .ui.link.menu .item:hover, .ui.menu .dropdown.item:hover, .ui.menu .link.item:hover, .ui.menu a.item:hover, i.icon.search::before {
    color: hsl(0, 37.2%, 73.1%);
  }
  
  .ui.link.menu .item:hover, .ui.menu .dropdown.item:hover, .ui.menu .link.item:hover, .ui.menu a.item:hover {
    background: hsla(0, 11.7%, 45.3%, 0.5);
  }
  
  .ui.input input:active, .ui.input.down input, .ui.input.focus input, .ui.input input:focus {
    background: hsl(0, 9.6%, 53.1%);
   color: hsl(0, 100%, 93.1%);
   border-color: hsl(0, 44%, 29.4%);
  }
  
  .results, .ui.menu .dropdown.item .menu {
    background: hsl(0, 0%, 30.6%)!important;
    border-color: hsl(0, 0%, 43.5%)!important;
  }
  
  .ui.search > .results .result .title, .ui.menu .ui.dropdown .menu > .item {
    color: hsl(0, 0%, 87.1%)!important;
  }
  
  .ui.search > .results .result:hover, .ui.category.search > .results .category .result:hover {
    background: hsl(0, 9.2%, 36.3%);
  }
  
  .videosnapsnap img {
    border-radius:4px;
  }
  
  .ui.menu .ui.dropdown .menu > .item:hover {
    background: hsl(0, 9.2%, 36.3%)!important;
    color: hsl(0, 0%, 87.1%)!important;
  }
  
  .ui.dropdown .menu > .header{
    color: hsl(0, 23.7%, 66.1%);
  }
  
  .ui.header {
    color: hsl(0, 56.7%, 66.5%);
    border-color: hsl(0, 56.7%, 66.5%);
  }
  
  .ui.basic.label {
    background: hsl(0, 0%, 32.5%);
    border-color: hsl(0, 19.8%, 55.5%);
    color: hsl(0, 64.2%, 66.1%);
  }
  
  .ui.form .field > label {
    color: hsl(0, 17.7%, 75.7%);
  }
  
  .ui.red.labels .label, .ui.red.label
  {
    background-color: hsl(0, 52.4%, 55.5%) !important;
   color: hsl(0, 94.4%, 92.9%) !important;
  }
  
  a.ui.basic.label:hover {
    background: hsl(0, 90.7%, 57.8%);
    border-color: hsl(0, 100%, 73.7%);
    color: hsl(0, 100%, 99%);
  }
  
  #dropzone {
    border-color: hsl(0, 62.3%, 31.2%);
    color: hsl(0, 34.5%, 55.1%);
    border-radius: 40px;
  }
  
  .ui.steps .step {
    background: hsl(0, 0%, 34.9%);
    color: #eee;
    border-color: transparent;
  }
  
  .ui.steps .step .description {
    color: hsl(0, 41.7%, 81.2%);
  }
  
  .ui.steps .step::after {
    background-color: transparent;
    border-color: hsl(0, 41.7%, 81.2%);
  }
  
  .ui.comments .comment .author {
    color: hsl(0, 58.4%, 51%);
  }
  
  .ui.comments .comment .metadata
  {
    color: hsl(0, 53.5%, 66.3%);
  }
  
  .ui.comments .comment .text {
    color: hsl(0, 39.8%, 79.8%);
   padding-left: 1rem;
   padding-top: 0.5rem;
  }
  
  .ui.comments .comment .actions a {
    color: hsl(0, 68.5%, 65.1%);
  }
 
  .ui.comments .comment .actions a:hover {
    color:   hsl(0, 93.9%, 54.7%);
  }

  .ui.loader::before {
    border-color: hsla(0, 0%, 84.7%, 0.5)!important;
  }
  
  .videoslider {
    background: hsl(0, 0%, 14.5%)!important;
  }
  
  .videosnaprest{
    color: hsl(120, 38.1%, 38%);
  }
  
  .slick-next, .slick-prev{
    background: hsl(0, 29.8%, 67.1%);
  }
  
  .sidebar, .ui.dropdown .menu, .info .button {
    background: hsl(0, 0%, 23.9%)!important;
  }
  
  .menuitem {
    color: hsl(0, 51.2%, 75.9%);
  }
  
  .dtubesidebarmenu:hover, .ui.dropdown .menu > .item:hover {
     background: hsl(0, 11.5%, 34.1%);
  }
  
}
