css:
@-moz-document url-prefix("https://skyblock.net") {
body, .button, .staffTitle li, .username .style2, .username .style24, .dark_postrating_neutral, span[style="color: #000000"], span[style="color: rgb(0, 0, 0)"], .bbCodeQuote .attribution, .event .content .description em, .discussionList .sectionFooter .contentSummary {
    color: #fff !important;
} a {
    color: #bb86fc !important;
} .formPopup .controlsWrapper, .subHeading, .textWithCount.subHeading .text, .discussionList .sticky .title a, .DateTime, .discussionList .sticky .EditControl, .discussionList .sticky .username, .discussionList .sticky .pairsJustified dt, a.username.author, .postNumber, .discussionList .sticky .DateTime {
    color: #03dac5 !important;
} #header, body, .pageContent, .mainContainer, .navTabs, .secondaryContent, .breadcrumb, .sectionMain, .primaryContent, .button, .profilePage .tabs.mainTabs, .profilePage .tabs.mainTabs li a, .nodeInfo.categoryForumNodeInfo, .discussionListItem .posterAvatar .avatarContainer, .discussionListItem, .discussionListItem .posterAvatar, .discussionListItem .stats, .dark_postrating_detail, .bbCodeQuote .quoteContainer, .bbCodeBlock pre, .bbCodeBlock .code, .tabs, .tabs li a {
    background: #121212 !important;
} .stackAlertContent, .navTabs .navTab.selected .tabLinks, .navTabs .navTab.selected .navLink, .navPopup .PopupItemLinkActive:hover, #QuickSearch, .primaryControls, .secondaryControls, .formPopup .controlsWrapper, a.crumb, .subHeading, .sectionFooter, .avatar img, .PageNav a, .nodeList .categoryStrip, .staffTitle, .profilePage .tabs.mainTabs li.active a, .sectionHeaders, .discussionList .sticky .posterAvatar, .discussionList .sticky .stats, .discussionList .sticky, .messageUserBlock div.avatarHolder, .messageUserBlock, .messageUserBlock .extraUserInfo, .bbCodeQuote .attribution, .bbCodeBlock .type, .redactor_toolbar, .tabs li.active a {
    background: #1f1b24 !important;
} .breadcrumb .crust .arrow span {
    border-left-color: #1f1b24 !important;
} .subHeading, .sectionFooter, .nodeList .categoryStrip, .sectionHeaders {
    border: 1px solid #d7edfc !important;
} .navTabs .navTab.selected .navLink {
    text-shadow: 0 0 0 transparent, 0px 0px 3px #000;
} .Menu {
    border: 1px solid #1f1b24 !important;
    border-top: 5px solid #1f1b24 !important;
} .discussionList .sticky .title a, .discussionList .sticky .DateTime, .discussionList .sticky .EditControl, .discussionList .sticky .username, .discussionList .sticky .pairsJustified dt {
    text-shadow: none !important;
}
}
