css:
@-moz-document domain("onehallyu.com") {
html, body {
    background: #232323 !important;
}
a {
    color: #ab6375;
}
.general_box {
    background: #2d2d2d;
}
.general_box h3 {
    background-color: #465f82;
}
.desc, .desc.blend_links a, p.posted_info {
    color: #c7c7c7;
}
.ipsSideBlock {
    background: #232323;
}
.ipsType_pagetitle, .ipsType_subtitle {
    color: #807e7e;
}
.progress_bar {
    background-color: #676464;
}
.progress_bar.topic_poll {
    border: 1px solid #ababab;
}
.poll_question ol {
    background-color: #353535;
    color: #989696;
}
.date, .poll_question .votes {
    color: #b7b7b7;
}
.poll_question h4 {
    background-color: #505050;
    color: #cccccc;
}
body#ipboard_body fieldset.submit, body#ipboard_body p.submit {
    background-color: #737373;
}
.post_block {
    background: #232323;
    border-top: 1px solid #3e3e3e;
    border-bottom: 1px solid #3e3e3e;
}
.post_block h3 {
    background: #404040;
}
.post_block h3 .author {
    color: #aaa;
}
.post_body .post {
    color: #aaa !important;
}
div.post_body div.post p span[style] {
    color: #aaa !important;
    background: #232323 !important;
}
/* for compilation page */
.post_body .post p, .post_body .post span[style] .bbc_spoiler span.spoiler_title, .post_body .post span[style] > span[style] > span[style] > strong, .post_body .post span[style] > span[style] > strong, .post_body .post span[style] > span[style], .post_body .post span[style] > strong, .post_body .post .bbc_spoiler .bbc_spoiler_content > strong, .post_body .post span[style] > strong > span[style], .post_body .post div[style] > span[style$="color:rgb(34,34,34)"] {
    color: #aaa !important;
    background: #232323 !important;
}
.post_body .post span[style] > strong > a > span[style], .post_body .post p[style] > strong > a > span[style] {
    color: inherit !important;
}
/* end */
blockquote.ipsBlockquote {
    background: #232323;
    border-color: #989898;
    border-left-color: #989898;
}
.post p.citation {
	background: #404040;
    border-radius: 0;
    border-top-color: #989898;
    border-right-color: #989898;
}
div.bbc_spoiler_content {
	background: #232323;
}
.desc.lighter, .desc.lighter.blend_links a {
    color: #797979;
}
.reputation.zero {
    background: #3a3a3a;
}
.reputation.positive, .reputation.negative {
    color: #bdbdbd;
}
.reputation.positive, .members li.positive {
    background: #3a3a3a;
}


table.ipb_table {
    background: #232323 !important;
}
table.ipb_table tr td {
   border-color: #232323;
}
table.ipb_table tr td ul li {
    color: #aaa;
}
table.ipb_table h4, table.ipb_table .topic_title {
	color: #ab6375;
}
.last_post a {
    color: #ab6375;
}
table.ipb_table .subforums a {
    color: #ab6375;
}
.col_c_forum h4 a {
    color: #547096;
}
#index_stats .ipsList_withminiphoto>li {
    border-color: #232323;
}
#board_stats li {
    color: #aaa;
}
.col_c_post a {
    color: #547096;
}
.maintitle {
    background-color: #547096;
}
.ipsBox_container {
    background: #232323;
}
.ipsSideBlock h3 {
    background-color: #547096;
}
.ipsSideMenu h4 {
    color: #aaa;
}
.ipsSideMenu ul {
    border-top: #232323;
}
.ipsSideMenu ul li {
    border-bottom: #232323;
}
.no_messages {
    background: #232323;
    color: #aaa;
}
table.topic_list:hover {
    opacity: 1 !important;
    transition: 700ms !important;
}
table.topic_list {
    opacity: 0 !important;
    transition: 1500ms !important;
}
.post_block .post_wrap .author_info fieldset {
    border: 0px !important;
}
.post_block .post_wrap .author_info .user_details .basic_info .avatar img {
    border: 0px !important;
    background: transparent;
}
}
