css:
@-moz-document domain("tailopez.com") {
#header .topPanel {
    background: #333;
}

.container.height_head {
    text-indent: 0;
}

.phone .mailer {
    padding: 5px 20px;
}

#header {
    position: relative;
    height: auto;
}

#main-content {
    top: 0;
}

#course .container > .row {
    background: white;
    padding-top: 20px;
    margin: 0;
}

.icon-star-medium {
    font-size: 1.25rem !important;
    line-height: 1.25rem;
    height: auto;
}

#navbar ul:not(.dropdown-menu) {
    min-height: 0;
}

#navbar ul li {
    height: auto;
}

#navbar li > a, #navbar li:first-child > a, #navbar li:last-child > a {
    padding: 8px 16px;
}

#course > .container > .row > div {
    padding-right: 0;
    padding-left: 0;
}

/* #courseSlides */
#courseSlides {
    box-sizing: border-box;
    margin: 0;
    padding: 0 15px;
}

#courseTab .owl-item, #courseTab .owl-wrapper {
    width: 100% !important;
}

#courseSlides .owl-carousel .owl-wrapper {
    padding: 0 10px;
}

#courseTab a.courseTabLink {
    font-size: 32px;
    font-weight: bold;
    text-align: center;
    padding: 40px 0 8px 10px;
}

#courseSlides .owl-carousel.lesson-slider-content .owl-item {
    width: 160px !important;
}

#courseSlides .item-box .item-img {
    position: relative;
}

#courseSlides .item-box .item-img::after {
    opacity: 0.8;
    font-family: Gotham, FontAwesome, sans-serif;
    content: '\f00c  COMPLETE';
    position: absolute;
    left: 0;
    top: 0;
    display: block;
    width: 100%;
    height: 2rem;
    line-height: 2rem;
    font-size: 1rem;
    padding: 0 5px;
    background: #5cb85c;
    color: white;
}

#courseSlides .item-box h2.title {
    display: block;
    text-align: left;
    line-height: 1.4em;
    max-height: 3.5em;
    overflow: hidden;
    text-overflow: ellipsis;
}

#courseSlides .item-box h2.title .text-success {
    display: none;
}

#courseSlides .item-box .text-center {
    padding-top: 5px;
    text-align: left;
    font-size: 0.75rem;
}

.lesson-rating-count {
    font-size: 1rem;
}

.item-img img {
    height: 80px;
}

/* /#courseSlides */

/* #course */
#course #up-stars, #course #text, #course #buybutton2 {
    display: none;
}

#course #leftside > div {
    border: none !important;
}

#course #leftside .title {
    font-size: 24px;
    margin: 0;
}

#course #leftside .note_ques {
    margin-top: 0;
    padding: 30px 20px;
}

#course #leftside .summary {
    margin-bottom: 0 !important;
}

#course #leftside .download {
    border-bottom: 1px solid #5198dd;
    padding-bottom: 20px;
    margin-bottom: 20px;
}

.download ul {
    border: none;
    margin-bottom: 0;
}

.download ul li {
    display: block;
    border: 0;
    margin-top: 10px;
    border-radius: 2px;
    background: linear-gradient(to bottom, #222, #161616);
    box-shadow: 0 2px 3px rgba(0, 0, 0, .2);
}

.download ul li .icon img {
    height: 24px;
    width: auto;
    margin-right: 10px;
}

.download ul li span.dis_table {
    display: block;
    padding: 0;
}

.note_ques input.btn_submit {
    display: block;
    border-radius: 2px;
    box-shadow: 0 2px 3px rgba(0, 0, 0, .2);
    background: linear-gradient(to bottom, rgba(0, 0, 0, 0), rgba(0, 0, 0, .2));
    background-color: #222;
}

.note_ques input.btn_submit:after {
    content: ' ';
    display: block;
    clear: both;
}

.note_ques input.btn_submit:hover {
    background-color: #D1232A;
    box-shadow: 0 3px 4px rgba(0, 0, 0, .15);
}

.note_ques textarea {
    padding: 10px;
    box-shadow: 0 0 1px rgba(0, 0, 0, .5);
}

#stars-submit-btn {
    margin: 0;
}

#star-hover-output {
    display: inline;
}

.lesson_section {
    border-bottom: 1px solid #5198dd;
}

.note_ques .lesson_section:last-child {
    border-bottom: none;
    padding-bottom: 0;
    margin-bottom: 0;
}

#feedback-input {
    border: none;
    box-shadow: 0 0 1px rgba(0, 0, 0, .5);
}

/* /#leftside */

/* #rightside */
#course #rightside .panel-default > .panel-heading .panel-title > a {
    background: none;
    padding: 0;
}

#course #rightside .panel-default > .panel-heading .panel-title {
    text-align: center;
    font-weight: bold;
    line-height: 34.2833px;
    height: 34.2833px;
}

#course #rightside .panel {
    padding-top: 10px;
}

#course #rightside .progresing {
    border-top: none;
    padding-top: 0;
    font-size: 12px;
}

.notes .dis_table p a {
    font-size: 12px;
    color: #666;
}
}
