css:
@-moz-document domain("pouke.org") {
/* 
Product Name: Pouke.org Dark Theme
Version: v2.1
Author: Vladimir Jovanović
Link URI: https://fb.com/VladimirWebDev
Licence: GNU v3
Year of production: 2018
*/
:root {
    --bgd-back: #111;
    --bgd-front: #1c1c1c;
    --link: #a6a6a6;
    --black: #000;
}

/* Linkovi */
a {
    color: #6a9dff !important;
}
a:hover {
    color: #284b8e !important;
}

/* Navigacija */
#ipsLayout_header {
    background: var(--bgd-front) !important;
}
#ipsLayout_header > div > div > div.focus-nav > nav > div > ul > li > a,
#ipsLayout_mainArea > div.cAnnouncementsContent > div > a {
    color: var(--link) !important
}
#ipsLayout_body > div > div.content-padding > div.precontent-blocks > div.ipsfocus-breadcrumb > nav {
    background: var(--black) !important;
}

/* Društvene mreže */
#ipsLayout_body > div > div.content-padding > div.precontent-blocks > div.ipsfocus-breadcrumb > ul > li > a {
    color: #fff !important;
}

/* Obaveštenje */
#ipsLayout_mainArea > div.cAnnouncementsContent > div {
    background: var(--black) !important;
}
body,
.ipsAreaBackground,
.ipsQuote {
    background: var(--bgd-front) !important;
    color: var(--link) !important;
}
.ipsApp .ipsComment .ipsComment_header,
.focus-transparent #elMessageViewer .ipsType_pageTitle {
    background: #23232d !important;
}
.ipsType_light,
.ipsApp .cke_contents,
.ipsComposeArea_dummy,
.ipsTabs_item,
.cPastLeaders_cell:after,
.ipsType_sectionHead,
.ipsMenu,
.ipsMenu_title,
.ipsMenu_item > a,
.ipsPageHead_barText > span,
.cAuthorPane_info span,
div.ipsReact_overview,
.focus-post-row .cTopic .ipsComment_controls a,
.fa-plus,
.ipsPager_type,
.ipsApp .ipsComposeArea_formControl label:not(.ipsField_unlimited),
.ipsApp .ipsComposeArea_dropZone .ipsButton_light,
.ipsType_richText span,
div.cPost_contentWrap.ipsPad > div.ipsType_normal.ipsType_richText.ipsContained > blockquote > div.ipsQuote_citation.ipsQuote_open {
    color: #a6a6a6 !important;
}

/* Navigacija */
.ipsNavBar_secondary > li > a {
    color: #fff !important;
    cursor: pointer;
}
.ipsNavBar_secondary > li > a:hover {
    color: #d9d9d9 !important;
}
.ipsNavBar_primary:not( .ipsNavBar_noSubBars) > ul:before {
    background: none !important;
}

/* Brzi čet */
#chatBoxForm {
    background: #000 !important;
}
.bimcb_chatInput {
    color: var(--link);
}

/* Linkovi */
.ipsType_richText,
.ipsBreadcrumb > ul > li > a,
.ipsType_pageTitle,
#ipsLayout_footer a,
#ipsLayout_footer p,
a.ipsType_blendLinks,
.ipsType_blendLinks a,
.cPost .ipsComment_meta a,
.cPost .cAuthorPane .cAuthorPane_author a,
.cPost_contentWrap.ipsPad > div > blockquote > div > a,
#ipsLayout_contentWrapper > nav.ipsBreadcrumb.ipsBreadcrumb_top.ipsFaded_withHover > ul > li,
#ipsLayout_contentWrapper > nav.ipsBreadcrumb.ipsBreadcrumb_top.ipsFaded_withHover > ul > li > a > span {
    color: #d9d9d9 !important;
}
.ipsBreadcrumb > ul > li {
    opacity: 1 !important;
}

/* Teme */
.ipsBox:not( .ipsBox_transparent):not( .ipsModerated),
.ipsAreaBackground_reset,
.ipsDataList.ipsDataList_zebra .ipsDataItem:not( .ipsDataItem_selected):not( .ipsModerated):not( .ipsDataItem_new):not( .ipsDataItem_success):not( .ipsDataItem_warning):not( .ipsDataItem_error):not( .ipsDataItem_info):not( .ipsDataItem_status):nth-child(even),
.ipsDataList_readStatus .ipsDataItem:not( .ipsDataItem_unread):not( .ipsDataItem_selected):not( .ipsModerated),
.ipsMenu,
.cMessage_active,
.ipsTabs_panel,
.ipsComposeArea_dummy,
.cke_wysiwyg_div,
.ipsNavBar_secondary,
#elClubControls .ipsTabs,
*::-webkit-scrollbar {
    background: #111 !important;
}

/* Titl foruma */
.ipsWidget.ipsWidget_horizontal .ipsWidget_title,
.ipsType_sectionTitle,
.ipsWidget.ipsWidget_vertical .ipsWidget_title,
.ipsQuote_citation,
.focus-post-row .cTopic .ipsComment_controls,
html[dir='ltr'].focus-post-bar .cTopic .ipsItemControls,
::-webkit-scrollbar-thumb:hover,
.gsc-control-cse {
    background: #000 !important;
}

/* Komentari */
.ipsBox .ipsComment {
    background-color: #1c1c1c !important;
    border-radius: 10px;
    border: 1px solid #000000;
}

/* Border oko komponenti */
.ipsBox,
.ipsDataItem,
.cke_reset,
.gsc-control-cse,
.ipsQuote,
.focus-post-bar .cTopic .ipsItemControls {
    border: none !important;
}
.ipsBox:not( .ipsBox_transparent):not( .ipsModerated),
.ipsBox,
.ipsPageHeader,
.focus-topic-compact .focus-topic,
.ipsPager,
.ipsWidget,
.ipsBox_alt,
.ipsfocusBox-inner {
    background-color: #111 !important;
}

/* Reakcije */
.ipsReactOverview ul img {
    border: 0 !important;
}

/* Ko je online */
.ipsType_normal .ipsType_break span {
    font-size: 16px !important;
    color: green !important;
}
.ipsType_normal .ipsType_break span:hover {
    color: lime !important;
}

/* Podrazumevana tema */
.content-wrap,
#ipsLayout_contentWrapper,
.ipsComposeArea_editor,
.cke_top,
#ipsLayout_mainArea > div:nth-child(2) > ul > li:nth-child(2) > section > div,
.cPastLeaders_cell,
.cPastLeaders_title span,
.ipsMenu_headerBar,
.ipsMenu_footerBar,
.ipsApp .ipsButton_link.ipsFollow {
    background: var(--bgd-front) !important;
}

/* Broj stranice */
ul.ipsPagination > li > a {
    color: #fff !important;
}
ul.ipsPagination > li.ipsPagination_active > a {
    color: #000 !important;
}

/* Kartica profila */
.cUserHovercard {
    background: var(--bgd-back);
}

/* Scroll */
::-webkit-scrollbar-thumb {
    background: #373744 !important;
    border-radius: 100px;
}

/* Footer */
.ipsApp .ipsBreadcrumb {
    background: var(--bgd-front) !important;
}
.ipsfocus_megaFooter {
    background: #000 !important;
}
}
