css:
@-moz-document domain("twitter.com") {
:root {
    --contentcolor: #3f3f3f!important;
    --maincolor: #353535!important;
    --navcolor: #212121!important;
    --borders: #383838!important;
}
body {
    background: var(--maincolor)!important;
}
.TwitterCardsGrid,
.TwitterCard,
.TwitterCard--animation {
    background: var(--contentcolor)!important;
}
.dashboard-right {
    display: none!important;
}
#page-container {
    overflow: hidden!important;
}
.content-main,
.top-timeline-tweetbox {
    width: 73.95%!important;
}
.AdaptiveMediaOuterContainer {
    width: 100%!important;
}
.AdaptiveMedia,
.AdaptiveMedia-video {
    max-width: 100%!important;
    width: 700px!important;
}
.AdaptiveMedia-singlePhoto img {
    display: flex!important;
    width: 700px!important;
    top: 0!important;
}
.AdaptiveMedia-doublePhoto img {
    display: flex!important;
    height: 100%!important;
    top: 0!important;
}
.AdaptiveMedia-doublePhoto {
    text-align: center!important;
    display: flex!important;
    width: 700px!important;
    height: 500px!important;
}
.AdaptiveMedia,
.is-square {
    max-height: 100%!important;
}
.u-lg-size2of3 {
    width: 700px!important;
}
.ProfileSidebar--withRightAlignment {
    display: none!important;
    padding: 0 5px;
}
.global-nav-inner {
    background: var(--navcolor);
}
.stream-item:not(.no-header-background-module) {
    background: var(--contentcolor);
    border-left: 1px solid var(--borders);
    border-right: 1px solid var(--borders);
    background-clip: padding-box;
}
.tweet {
    border-bottom: 1px solid var(--borders);
}
.tweet:hover {
    background: var(--contentcolor);
    border-bottom: 1px solid var(--borders);
}
.js-new-tweets-bar:hover {
    background: var(--contentcolor);
    border: none;
}
.js-new-tweets-bar {
    background: var(--contentcolor);
    border: none;
}
.module .flex-module {
    background-color: var(--maincolor);
    border: none;
}
.DashboardProfileCard {
    background: var(--maincolor);
    border: none;
}
.DashboardProfileCard-avatarLink {
    background-color: transparent;
}
.Trends {
    background: var(--maincolor);
    border: none;
}
.top-timeline-tweetbox .timeline-tweet-box {
    border-top-left-radius: inherit;
    border-top-right-radius: inherit;
    border: 1px solid var(--borders);
}
.home-tweet-box,
.LiveVideo-tweetBox,
.RetweetDialog-commentBox {
    background-color: var(--contentcolor);
}
.ProfileCanopy-navBar,
.ProfileNav-item--userActions,
.ProfileHeading-content {
    background-color: var(--navcolor);
    border: none;
}
.dropdown-menu {
    background-color: var(--contentcolor);
}
.DashUserDropdown.dropdown-menu .DashUserDropdown-userInfoLink:hover,
.DashUserDropdown.dropdown-menu .DashUserDropdown-userInfoLink:focus,
.DashUserDropdown.dropdown-menu .DashUserDropdown-userInfoLink:active {
    background-color: transparent!important;
    text-decoration: none;
}
li.stream-item.highlighted {
    background-color: transparent;
}
.ActivityItem {
    padding: 10px 12px 10px 70px;
    border-bottom: 1px solid var(--borders);
}
.ProfileHeading-toggle {
    background-color: var(--navcolor);
}
.NotificationsHeadingContent {
    border: 0px solid #e6ecf0;
}
.content-header,
.content-no-header {
    border: 0px solid #e6ecf0;
}
.AppContent {
    background-color: var(--maincolor);
}
.MomentGuideNavigation {
    background-color: var(--navcolor);
}
.MomentsGuidePage-content {
    background-color: var(--contentcolor);
}
.permalink {
    padding: 0;
    background-color: rgba(0, 0, 0, 0.6)!important;
    border: 1px solid #e6ecf0;
    border: 1px solid rgba(0, 0, 0, 0.1);
}
.permalink:hover {
    padding: 0;
    background-color: rgba(0, 0, 0, 0.6)!important;
    border: 1px solid #e6ecf0;
    border: 1px solid rgba(0, 0, 0, 0.1);
}
.permalink:focus {
    padding: 0;
    background-color: rgba(0, 0, 0, 0.6)!important;
    border: 1px solid #e6ecf0;
    border: 1px solid rgba(0, 0, 0, 0.1);
}
.permalink-tweet:hover,
.permalink-tweet:focus {
    background-color: transparent!important;
}
.Gallery-media,
.Gallery-content {
    background: transparent !important;
}
.stream-item.separated-module {
    margin-top: 0px!important;
    margin-bottom: 0px!important;
}
.MomentGuideNavigation-container {
    height: 50px!important;
}
.MomentsGuidePage-capsules {
    margin: 0px auto 60px!important;
    padding-top: 24px!important;
}
.stream-item+.stream-item.separated-module:not(.no-header-background-module),
.stream-item.separated-module+.stream-item:not(.no-header-background-module),
.PromptbirdPrompt-streamItem.separated-module+.stream-item:not(.no-header-background-module) {
    border-top: 1px solid var(--borders)!important;
}
.ProfileHeading-toggleItem.is-active,
.ProfileHeading-toggleItem.is-active:hover,
.ProfileHeading-toggleItem.is-active:focus {
    color: #657786;
    cursor: default;
    font-weight: bold;
    text-decoration: none;
}
}
