@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("portal-akademik.unhas.ac.id") {
.nobreak > table:nth-child(8) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(4) > div:nth-child(1) {
    height: 2.1cm !important;
    background-image: url("/*[[avatar]]*/") !important;
    background-position: center top;
    background-repeat: no-repeat;
    background-size: cover;
  }
.nobreak > img:nth-child(1):before, .nobreak > table:nth-child(8) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(4) > div:nth-child(1) > center:nth-child(3) {
    display: none !important;
  }
.nobreak > img:nth-child(1) {
    color: transparent !important;
    text-indent: -20px !important;
}
.nobreak > img:nth-child(1), .header-khs > img {
    background-image: url("data:image/jpeg;base64,") !important;
    background-position: center center;
    background-repeat: no-repeat;
    background-size: contain;
    height: 56px !important;
    width: 45px !important;
    color: transparent !important;
  }
}
