css:
@-moz-document domain("blackboard.stlcc.edu") {
/* course top nav */
.breadcrumbs {
    background: #00315c;
    color: #fff;
}
.breadcrumbs a{
    color: #fff;
}
.breadcrumbs .placeholder {
    color: #9adaff;
}
.path a:hover, .path a:focus, .simplePath a:hover, .simplePath a:focus {
    color: #fff;
    text-decoration: underline !important;
}
.switcherLabel {
    color: #ccc;
}

/* course menu */
#courseMenuPalette {
    background-color: #000;
}
#courseMenuPalette div.navPaletteContent {
    background-color: #000 !important;
}
.menuWrap {
    background-color: #000;
}
#courseMenuPalette ul {
    background-color: #000 !important;
}
.listCm .subhead h3 {
    background: transparent !important;
}
.subhead {
    background-color: #00315c !important;
}
#courseMenuPalette_contents li.subhead h3 span {
    color: #fff!important;
}

/* 'add' menu */
.nav .sub ul li a {
    color: #fff;
}

/* add content menus */
.actionBar ul {
    background: #000;
    color: #ccc;
}
.nav .sub .flyout ul {
    background: black;
}
.actionBar ul li a{
	color: #9ADAFF !important; 
}

/* ****************** */
/* modules */
.container {
    background-color: darkgrey;
}
.portlet h4 {
    color: inherit;
}
.collapsible {
    background-color: #000;
    color: #ccc;
}
.collapsible a{
	color: #9ADAFF;
}
.noItems, .additional+p .warnFont {
    color: #ccc;
}
.portlet h3, #lightbox h3, .portlet div.portletBlock h4, .portlet div.blockContents h4, .flyout h3, .labelMenuItem {
    color: #fff;
}


/* pane tabs */
.paneTabs {
    background: #00315c;
    border: 1px solid #000;
}
.paneTabs a{
    color: #9adaff;
}
.paneTabs .active a, .paneTabs .active a:hover, .paneTabs .active a:focus, .paneTabs .activepersonal a, .paneTabs .activepersonal a:hover, .paneTabs .active h3 {
    color: #fff;
    font-weight: bold;
}

/* generic elements */
.genericButton, .genericButtonImg, input.genericButton[type="submit"], .browse, .browseIcon, .rumble h3+ul>li>a, .rumble h3+ul>li>a, .rumble_top h3+ul>li>a, .rumble_top h3+ul>li>a, .paging a, .itemHeaderControl>a, .actionMenuButton, .button-3, .button-3-img, .button-4, .button-4-img, #toolsSearchBox+.button-4, .treeContainer .tree li a.button-4-img, .rumble a, .rumble_top a, .rumble .abutton, .rumble_top .abutton {
    background-color: #000;
    color: #9adaff;
    border: 1px solid;
    box-shadow: none;
}
.nav .sub ul, .nav.gb_currView .sub>ul, .menumini ul, .quickAddPal, .keyboardAccess, span.currentTags, .liveArea, .liveArea-slim, .previewArea, .jumptopage, .panelTabs li.active a.edit, .panelTabs li.active a.edit:hover, .panelTabs li.active a.edit:focus, .flyout, .bcContent .flyout, ul.tree li a+img+a:focus, ul.tree li img+img+a:focus, div.treeContainer ul.tree li a.itemActive, #learningUnitToc ul.tree li a.itemActive, ul.tree li a.itemActive, #loginLang ul{
    background-color: #000;
}
}
