css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("tecdigital.tec.ac.cr") {
  
  .ui-widget-content{
    background-image: none;
  }
  
  #top-container, #footer, #investigation, #news, #news_slider, #news_slider .new, #news_slider .news_navigation, #loginbox, #wrapper, body, #contenido, #Historial #barra_ponderado, #Historial #barra_porcentaje_aprobacion, #tRegistro thead td, #tRegistro tbody td, #tcitas_Matricula thead tr, #tcitas_Matricula tbody tr:nth-child(2n), #tPromedioPonderado thead tr, #tPromedioPonderado tbody tr:nth-child(2n), #contenendor_plan, #jsPlumbContainer, table#tHorario.celdas_alternadas thead td{
   background-color: #182128;
  }
  
  #tPromedioPonderado tbody tr:nth-child(2n+1), #tcitas_Matricula tbody tr:nth-child(2n+1){
    background-color: #253032;
  }
  
  #tRegistro thead td, #tRegistro tbody td, #tSimbologia, #tPromedioPonderado{
    color: #aaa;
  }
  
  table#tHorario.celdas_alternadas tbody tr, #tcitas_Matricula, encabezado_expandible::before{
    color:#fff;
  }
  
  .con_Texto{
    background-color: #282828;
  }
  
  #Contenedor_periodos td:nth-child(2n+1), table#tHorario.celdas_alternadas tbody tr:nth-child(2n+1){
    background-color: #222532 !important;
  }
  
  #Contenedor_periodos td:nth-child(2n), table#tHorario.celdas_alternadas tbody tr:nth-child(2n){
    background-color: #262936 !important;
  }
  
  #tabs .ui-tabs-nav li.ui-state-hover{
    background-color: #bbb;
  }
  
  #Historial #detalle_cursos{
    color: #777;
  }
  
  #firmadigital{
   background-color: #252525;
  }
  
  #tabs{
   background-color: #131313;
    border-color: #000;
  }
  
  .portlet-header, .portlet-header h1, .list-table caption, .cal-table-caption{
    background-color: #1d1d1d;
    background: #1d1d1d;
    border: 0px black dotted;
    color:#bbb;
  }
  
  table.list-table, .list-table tr.list-header{
    border-color: #000;
    background-color: #1d1d1d;
  }
  
  .list-table tr.odd{
    border-color: #000;
    background-color: #111;
  }
  
  .list-table tr.even{
    border-color: #000;
    background-color: #222;
  }
  
  section#sub-navigation ul li a{
    border:0px solid #000;
    background: #222;
  }
  
  section#main-menu-td nav li a:hover{
    background:rgba(11,46,73,0.5);
    color: #fff;
  }
  
  #cal-table-day th{
    border-color: #000;
    background-color: #1d1d1d;
  }
  
  #cal-table-day tr{
    border-color: #000;
    background-color: #000;
  }
  
  table.topnavbar{
    border-color: #000;
    background-color: #000;
  }
  
  table#at-a-glance th, table#at-a-glance td.active{
    border-color: #000;
    background: #000;
  }
  
  table#at-a-glance td.inactive{
    border-color: #000;
    background: #222;
  }
  
  table#at-a-glance td.today{
    border-color: #000;
    background: #666;
  }
  
  table.topnavbar span.active{
    border-color: #000;
    background-color: #222;
  }
  
  #cal-table-day tr [class=day-time-1] a:hover{
    color: #fff;
  }
  
  #cal-table-day tr [class=day-time-2] a:hover{
    color: #fff;
  }
  
  #cal-table-day tr [class=day-time-1]{
    border-color: #000;
    background-color: #111;
  }
  
  #cal-table-day tr [class=day-time-2]{
    border-color: #000;
    background-color: #222;
  }
  
  #cal-table-day tr [class=day-event-1]{
    border-color: #000;
    background-color: #111;
  }
  
  #cal-table-day tr [class=day-event-2]{
    border-color: #000;
    background-color: #222;
  }
  
  .calendar-Item{
    border-color: #000;
    background-color: #333;
  }
  
  .calendar-Item a:link{
    color: #aaa;
  }
  
  .calendar-Item a:hover{
    color: #fff;
  }
  
  #InfoPersonal #divInfoPersonal span,#InfoPersonal #divInfoAcademica span{
    color:#fff;
  }
  
  section#sub-navigation, section#sub-navigation ul, section#sub-navigation li, a.button, button#cita_matricula, button#guia_horario,
  #disconnect_1, #join_1, #descargar_pdf, #seleccionar_plan, #actualizar_cursos2{
    color: #ddd;
    border:0px;
    margin:0px;
    background: #3d3d3d;
  }
  
  .h1 {
    background: #000;
    color: #000;
  }
}
