css:
@-moz-document domain(feedly.com) {
/* START STYLE */

.fx .container,
.u100Entry,
.entryBody {
    width: 100% !important;
    max-width: 100% !important;
}
.entryholder {
    padding-bottom: 0 !important;
}
.entryholder .fx-button.secondary.visitWebsiteButton {
    display: none !important;
}
.shareBarHolder {
    margin-top: 0 !important;
    margin-bottom: 0 !important;
    position: absolute;
    top: 33px;
    right: 33px;
}
.shareBarHolder .headerInfo {
    margin-top: 0 !important;
}
.headerInfo-expanded-img {
    display: none !important;
}
.shareBarHolder:hover .headerInfo-expanded-img,
.headerInfo-expanded-img.icon-fx-more-ios-md-black {
    display: inline-block !important;
}
.entryBody img,
.entryBody video,
.entryBody embed {
    max-width: 100% !important;
    width: auto !important;
}

/* END STYLE */
}
