css:
@-moz-document domain("reddit6.com") {
div#header_container {
    zoom: 0.6;
}
div#leftNav {
    zoom: 0.7;
}
::-webkit-scrollbar {
    background: #111;
}
::-webkit-scrollbar-thumb {
    background: #444 !important;
}
body.leftPaneOpen #ColumnContainer {
    width: -webkit-calc(100% - 121px);
    margin-left: 120px;
}
.plm a {
    text-decoration: none;
}
body {
    /* zoom: 1.5;*/
}
body,
html {
    background: black;
}
div#leftNav {
    filter: invert(90%);
}
div#leftNav::-webkit-scrollbar {
    background: white;
}
div#header_container {
    filter: grayscale(100%);
    background: red;
}
#Header button.redButton {
    color: black;
}
div#Header {
    background: #191919;
    border-bottom: none;
}
#Nav #searchNav input {
    background: #2b2b2b;
}
.stream {
    background: black;
}
.stream>.post {
    background: #151515;
    border-color: #333;
}
.stream .post .postContent>.title {
    color: #b9b9b9;
}
.topComments {
    filter: invert(90%);
}
body.leftPaneOpen #leftNav {
    height: 100%;
}
body.leftPaneOpen #ColumnContainer {
    height: 100%;
}
div#ColumnContainer {
    background: black;
}
.container.stickleft {
    filter: invert(90%);
    zoom: 0.8;
}
div#PostView {
    margin-left: 235px;
}
div#CommentContent {
    filter: invert(90%);
}
#PostView .header,
#PostView .commentsHeader {
    filter: invert(90%);
}
button {
    filter: invert(90%);
}
.stream .streamPostOptions {
    background: #191919;
    border: 1px solid #252525;
    color: #777;
    /* zoom: 0.7; */
}
.stream .post:hover .streamPostOptions {
    display: none;
}
.stream .post:hover .streamPolitics {
    right: 11px;
}

.stream .post .smartContent>.infoDescription {
    filter: invert(90%);
}


.menubarDropdown {
    top: 36px;
    zoom: 0.7;
    filter: invert(90%);
    border: 1px solid white;
}

.menubarDropdown .node.header {
    background: #ddd;
    color: black;
}

.politics {
    filter: invert(90%);
}

.contextMenu {
    filter: invert(90%);
}

.contextMenu .contextOptions {
    zoom: 0.8;
}



.complexAlertContainer {
    filter: invert(90%);
}

.complexAlertContainer button {
    filter: grayscale(90%);
}


#PostView .Comment.OP>.AuthorContainer>.Author {
    background: #0ccaff;
    border: 1px solid #ddd;
    color: black !important;
    filter: hue-rotate(100deg);
}


#PostView .Comment.cc.newComment {
    filter: hue-rotate(310deg) drop-shadow(0px 0px 1px yellow);
}.stream .topComments .option .newComments {
    filter: hue-rotate(170deg);
    color: black;
}

::selection {
     color:black; 
     background: aquamarine;
}

.contextOptions #fb, .contextOptions
 #twitter, .contextOptions #google {
    display: none;
}

.loadingcss, .LoadingPost, .individualColumn.loading {
    background-color: #191919;
    color:white;
}

#PostView .commentsStick.stick {
    left: 335px;
    zoom: 0.7;
    background: #f3f3f3;
    border: 1px solid #fff;
    border-left: none;
}

#PostView .Comment.cc.deleted {
    background: #d9f5f5;
}

.smartContent .Comment.c {
    filter: invert(90%);
}


.stream .gilded {
    filter: invert(100%);
}

.showMoreComments {
    filter: invert(90%);
}


div.complexAlertContainer div#imageContainer {
    filter: invert(100%);
}

.md {
    color: #a4a4a4;
    filter: invert(100%);
}

div#global-zeroclipboard-html-bridge {
    zoom: 0.79;
}
}

@-moz-document domain("reddit6.com") {
.container.stickleft img.Thumb {
    filter: invert(100%);
}

.stream .post .smartContent>.infoDescription>.more {
    z-index:9999;
}

.complexAlertContainer div#bareSourceImages {
    filter: invert(100%);
}

.infoDescription .more {
    z-index: 999;
}

:root body.leftPaneOpen #ColumnContainer
{
        width: 100%;
    margin-left: 0;
}

:root div#leftNav {
    left: -140px;
    opacity: 0.1;
    transition: all cubic-bezier(1, -0.26, 0.76, 1.09) 0.3s;
    z-index: 9999;
    position: absolute;
}

:root div#leftNav:hover {
    left: 0;
    opacity: 1;
}

video.gfy_custom {
    max-width: 60%;
    margin: 0 auto;
    display: block;
    width: auto;
}
.stream .post img {
    background: white;
}

blockquote.twitter-tweet {
    color: #aaa;
}
}
