css:
@-moz-document domain("najserialy.com"), domain("najserialy.to")
{
body {
	background-color: #292929;
	font-family: 'Open Sans', sans-serif;
	font-weight: 400;
	position: relative;
	padding-bottom: 130px;
	min-height: 100%;
	background-repeat: no-repeat;
	background-position: center 70px;
}
.serialList .titleFirst {
	color: #2979f2;
	font-size: 17px;
	font-weight: 500;
}
.FSdetail {
	background-color: #242424;
	padding-top: 15px;
	padding-bottom: 15px;
	margin-bottom: 20px;
}
.latest .episodes .item:hover .overlay {
	opacity: 1;
	filter: alpha(opacity=100);
	-webkit-transform: rotateY(0) scale(1);
	transform: rotateY(0) scale(1);
}
.latest .movies .item .link .overlay .icon {
	-webkit-transform: translate(-50%, -50%);
	transform: translate(-50%, -50%);
	font-size: 54px;
	color: rgba(165, 162, 162, 0.50);
}
.latest .episodes .item .overlay .icon {
	-webkit-transform: translate(-50%, -50%);
	transform: translate(-50%, -50%);
	color: rgba(255, 255, 255, 0.5);
	top: 73%;
	left: 55%;
}
.head .control .user .dropdown .dropdown-menu {
	background-color: #191919;
	left: 50%;
	transform: translateX(-50%);
	-webkit-transform: translateX(-50%);
	-ms-transform: translateX(-50%);
}
.latest {
	background-color: #212121;
	margin: 10px;
	padding: 5px 20px;
}
.serialList ul {
	list-style: none;
	margin-bottom: 12px;
	padding-left: 15px;
}
.latest .episodes .item .info .titulky.active, .latest .episodes .item .info .viewed, .latest .episodes .item .info .dubbing {
	width: 22px;
	height: 17px;
	vertical-align: top
}
.serialList ul li {
	padding: 2px 0;
}
.FSdetail .wrap .info .text.episodes .panel .panel-heading {
	border-radius: 0;
	background-color: #2979f2;
}
.serialList ul li a {
	display: block;
	color: #f5eeeb;
	transition: all 0.12s ease-in-out;
}
.serialList ul.alphabet li {
	background-color: #171717;
}
.filter {
	background-color: #212121;
	margin-bottom: 20px;
}
.filter .select .category button, .filter .select .category input, .filter .select .category select,
.filter .select .name button, .filter .select .name input, .filter .select .name select {
	color: #5a5a5a;
}
#comment-form, #comments {
	background-color: #242424;
}
.control-group .control--checkbox .control__indicator:after {
	border: solid #2979f2;
}
#comments h3, #comment-form h3 {
	color: #a9a9a9;
	padding-bottom: 9px;
	border-bottom: 1px solid #a9a9a9;
}
#comments .comment .info span {
	color: #ccc;
	font-weight: 500;
}
#comment-form {
	margin-bottom: 10px;
}
#comment-form #text {
	color: #a9a9a9;
	background-color: #1c1c1c;
	border: 0;
}
#comment-form button,
.paginate ul.pagination li.active a, .paginate ul.pagination li.active:hover a {
	background: #2979f2;
	color: #fff;
}
.paginate ul.pagination {
	background-color: #191919;
}
.paginate ul.pagination li a:focus, .paginate ul.pagination li a:hover {
	background: #272727;
	color: #fff;
}
.footer {
	background: #212121;
	padding: 20px 0;
}
.footer .links li a {
	color: #ffffff;
	transition: color 300ms ease;
}
.FSdetail .wrap .info .text .names h2 a {
	color: #ddd;
	transition: all 0.3s ease;
}
.actions {
	display: table-header-group;
}
.btn-downld > a, a.trailer {
	background: #2979f2;
	background-color: #2979f2;
}
text.timer, text {
	fill: #2979f2;
}
circle.circle {
	stroke: #2979f2;
}
.wrapper.container {
	padding-bottom: 20px;
}
.btn-social, .ellip-line {
	white-space: nowrap;
	text-overflow: ellipsis;
}
.base .btn-orange, .menu {
	text-transform: uppercase;
}
.serials.filt>a {
	background: #2979fA;
	border: 4px solid #2979fA;
}
.watched {
	color: #2979f2 !important
}
.watched-b .link img {
	background-color: #2979f2 !important;
}
.btn:focus, button:focus, input:focus, textarea:focus {
	outline: 0;
}
.head .control .user.bt li {
	border-right: 1px solid #212123;
}
.head .control .user .dropdown .user-avatar {
	border: 1px solid #212123;
}
.head .control .user .dropdown .dropdown-menu.notifi {
	border-top: 1px solid #232321;
}
.head .control .user .dropdown .dropdown-menu.notifi li {
	border-bottom: 1px solid #212123;
}
.head .control .user .dropdown .dropdown-menu.notifi li .notifications li a .image {
	width: 20%;
}
.head .control .user .dropdown .dropdown-menu.notifi li .notifications li a h4,
.head .control .user .dropdown .dropdown-menu.notifi li .notifications li a p {
	width: 80%;
}
.head .control .user .dropdown .dropdown-menu li.header {
	border-bottom: 1px solid #292929;
}
.alert {
	background-color: #141414 !important;
	border-color: #292929 !important;
}
.menu {
	background-color: #2979f2;
	color: #fff;
	height: 45px;
}
.menu .collapse {
	display: block;
}
.menu ul li {
	padding-bottom: 5px;
}
.menu ul li a.active, .menu ul li a:hover {
	background-color: #292929;
	color: #2979f2;
}
.wrapper .wrap h2 {
	margin: 10px 0 14px;
	color: #f5f5f5;
	font-weight: 400;
}
.base h2.title {
	margin: 10px 0 14px;
	color: #f5f5f5;
	font-weight: 400;
}
.base .form-group .form-control {
	color: #a9a9a9;
	border-radius: 0;
	border-color: #3e3e3e;
	background-color: #0f0f0f;
}
.base .btn-orange {
	background-color: #2979f2;
	color: #fff;
	border-radius: 0;
}
.base.profil .info #file {
	background-color: #141414;
	color: #a9a9a9;
}
.base.request .item:hover {
	background-color: #292929;
}
.latest h2.title {
	margin: 10px 7.5px 14px;
	color: #f5f5f5;
	font-weight: 400
}
.latest .movies.viewed li:focus a .fa-check.active, .latest .movies.viewed li:hover a .fa-check.active {
	color: #2979f2;
}
.latest .movies .item .link .info {
	font-weight: 600;
}
.latest .movies .item .link:hover .play {
	top: -3px;
	width: 21px;
}
.latest .movies .item .link:hover .play .play-icon {
	font-size: 25px;
}
.latest .movies .item .link:hover .play .play-icon:before {
	content: "\f4ad";
}
.latest .episodes .item {
	background-color: #191919;
	text-align: center;
}
.latest .episodes .item img {
	height: 142px;
}
.latest .episodes .item .overlay {
	text-align: center;
}
.latest .episodes .item .overlay .icon:before {
	content: "\f215";
}
.latest .episodes .item .info a {
	color: #f6f6f6;
}
.FSdetail .wrap .players .relPlayer .overlay {
	background: rgba(33, 33, 33, 0.85);
}
.FSdetail .wrap .players .relPlayer .overlay .ad-en {
	background: #212121;
	color: #a9a9a9;
}
.FSdetail .wrap .players .relPlayer .overlay .ad-en .ion-android-hand {
	font-size: 100px;
	color: #e6403d;
}
.FSdetail .wrap .players .relPlayer .overlay .reklama .close, .FSdetail .wrap .players .relPlayer .overlay .rekRPlama .close {
	padding: 0 6px 2px;
	font-weight: 400;
}
.FSdetail .wrap .players .playerTabs li.active a, .FSdetail .wrap .players .playerTabs li:hover a {
	background-color: #2979f2;
	color: #fff;
}
.FSdetail .wrap .players .playerTabs li.arrow.first {
	border-left: 1px solid #272727;
}
.FSdetail .wrap .info .image img {
	border: 5px solid #181818;
}
.FSdetail .wrap .info .image .actions .other button i {
	vertical-align: inherit;
}
.FSdetail .wrap .info .image .actions .other button.viewed.active, .FSdetail .wrap .info .image .actions .other button.viewed:hover {
	color: #ec2b2b;
}
.FSdetail .wrap .info .image .actions .other button.viewed i {
	vertical-align: text-top;
}
.FSdetail .wrap .info .image .actions .views {
	background-color: #141414;
	color: #3b763b;
}
.FSdetail .wrap .info .image .actions .rate .dislike, .FSdetail .wrap .info .image .actions .rate .like {
	background-color: #141414;
}
.FSdetail .wrap .info .text {
	color: #a9a9a9;
	border-left: none;
}
.FSdetail .wrap .info .text .tbl .inf {
	border-top: 1px solid #515151;
}
.FSdetail .wrap .info .text .tbl .inf .value,
.FSdetail .wrap .info .text .tbl .inf .value a {
	color: #bcbcbc;
}
.alert.alert-dismissible .close:hover {
	color: #efefef;
}
.FSdetail .wrap .info .text.episodes .panel .panel-body li, .base.request .item, .latest .movies.viewed li, .wrapper .wrap, .base {
	background-color: #212121;
}
.FSdetail .wrap .info .text.episodes .panel .panel-body li a .fa-check, .FSdetail .wrap .info .text.episodes .panel .panel-body li a .fa-play-circle {
	color: #444;
}
.latest .movies.viewed li:focus a .fa-check, .latest .movies.viewed li:hover a .fa-check {
	color: #555;
}
.latest .episodes .item .info a span, .latest .episodes .item .info .more {
	color: #666;
}
.FSdetail .wrap .info .image .actions .other button.favorite, .FSdetail .wrap .info .image .actions .other button.viewed {
	color: #3f3f3f;
}
.serialList ul.alphabet li:hover, .filter .select ul.alphabet li.active, .filter .select ul.alphabet li:hover {
	background-color: #2979f2;
}
#comment-form button:hover, .base .btn-orange:hover {
	background-color: #054DB7;
}
.autors h2, .autors h3, .control-group .control, .base.login ul li strong {
	font-weight: 400;
}
.control-group .control input:checked ~ .control__indicator,
.head .search .search-form input:focus, .head .control .user .dropdown .dropdown-menu.notifi li .notifications li.active a {
	border-color: #2979f2;
}
.FSdetail .wrap .info .text.episodes .panel .panel-heading h4 a.collapsed:after,
.latest .movies .item .link:hover .remove:hover .remove-icon:before, .filter .reset:hover:before {
	content: "";
}
.FSdetail .wrap .info .text.episodes .panel .panel-body li a .titulky,
.FSdetail .wrap .info .text.episodes .panel .panel-body li a .dubbing {
	width: 22px;
	height: 17px;
}
.FSdetail .wrap .info .text .tbl .inf .value a.trailer,
.footer .scrollTop a, .serialList ul li a span.new {
	background: #2979f2;
}
.control-group .control input:checked:focus ~ .control__indicator, .control-group .control:hover input:not([disabled]):checked ~ .control__indicator,
.filter .reset, .latest .movies .item .link:hover .remove {
	background: 0 0;
}
.FSdetail .wrap .info .text.episodes .panel .panel-body li:hover a, .FSdetail .wrap .info .text.episodes .panel .panel-body li.active a,
.alert.alert-dismissible .close, .latest .movies.viewed li:focus a, .latest .movies.viewed li:hover a {
	color: #bbb;
}
.FSdetail .wrap .info .text.episodes .panel .panel-body li:hover a .fa-cc,
.FSdetail .wrap .info .text.episodes .panel .panel-body li:hover a .fa-play-circle,
.FSdetail .wrap .info .text.episodes .panel .panel-body li.active a .fa-cc,
.latest .movies.viewed li:focus a .fa-cc, .latest .movies.viewed li:focus a .fa-play-circle,
.latest .movies.viewed li:hover a .fa-cc, .latest .movies.viewed li:hover a .fa-play-circle,
.latest .movies .item a.name {
	color: #ccc;
}
.FSdetail .wrap .info .text.episodes .panel .panel-body li a span,
.FSdetail .wrap .info .text .names h2, .latest .movies.viewed li a .name {
	color: #ddd;
}
.FSdetail .wrap .info .text.episodes .panel .panel-body li:hover a span, .FSdetail .wrap .info .text.episodes .panel .panel-body li.active a span,
.latest .movies.viewed li:focus a span, .latest .movies.viewed li:hover a span {
	color: #eee;
}
.FSdetail .wrap .info .text.episodes .panel .panel-heading h4 a,
.FSdetail .wrap .info .text .names h2 a:focus, .FSdetail .wrap .info .text .names h2 a:hover,
.FSdetail .wrap .info .text .names h3 a:focus, .FSdetail .wrap .info .text .names h3 a:hover,
.FSdetail .wrap .info .text .names h4 a:focus, .FSdetail .wrap .info .text .names h4 a:hover,
.serialList ul.alphabet li:hover a, .filter .select ul.alphabet li.active a, .filter .select ul.alphabet li:hover a,
.menu ul li a, .latest .episodes .item .overlay .icon:hover, .latest .movies .item .link .overlay .icon:hover {
	color: #fff;
}
.FSdetail .wrap .info .image .actions .rate .dislike.active, .FSdetail .wrap .info .image .actions .rate .dislike:hover,
.FSdetail .wrap .info .image .actions .rate .like.active, .FSdetail .wrap .info .image .actions .rate .like:hover,
.FSdetail .wrap .info .image .actions .other button, .FSdetail .wrap .players .playerTabs,
.FSdetail .wrap .players .relPlayer.serial, .base.profil .info #file:hover,
.FSdetail .wrap .info .text.episodes .panel, .FSdetail .wrap .info .text.episodes .panel .panel-body,
.FSdetail .wrap .info .text.episodes .panel .panel-body li:hover, .FSdetail .wrap .info .text.episodes .panel .panel-body li.active,
.latest .movies.viewed, latest .movies.viewed li:focus, .latest .movies.viewed li:hover {
	background-color: #181818;
}
#comment-form .login a, #comment-form .comm-reply-msg span, #comments .comment .info span.admin, #comments .comment .text .reply:hover,
.FSdetail .wrap .info .image .actions .other button.report, .FSdetail .wrap .info .rating .nula span,
.FSdetail .wrap .info .text.episodes .panel .panel-body li:hover a .fa-check.active,
.FSdetail .wrap .info .text.episodes .panel .panel-body li.active a .fa-check.active,
.FSdetail .wrap .info .text.episodes .panel .panel-body li.active a .fa-play-circle,
.FSdetail .wrap .info .text.episodes .panel .panel-body li a .fa-check.active,
.FSdetail .wrap .info .text .tbl.serial .inf .name .star:after, .FSdetail .wrap .info .text .tbl .inf:after,
.serialList ul li a span.favorite, .serialList ul li a:hover, .latest .movies.viewed li a .fa-check.active,
.head .control .user.bt li.active a, .head .control .user.bt li.active i, .head .control .user.bt li:hover a, .head .control .user.bt li:hover i,
.head .control .user .dropdown.open, .head .control .user .dropdown.open a, .head .control .user .dropdown.open i,
.head .control .user .dropdown.open .fa, .head .control .user .dropdown:hover .fa, .footer .right .scrollTop .fa, .footer .links li a.active,
.footer .links li a:hover, .autors h3:after, .autors .right ul li.active a, .base .action a:hover {
	color: #2979f2;
}
#comment-form .comm-reply-msg, #comment-form .login,
#comments .comment .text, #comments .empty, .autors, .autors .right ul li a, .autors .item span,
.paginate ul.pagination li a, .latest .movies.viewed li a, .FSdetail .wrap .info .desc, .FSdetail .wrap .info .rating .nula,
.FSdetail .wrap .info .text.episodes .panel .panel-body li a, .FSdetail .wrap .info .text a,
.FSdetail .wrap .info .text .names h2, .FSdetail .wrap .info .text .names h3, .FSdetail .wrap .info .text .names h4,
.FSdetail .wrap .info .text .names h2 a, .FSdetail .wrap .info .text .names h3 a, .FSdetail .wrap .info .text .names h4 a,
.base .action, .base.profil .info, .FSdetail .wrap .players .playerTabs li a, .base.login ul, .base.contact .text, .base.request {
	color: #a9a9a9;
}
.menu button, .info > .image.serial > .img-responsive, .FSdetail .overlay {
	display: none;
}
::-webkit-scrollbar
{
  max-width: 10px !important;
  max-height: 10px !important;
  background: #1d1d1d !important;
}

::-webkit-scrollbar-track, ::-webkit-scrollbar-corner
{
  background: #1d1d1d !important;
}

::-webkit-scrollbar-thumb
{
  background: rgba(175, 175, 175, .5) !important;
}

::-webkit-scrollbar-thumb:hover
{
  background: rgba(65, 131, 196, .8) !important;
}
}
