css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document 
url-prefix("http://mail.google."),
url-prefix("https://mail.google.") {

body {
margin:0 !important;
padding:0 !important;
}
/*Remove Logo*/
#ds_inbox img {
display:none;
}
/*Margins around search box*/
div[style="padding: 0pt 1ex;"] {
margin:0 !important;
padding:5px 0 0 10px !important;
}
#mt1 {
margin:0 !important;
padding:0 !important;
}
/*Remove space on top of "Contacts"*/
#nds + div { 
display:none !important;
}
/*"Contacts" normal font weight*/
.nl #cont b {
font-weight:normal !important;
}
/*Navigation of left*/
#nds {
margin:0 !important;
padding:0 !important;
}
/*Border on left of mail box*/
.fs {
border-left-width:3px !important;
}
/*Memory used/left*/
.fq {
margin:0 !important;
padding:0!important;
position:absolute !important;
top:22px !important;
right:1px !important;
font-size:9px !important;
}
/*Display HTML/with chat/without chat*/
.fv {
margin:0 !important;
padding:0 !important;
position:absolute !important;
bottom:1px !important;
left:1px !important;
font-size:10px !important;
display:none !important;
}
/*Remove margins and padding from Compose*/
#cm_compose {
margin:0 !important;
padding:0 !important;
}
#of,#ob,#oe,             /*Remove Extra*/
.fcs,                    /*Remove Copyright*/
.fspc,
.ft,
div[class^=rh],          /*Remove Ads*/
#nb_1,                   /*Remove Invites*/ 
#mt_cf1,                 /*Removes "Create a filter"*/
#s input + input + input,/*Remove "Search the web" button*/
#nt1                     /*Remove space below search box*/ 
{
display:none !important;
}
}
