css:
@-moz-document domain("www.skroutz.gr") {
/* Removal  */
.verticals-promo,
.js-card-bottom,
#featurette {
    display: none;
}
#sku_reviews_list > li > .actions,
#recommendations {
    background: none !important;
}
#sku_reviews_list > li > .actions > * {
    filter: brightness(.5) !important;
}
#categories_index header,
.card,
.bc::after,
#sku-info,
.scrollable,
.sku-card,
#suggestions_container,
div#suggestions_container ul#suggestions_list li.r_row
{
    background-image: none;
    background: linear-gradient(to bottom, #434b4d 0%, #3b4044 100%) !important;
    height: -moz-fit-content;
    border-color: grey;
    border-radius: 0px 0px 7px 7px;
}
.specs-container *,
.sku-card .sku-sections a.active,
.sku-sections > li {
    background: rgba(86, 95, 102, .34) !important;
}
.sku-card .sku-sections a:hover {
    background: rgb(158, 170, 178) !important;
    color: rgb(225, 235, 255) !important;
    filter: brightness(.9) !important;
    transition: all .2s ease-in-out !important;
}
header,
header::before {
    background: linear-gradient(to bottom, #313436 80%, rgb(65, 89, 107) 200%) !important;
    border-color: rgb(166, 177, 185) !important;
    border-bottom-style: outset !important;
}
[value="submit"],
[title="Skroutz.gr - Αρχική σελίδα"] {
    filter: invert(75%) brightness(1.25) saturate(60%) !important;
}
[value="submit"] {
    border: 1px solid rgb(150, 92, 42) !important;
}

/* login */
#modal_freak_content {
    background: rgba(100, 100, 105, .94) !important;
}
#modal_freak_content *:not(input):not(a) {
    color: white !important;
}
#modal_freak_content a:not(.btn-orange) {
    color: rgb(180, 190, 250) !important;
}
.social-block > form:not(.google):not(.facebook) {
    background: rgb(125, 108, 144) !important;
    margin: 2px !important;
    width: 48% !important;
    border-radius: 100px / 170px !important;
}
.social-block > form:not(.google):not(.facebook) > [type="submit"] {
    color: white !important;
}
document,
body {
    background: linear-gradient(to bottom, #44494d 0%, #000 100%);
}
#search-bar-input,
#search-bar-input::-moz-placeholder {
    background: rgb(140, 161, 182) !important;
    color: rgba(90, 80, 70);
    font-weight: bold;
    border: 1px solid rgb(170, 194, 207) !important;
}
.minisku.full > li {
    background-image: linear-gradient(to bottom, rgba(171, 182, 189, 0) 30%, rgba(135, 148, 159, .8) 100%);
    border-radius: 93px 93px 30px 30px;
    -webkit-border-radius: 93px 93px 30px 30px;
    border: 5px hidden #7cabc4;
}
.verticals {
    display: grid;
    grid-template-columns: repeat(3, 1fr);
    grid-gap: 5px 10px;
    width: -moz-available;
}

#categories > ol *{
    background-color: rgb(26, 34, 41) !important;
}
ol.verticals.content.cf *:hover{
    color: #64abe8 !important;
}
#categories > ol > * {
    filter: brightness(.9) saturate(.7) blur(1px) !important;
    transition: all .5s !important;
}
#categories > ol > *:hover {
    filter: none !important; 
    transition: all .5s !important;}

.price-content > span,
.availability span,
.js-card-head > * {
    filter: brightness(.55) !important;
}
.verticals > li {
    width: 100% !important;
}
.bc > nav,
header > .content {
    width: 100% !important;
    max-width: none !important;
    background: none !important;
}
.bc > nav > h2,
.bc > nav > h3,
.bc > nav > h4,
.bc > nav > h5 {
    margin-left: 0px !important;
    padding-left: 60px !important;
    margin-right: -50px !important;
}
.filters-list li > a::before,
.popular-searches a {
    border: 1px solid grey;
}
.paginator > li a,
.paginator span,
.sku-card .sku-sections a,
.sku-card,
.sku-sections > li,
.sku-card .sku-sections a:hover,
.sku-card .sku-sections a.active {
    border: none;
}
.sku-card .sku-sections,
.main-sidebar,
#specs,
.content,
#reviews {
    background: none !important;
}
.list h2 > a, .collapsable-title, .filter-option,.sku-specifications > a, .sku-reviews > a, .sku-also-viewed > a, .sku-comments-link > a, .sku-price-history > a, .availability, .description-top > p {
    color: rgb(255, 255, 255) !important;
}
.sub-items a {
    color: rgb(55, 55, 55) !important;
}
.details > h2 > a {
    color: rgb(0, 0, 0) !important;
}
.sub-items > h3 > a, .sku-info-actions * {
    color: rgb(13, 21, 26) !important;
}
.sub-items a:hover,
.card > h2 > a:hover, .description-more {
    text-decoration: none !important;
    color: whitesmoke !important;
}
html body#categories_index.logged-out header div.content form {
    z-index: 999 !important;
}
}
