css:
@-moz-document domain("www.e-shop.gr") {
  
document, body, .home-productsBuy-info, .web-footer-newsletter-section, .web-footer-links-section, #web_menu {
background-color: rgb(99, 99, 90) !important;
}
  
#web_header > table, .web-subheader-links, .web-subheader-actions, [style="text-align:left;padding:0 0 5px 10px;"], .primary-cta, .vitrina-boxlinks, .home-protaseis-cta, .home-more-link, .home-productsBuy-info-text a, .home-buy-cta {
filter: blur(0.5px)  hue-rotate(120deg) saturate(0.2) !important;
}
#web_header > table:hover, .web-subheader-links:hover, .web-subheader-actions:hover, [style="text-align:left;padding:0 0 5px 10px;"]:hover, .primary-cta:hover, .vitrina-boxlinks:hover, .home-protaseis-cta:hover, .home-more-link:hover, .home-productsBuy-info-text a:hover, .home-buy-cta:hover {
filter: blur(0px)  hue-rotate(120deg) saturate(0.5) !important;
}
  
  /* Στο καλάθι */
[style="text-align:left;padding:0 0 5px 10px;"]  {
transition: 0.2s ease-in !important; 
}
[style="text-align:left;padding:0 0 5px 10px;"]:hover, .home-buy-cta:hover {
position: absolute !important;
top: 80%;
transform: scale(1.8);
transition: .08s ease-out !important;  
z-index: 9999 !important;
}
    
#web_header {
background: linear-gradient(white 10%, rgba(0,0,0,0.7));
border-radius: 50px;
}
.web-subheader {
height: -moz-available;
}
.web-header-search-box {
color: white !important;
background: rgb(132, 131, 126);
width: 180%;
margin-left: 20px;  
}
.web-header-search {
margin-left: 160px;  
margin-top: 3px;
}

#navigation_vert > ul > li, #navigation_vert > ul > li > a.navlink {
color: white !important;  
background-color: rgb(120, 120, 113) !important;
}
#navigation_vert > ul > li, #navigation_vert > ul > li > a.navlink:hover:not([background='http://images.e-shop.gr/images/menu_logos_affiliates2.png'])  {
background-color: rgb(137, 132, 75) !important;
}  
  
#navigation_vert > ul > li.menuinline:not([background='http://images.e-shop.gr/images/menu_logos_affiliates2.png']) {
background: none !important;
background-color: rgb(255,40,40) !important;
border-bottom: 1px #fff solid;
}  
  
.menu_linksbox > a:hover, .menu_sublinksbox > a:hover, .home-productBuy-price > b {
color: White !important;
}
  
.panel-link-box, .vitrina_table_title  {
color: rgb(205, 205, 200) !important;
}
  
.home-protaseis-price, .home-protaseis-price > b > * , .web-subheader-breadcrumb a, A.faint_link1 {
color: rgb(240, 240, 250) !important;
}  
a.bread-link:hover {
color: rgb(20, 220, 250) !important;
}  
.web-subheader-actions a:hover {
color: rgb(100, 100, 100) !important;
text-shadow: 0px 0px 22px white !important;
z-index: 99999 !important;
}  
  
table.links-table-wrap {
border-color: #6FE7E9 !important;
} 
table.links-table-wrap:hover {
-webkit-box-shadow:0 2px 14px -4.7px black,0 1px 3px black;
box-shadow:0 2px 15px -4.7px black,0 1px 3px black;
}
    
.menu_eartxtcell {
background-color: rgba(120, 120, 120, 1) !important;
}

.menutcell, .menu_bodycell, .menu_linksbox > a, .menu_sublinksbox > a, td.links_table_title, td.links_table_links, .links_table_links_all, .product_table_title {
opacity: 0.95 !important;
background: none !important;  
background-color: rgba(190, 190, 180, 1) !important;
color: rgb(50, 50, 50) !important;  
border-left: 2px solid #CDCDCD !important;
font-size: 14px;  
font-weight: 600;  
}  
  
.home-more-link, .home-protaseis-cta {
border-radius: 50px 0px 50px 0px !important;  
border: 5px black !important;
}  
  
[name="mailaddr"] {
background: none !important;  
background-color: rgba(200,200,200, 1) !important;
color: rgb(10, 10, 10) !important;   
border-radius: 20px 50px 20px 50px/200px !important;  
border: black !important;
}
.submit_newsletter {
background-color: rgba(240,210,200, 1) !important;
color: rgb(10, 10, 10) !important;   
border-radius: 20px 50px 20px 50px/200px !important;  
width: calc(25% + 0px) !important;
}  
  
  
.vitrina_table_title{
height: 45px;
}
  
.web-title-link {
color: white !important;
}  
    
#cell_na_1, #cell_na_2, #cell_na_3, #cell_na_4, #cell_na_5,#cell_na_6, #cell_na_7, #cell_na_8 {
background-image: none !important;
color: white !important;
}
  
#vitrina_banner, #slides, .img-responsive, #right_panel, #web_footer_banner, #web_body > table > tbody > tr > td > p > a > img { 
display: none;
}  
}
