css:
@-moz-document domain(lesnumeriques.com) {
  
#content
{
  margin-top: 0;
}

#home-v5 .box-compare-counts
{
  top: -21px;
}

.body-v5
{
  margin-top: 60px;
}

.box-compare
{
  height: 103px;
  margin-bottom: 0;
}

.box-compare-content,
.box-compare-item
{
  top: -31px;
}

.main-header
{
  height: 52px;
  opacity: 0.6;
}

.main-header .main-header-logo
{
  background-position: -266px -55px;
  height: 31px;
  left: 127px;
  top: -4px;
  width: 40px;
}

.main-header .main-header-membership
{
  margin-top: 3px;
}

.main-header .main-header-nav-dropdown
{
  top: -1px;
}

.main-header .main-header-search
{
  top: -13px;
}

.main-header-categories
{
  margin-top: 21px;
}

.main-header-container
{
  height: 27px;
}

.main-header-search input[type=text]
{
  min-height: 24px;
  padding: 0px 8px;
}

.main-header-types
{
  top: 53px;
}

.main-header.mod-scroll-down .main-header-logo
{
  left: -31px;
}

.main-header.mod-scroll-down:hover
{
  opacity: 0.95;
}

a.cLink
{
  color: #7ABC1F;
}

body:not([data-user-id]):not([dir])
{
  margin-top: 50px;
}

#product-navigation
{
    -moz-transition: opacity 1.0s ease-out;
    -o-transition: opacity 1.0s ease-out;
	-webkit-transition: opacity 1.0s ease-out;
	opacity: 0.2;
	transition: opacity 1.0s ease-out;
}

#product-navigation:hover,
#product-navigation:focus,
#product-navigation:active
{
    -moz-transition: opacity 0.3s ease-out;
    -o-transition: opacity 0.3s ease-out;
	-webkit-transition: opacity 0.3s ease-out;
	opacity: 0.80;
	transition: opacity 0.3s ease-out;
}

#main.product-v4 #product-navigation
{
    margin-left: 15px;
}

#main.product-v4 #product-body #product-content .image-large {
    margin-left: auto;
}

}
