css:
@-moz-document domain(addic7ed.com) {

#comments .tabel90 tr:nth-child(2n+2)
{
	background: #EFEFEF;
}

#comments .tabel90 tr:nth-child(2n+3)
{
	background: #FFFFFF;
}

#comments .tabel90 tr:nth-last-child(3)
{
	background: #FFFFFF;
}

#containernews
{
	height: auto;
}

#footermenu
{
	color: black;
}

#footermenu a
{
	text-decoration: underline;
}

#footermenu a:hover
{
	background-color: #455B76;
	text-decoration: none;
}

#hBar .button.white
{
	padding-bottom: 5px;
	padding-top: 5px;
}

div#hBar
{
  position: relative;
  top: -5px;
}

#langs table
{
	width: 204px;
}

#season thead tr,
#langs thead tr
{
	text-shadow: none;
}

.NewsTitle,
.language,
form[name="newversion"] .tabel .NewsTitle,
.tabel195 .NewsTitle,
#lista > table
{
	background-color: #455B76;
}

.buttonDownload
{
	font: inherit;
}

.buttonDownload,
input[type="submit"].coolBoton[value="Send comment"],
input[type="submit"].coolBoton[value="upload"],
#lista > input.coolBoton[value="Replace"],
#lista > input.coolBoton[value="Multi Edit"],
#lista > input.coolBoton[value="Multi Save"]
{
	background: #C0CDD4;
	border: 2px #455B76 solid;
	border-radius: 0;
	box-shadow: none;
	color: #455B76;
	cursor: pointer;
	text-shadow: none;
}

.dropMenu
{
	border: 0 none;
}

.tabel70
{
	width: 98%;
}

.tabel70 .tabel tr:nth-child(2n+3)
{
	background: #FFFFFF;
}

.tabel70 .tabel tr:nth-child(2n+4)
{
	background: #EFEFEF;
}

.tabel70 .tabel tr:nth-last-child(1)
{
	background: #FFFFFF;
}

.tablecorner
{
	height: 15px;
	width: 15px;
}

.tablecorner img
{
	display: none;
}

.titulo,
font[color="blue"]
{
	color: #455B76;
}

H1
{
	color: #C0CDD4;
}

a,
#comments a,
form[name="multidl"] a,
#lista > table:not(first-of-type) a,
#lista > a
{
	color: #455B76;
}

body
{
	font-size: 11px;
}

body > center > center:last-of-type .NewsTitle,
.tabel .NewsTitle,
.topItem
{
	background-color: #444;
	color: white;
	font-size: 12px;
	font-weight: normal;
	height: 19px;
	padding-bottom: 2px;
}

body > center:nth-of-type(1) > table tr:nth-of-type(2)
{
	height: 42px;
	max-height: 42px;
}

body > center:nth-of-type(2)
{
	/*-13px;*/
	
	position: relative;
	top: -22px;
}

body center center > table:nth-of-type(1),
table[width="94%"] th[bgcolor="#009BCA"],
table[width="94%"] tr[bgcolor="#009BCA"],
table[width="94%"] td[bgcolor="#009BCA"],
.dropMenu,
body > center > table[width="90%"]
{
	background-color: #C0CDD4;
}

body center:first-of-type table td a img
{
	filter: invert(1);
	left: -90px;
	position: relative;
}

body center:first-of-type table:first-of-type tr:first-of-type td:first-of-type a
{
	display: inline-block;
	height: 140px;
	margin-top: -20px;
	overflow: visible;
	width: 250px;
}

body center:nth-of-type(2) br:nth-of-type(1),
body center:nth-of-type(2) br:nth-of-type(2)
{
	display: none;
	visibility: hidden;
}

form > div[align="center"],
p,
#sl > span,
#langs table tbody tr:nth-child(even),
body > center > center > b,
#qssShow > b,
#lista > table:first-of-type
{
	color: white;
}

html,
body,
center > table:nth-of-type(1),
#header
{
	background-color: #455B76;
}

img[src="http://cdn.addic7ed.com/images/addic7edheader.jpg"]
{
	height: 100px;
	width: 175px;
}

img[src="http://www.addic7ed.com/friends/addic7ed.jpg"]
{
	filter: hue-rotate(20deg) brightness(0.65) saturate(0.3);
}

input[type="submit"].coolBoton,
.button.white,
form[name="filter"] > input.coolBoton[value="Replace"],
form[name="filter"] > input.coolBoton[value="Multi Edit"],
form[name="filter"] > input.coolBoton[value="Multi Save"]
{
	background: white;
	border: 2px #C0CDD4 solid;
	border-radius: 0;
	box-shadow: none;
	color: #455B76;
	cursor: pointer;
	padding: 1px 10px;
	text-shadow: none;
}

script+br,
table+br,
p+p
{
	display: none;
	visibility: hidden;
}

span a,
#header font,
body > center > center > b > a
{
	color: #C0CDD4;
}

table[width="100%"][align="center"] td[width="50%"][align="center"]
{
	position: relative;
	top: -3px;
}

}
