css:
@-moz-document domain("0en.ru") {
body, html, p, .common_padding, .header_text {
    color: #e8f1f9 !important;
	font-family: 'Roboto', sans-serif !important;
} 
::-webkit-scrollbar-track
{
 -webkit-box-shadow: inset 0 0 0px rgba(0,0,0,0.1);
 background-color: white !important;
 z-index:-9999;
}
::-webkit-scrollbar
{
 width: 5px;
 background-color: rgba(255,255,255,0.1);
}
::-webkit-scrollbar-thumb
{
 background-color:#516982 ;
 border: 0;border-radius:5px;
}
.header_text {
    width: 100%;
    height: 70px;
    background-color: #516982;
    background-repeat: repeat-x;
    border-bottom: solid 0px #c6c7c2 !important;
-webkit-box-shadow: 0px 0px 12px 0px rgba(0,0,0,0.75);
-moz-box-shadow: 0px 0px 12px 0px rgba(0,0,0,0.75);
box-shadow: 0px 0px 12px 0px rgba(0,0,0,0.75);
}
textarea {
    font-family: 'Roboto', sans-serif !important;
    font-size:15px;
    border-color: #516982;
    border-top-color: #516982;
    border-right-color: #516982;
    border-bottom-color: #516982;
    border-left-color: #516982;
    border-radius:5px;
    -webkit-appearance: textarea;
    background-color: #e8f1f9;
    -webkit-rtl-ordering: logical;
    user-select: text;
    flex-direction: column;
    resize: auto;
    cursor: auto;
    white-space: pre-wrap;
    word-wrap: break-word;
    padding: 2px;
    -webkit-box-shadow: inset 0 0 0px rgba(0,0,0,0.1);
}
.common_color {
    background-color: #6784a1;
}
.inner-box-3, div#sub_head_users {
-webkit-box-shadow: 0px 0px 12px 0px rgba(0,0,0,0.75);
-moz-box-shadow: 0px 0px 12px 0px rgba(0,0,0,0.75);
box-shadow: 0px 0px 12px 0px rgba(0,0,0,0.75);
    background: #516982;
    color: #bcd4ea;
    padding: 10px 0 10px 0;
    text-align: left;
    position: relative;
    top: 0;
    width: 100%;
    margin: 0;
    margin-bottom: 0px;
}
.r1, .r2, .r3, .r4, .r5, .r6, .r7, .r8, .r9, .r10 {
    display: block;
    height: 0px;
    overflow: hidden;
    font-size: 1px;
    background-color:transparent;
}
.data_input {
    font-family: verdana;
    font-size: 14px;
    height: 26px;
    background-color: #e8f1f9;
    border-bottom: 1px solid #7e9ab5;
    border-left: 1px solid #7e9ab5;
    outline: 0;
    border-top: 2px solid #7e9ab5;
    border-right: 1px solid #7e9ab5;
    border-radius:5px;
    color: #516982;
}

h4 a {
    color: #78a7d5;
    text-decoration: none;
    padding-bottom: 2px;
}
h4 a:hover {
    text-decoration: none;
    padding-bottom: 2px;
    background-color:#516982;
    color:#8dc3f9;
}
.submit_buttons {
    background-image: url(https://pp.userapi.com/c638622/v638622250/4f7c4/JkoFIomzPpY.jpg);
    border: none;
    color: white;
    border-radius:5px;
    cursor: pointer;
    height: 30px;
    margin: 0;
    outline: 0;
    font-size: 15px;
    vertical-align: top;
    padding: 0 12px;
}
#rounded-box-3 b {
    background-color: #6784a1;
}
#vert_menu li a, #vert_menu li #current {
    color: #516982 !important;
    background: url(https://pp.userapi.com/c638622/v638622841/57b0e/SGKyncV9rtU.jpg) 0 -37px no-repeat !important;
    border-radius:9px;
    padding: 10px 0 10px 15px !important;
}
#vert_menu li a:hover, #vert_menu li #current {
    color: #ffffff !important;
    background: url(https://pp.userapi.com/c638622/v638622841/57b0e/SGKyncV9rtU.jpg) 0 -37px no-repeat !important;
    border-radius:9px;
    padding: 10px 0 10px 15px !important;
}
@font-face {
  font-family: 'Roboto';
  font-style: normal;
  font-weight: 300;
  src: local('Roboto Light'), local('Roboto-Light'), url(https://fonts.gstatic.com/s/roboto/v16/0eC6fl06luXEYWpBSJvXCBJtnKITppOI_IvcXXDNrsc.woff2) format('woff2');
  unicode-range: U+0460-052F, U+20B4, U+2DE0-2DFF, U+A640-A69F;
}

}
