css:
@-moz-document url-prefix('http://www.americanthinker.com/articles/'),
url-prefix('http://www.americanthinker.com/blog/'),
url-prefix('http://www.americanthinker.com/archives')
{
    #wrapper {
        margin-left: auto;
        margin-right: auto;
    }
    #wrapper *,
    main * {
        box-sizing: border-box;
    }
    main .follow_us {
        display: none;
    }
    main [class^="column_"] {
        width: auto !important;
        float: none !important;
        padding: 2rem 3rem !important;
    }
    @media screen and (min-width: 768px) {
        main.blog_post [class^="column_"] {
            padding: 2rem 0 !important;
        }
    }
    main.blog_post {
        padding: 0 2rem;
        background-color: #f2f2f2;
        border: 3px double #999;
        /* margin-top: 2rem  !important; */
    }
    header[role=banner] {
        content: "";
        display: table;
        clear: both;
        margin-bottom: 2rem;
    }
    .article blockquote {
        margin: 2rem 0.5rem 2rem 1rem;
        padding: 1rem;
        border: 3px double #aaa;
        font-style: italic;
        background-color: white;
        background-image: linear-gradient(120deg, #fdfbfb 0%, #ebedee 100%);
        background-image: linear-gradient(to top, #d5d4d0 0%, #d5d4d0 1%, #eeeeec 31%, #efeeec 75%, #e9e9e7 100%);
        background-image: linear-gradient(to top, #f3e7e9 0%, #e3eeff 99%, #e3eeff 100%);
        background-image: linear-gradient(145deg, #c4c5c7 0%, #dcdddf 52%, #ebebeb 100%);
        color: #555 !important;
    }
    .article img {
        box-shadow: 4px 7px 22px rgba(0, 0, 0, .4);
    }
    .article .social_tools img {
        box-shadow: none;
    }
    @media all and (max-width: 768px) {
        main [class^="column_"] {
            width: auto !important;
            float: none !important;
            padding: 2rem !important;
        }
        img {
            float: none !important;
            display: block;
            margin: 2rem auto !important;
            max-width: 100% !important;
            height: auto !important;
        }
        main.article {
            margin-top: 3rem;
        }
        span.button_menu img {
            height: inherit !important;
            background: rgba(0, 0, 0, .6);
        }
        .article_body {
            width: auto;
        }
        .article blockquote {
            margin: 2rem -0.250rem;
            padding: 1.6rem;
            box-shadow: 4px 7px 22px rgba(0, 0, 0, .4);
        }
    }
    main .side_links,
    main .link_list {
        width: 100%;
        margin-left: auto;
        margin-right: auto;
        font-size: 19px;
        line-height: 1;
    }
    main ul.link_list {
        line-height: 1.7;
        margin-bottom: 3rem;
        padding: 2rem;
    }
    .article_body {
        max-width: 100% !important;
        width: auto !important;
    }
    main.article,
    main.blog_post {
        border: 4px double #ccc;
        margin-bottom: 2rem;
    }
    main .blog_nav {
        margin-bottom: 0;
        border-top: 3px double #c1c1c1;
        border-bottom: 3px double #c1c1c1;
        padding-top: 7px;
        padding-bottom: 7px;
        text-align: center;
    }
    @media screen and (max-width: 767px) {
        main.article, main.blog_post {
            padding: 0
        }
        .social_tools {
            display: none;
        }
        main .blog_nav {
            margin-bottom: 2rem;
        }
    }
}
