css:
@-moz-document domain("allo.google.com") {
:root {
  --ate-color: var(--ate-pref-color, /*[[ACCENTCOLOR]]*/);
}

div.themedIncomingBubble.bubble.style-scope.user-text-message {
    border-radius: 4px !important;
    color: #263238 !important;
    background-color: #ffffff !important;
}
paper-item.suggestion.bubble.themedSmartReply.style-scope.suggestion-message {
    border-radius: 4px !important;
    background-color: #f5f7f8 !important;
    border-color: #f5f7f8 !important;
}

paper-item.suggestion.bubble.themedSmartReply.style-scope.suggestion-message:hover {
    background-color: #cfd8dc !important;
    border-color: #cfd8dc !important;
}

paper-item.suggestion.bubble.themedSmartReply.style-scope.suggestion-message:focus {
    background-color: #c0c8cb !important;
    border-color: #c0c8cb !important;
}

div.themedOutgoingBubble.bubble.style-scope.user-text-message {
    border-radius: 4px !important;
    background-color: #cfd8dc !important;
    color: #263238 !important;
}

/* conversation background */
scrolling-messages-list.style-scope.messages-view {
    background-color: #eceff1 !important;
/*[[CONVOBG]]*/
}

p.style-scope.suggestion-message {
    color: #263238 !important;
}
div.style-scope.compose-bar {
    border-radius: 4px !important;
}
div.style-scope.user-media-message {
    border-radius: 4px !important;
}

.conversationHeader.wireball-app {
    border-left: 1px rgb(38, 50, 56) !important;
    box-shadow: 0 4px 6px -3px rgba(0, 0, 0, 0) !important;
    background: var(--ate-color) !important;
    color: #fafafa !important;
}

.themedDeliveryIconRead {
    fill: #afb1b2 !important;
    stroke: #afb1b2 !important;
}

.iron-icon.message-status {
    fill: #afb1b2 !important;
    stroke: #afb1b2 !important;  
    
}

.themedSendButton.active {
    color: var(--ate-color) !important;
}

div.flex.style-scope.wireball-app {
    color: #fafafa !important;
}

#conversationName {
    background-color: var(--ate-color)
}

/* new chat button */

#startChatButtonIcon.wireball-app {
    background: var(--ate-color) !important;
}

#startChatButtonIcon.wireball-app:hover {
    box-shadow: 0 6px 10px 0 rgba(0,0,0,0.3) !important;
}
#contactSelectionContainer.wb-new-conversation-view {
    border-bottom: 2px solid var(--ate-color) !important;
}

#conversationBackButton #icon {
    background-color: var(--ate-color) !important;
}

div.style-scope.app-header > app-toolbar::before {
    content: "";
    width: 100%;
    height: 4px;
    background: linear-gradient(rgba(0,0,0,.2), rgba(0,0,0,0));
    position: absolute;
    z-index: 997;
    top: 68px;
    left: 0;
    pointer-events: none;
}

div.style-scope.app-header > app-toolbar {
        background-image: url(data:image/svg+xml;base64,PHN2ZyBmaWxsPSIjRkZGRkZGIiBoZWlnaHQ9IjI0IiB2aWV3Qm94PSIwIDAgMjQgMjQiIHdpZHRoPSIyNCIgeG1sbnM9Imh0dHA6Ly93d3cudzMub3JnLzIwMDAvc3ZnIj4gICAgPHBhdGggZD0iTTAgMGgyNHYyNEgweiIgZmlsbD0ibm9uZSIvPiAgICA8cGF0aCBkPSJNMyAxOGgxOHYtMkgzdjJ6bTAtNWgxOHYtMkgzdjJ6bTAtN3YyaDE4VjZIM3oiLz48L3N2Zz4=) !important;
    background-repeat: no-repeat !important;
    background-position: 29px 22px !important; 
    border-right: 0 rgb(38, 50, 56) !important;
    background-color: var(--ate-color) !important;
    padding-right: 2px !important;
    width: calc(100% + 1px);
}

#conversationsViewContainer .app-header {
}

#allo-logo.wireball-app {
    --iron-icon-fill-color: #fafafa !important;
    padding-left: 58px !important;
}

#arrowBack #icon {
    fill: #fafafa !important;
    background-color: var(--ate-color) !important;
}


#arrowBack #icon:hover {
    fill: #fafafa !important;
    background-color: none !important;
}

.conversation_item.selected.conversations-view, .conversation_item.conversations-view:hover {
    background-color: #eeeeee !important;
}

div.flex.style-scope.wb-new-conversation-view {
    color: #fafafa !important;
}

relative-time.themedText.timestampBanner.style-scope.messages-view {
    color: #6c6e6f !important;
}

p.themedText.senderName.style-scope.user-message {
    color: #6c6e6f !important;
}

.themedDeliveryIconDelivered {
    fill: #afb1b2 !important;
    stroke: #afb1b2 !important;
}

.message-status {
    fill: #afb1b2 !important;
}


paper-tabs.wb-emoji-picker {
    --paper-tabs-selection-bar-color: var(--ate-color) !important;
}

#menu.wb-emoji-picker paper-tab.wb-emoji-picker {
    color: var(--ate-color) !important;
}

.emojiVisible.compose-bar #emojiAction.compose-bar, .stickerVisible.compose-bar #stickerAction.compose-bar, .emojiVisible.compose-bar #emojiAction.compose-bar:hover, .stickerVisible.compose-bar #stickerAction.compose-bar:hover, .emojiVisible.compose-bar #emojiAction.compose-bar:focus, .stickerVisible.compose-bar #stickerAction.compose-bar:focus {
    color: var(--ate-color) !important;
}

#popout.wb-emoji-picker {
    color: var(--ate-color) !important;
}

paper-tab.wb-sticker-picker {
    color: var(--ate-color) !important;
}

paper-tab.wb-sticker-picker {
    color: var(--ate-color) !important;
}

paper-tabs.wb-sticker-picker {
    --paper-tabs-selection-bar-color: var(--ate-color) !important;
}

#popout.wb-sticker-picker, #store.wb-sticker-picker {
    color: var(--ate-color) !important;
}

paper-tabs.style-scope.wb-emoji-picker {
    background-color: #f8f8f8 !important;
}

div.style-scope.wb-emoji-picker {
    background-color: #f8f8f8 !important;
}

paper-tabs.style-scope.wb-sticker-picker {
    background-color: #f8f8f8 !important;
}

wb-sticker-picker.style-scope.compose-bar.iron-selected {
    background-color: #f8f8f8 !important;
}

div.style-scope.wb-sticker-picker {
    background-color: #f8f8f8 !important;
}


#sets.wb-sticker-set-list .actions.wb-sticker-set-list iron-icon.downloadAction.wb-sticker-set-list {
    background-color: var(--ate-color) !important;
}

#chip.wb-bot-chip {
    border-radius: 4px !important;
} 


#conversationsViewContainer .wireball-app .wireball-app #avatar {
    background-color: none !important;
    background-image: none !important;
    background-size: 20px 20px !important;
    background-repeat: no-repeat !important;
    background-position: center !important; 
    border-radius: 0 !important;
    box-shadow: none !important;
    margin-left: -394px !important;
    width: 250px !important;
}


#messageHeaderLayout .avatar-button #avatar {
    background-color: none !important;
    background-image: url(data:image/svg+xml;base64,) !important;
    background-size: 26px !important;
    background-repeat: no-repeat !important;
    width: 40px;
    transform: translateX(-30px);
}


.content.user-info-panel paper-toggle-button.user-info-panel {
    --paper-toggle-button-checked-bar-color: var(--ate-color) !important;
    --paper-toggle-button-checked-button-color: var(--ate-color) !important;
}

#messageHeaderLayout .avatar-button #avatar {
    background-color: var(--ate-color) !important;
}

.avatar-button #icon {
    visibility: hidden !important;
}

div.header.style-scope.user-info-panel {
    height: 68px !important;
}

ul.style-scope.user-info-panel:after {
    content: 'Theme by Linked Designs' !important;
}

p.themedTombstone.tombstone.style-scope.tombstone-message {
    font-size: 0 !important;
}

p.themedTombstone.tombstone.style-scope.tombstone-message:after {
    content: ' MD+ is incompatible with Allo themes!' !important;
    font-size: 12px !important;
}

h3.style-scope.wb-lightbox {
    color: #fafafa !important;
}

relative-time.style-scope.wb-lightbox {
    color: #fafafa !important;
}

#lightbox #icon {
    color: #fafafa !important;
}

div.open.style-scope.messages-view {
    box-shadow: 1px 0 4px 1px rgba(0,0,0,.12) !important;
}

paper-button.style-scope.wb-confirmation-dialog.keyboard-focus {
    color: var(--ate-color) !important;
}

paper-button.style-scope.wb-confirmation-dialog {
    color: var(--ate-color) !important;
}

paper-tab.wb-popout-sticker-picker {
    color: var(--ate-color) !important;
}

paper-tabs.wb-popout-sticker-picker {
    --paper-tabs-selection-bar-color: var(--ate-color) !important;
}

paper-tabs.wb-popout-picker {
    --paper-tabs-container_-_background: var(--ate-color) !important;
}

#copyButton.wb-invite-dialog {
    background:  var(--ate-color) !important;
}

#cancelButton.wb-invite-dialog {
    color: var(--ate-color) !important;
}

.ghp-header {
    background-color: var(--ate-color) !important;
}

#sidebar h4:nth-child(1) {
    font-size: 0 !important;
}

#sidebar h4:nth-child(1):After {
    content: ' MD+ is incompatible with Allo themes!' !important;
    font-size: 14px !important;
}

.storeActions .wb-sticker-set-list+ .wb-sticker-set-list .wb-sticker-set-list {
    fill: var(--ate-color) !important;
}

#switchButton.swap-view {
    background-color: var(--ate-color) !important;
}

#dialogFooter.swap-view {
    background-color: var(--ate-color) !important;
}

#logout.swap-view {
    color: rgb(250,250,250) !important;
}

.close {    
    background-color: var(--ate-color) !important;
}

#tooltip.conversations-view {
    border-radius: 4px !important;
}

/* Remove scrolling shadow */

app-header::before {
    box-shadow: 0 6px 10px 0 rgba(0,0,0,0) !important;
}
}

