css:
@-moz-document domain("forum.gamer.com.tw") {
.as-mes-fixed-toolbox { display: none; }

/* 連結 */
a { color: #ccc; }

/* 背景圖片 & 文字顏色 */
body {
	background: #111;
	background-image: none;
	color: #bbb; }

/* 背景 
#BH-wrapper { background-color: #111; }
*/

/********** 哈拉區 **********/
.forum_list_title span:last-child a { color: #58c; }
.forum_list:first-child { border-top: 1px solid #555; }
.forum_list {
	border-right: 1px solid #555;
	border-left: 1px solid #555;
	border-bottom: 1px dotted #555;
	background: #111; }
.forum_list:hover { background: #000; }

/********** 哈拉板 進板 **********/
.BH-lbox {
	background-color: #111;
	border: 1px solid #555; }
.FM-abox2B {
	color: #47b;
	border-bottom: 1px dotted #555; }

/********** 哈拉板 **********/

/* 熱門文章背景 */
.popular { background: #111; }
.popular .popular__item .img {
	outline: none;
	border: 1px solid #333; }

/* 分類標籤 */
.b-tags__item a { background: #333; }

/********** 標題文字顏色 **********/

/* 文章顏色 */
.b-list__main__title { color: #ccc; }
.b-list__main__title:hover { color: #7ac; }

/* 已讀文章顏色 */
.b-list__main__title:visited { color: #98d; }
.b-list__main__title:visited:hover { color: #a9e; }

/* 文章高亮 */
.b-list__main__title.is-highlight { color: #69d; }
.b-list__main__title.is-highlight:hover { color: #7ae; }


/* 頁數顏色 */
.b-list__main__pages a { color: #47b; }

/* 作者名 */
.b-list__count__user a { color: #47b; }

/* 回覆者名 */
.b-list__time__user a { color: #47b; }

/* 文章標題分類 */
.b-list__summary__sort a { color: #ddd; }
.b-list__summary__sort a:hover { color: #7ac; }

/********** 標題背景顏色 **********/

/* 置頂標題背景 */
.b-list__row.b-list__row--sticky {
	background: #111;
	border-bottom: 1px dashed #855; }

/* HOVER 置頂標題背景 */
.b-list__row.b-list__row--sticky:hover { background: #000; }

/* 文章標題背景 */
.b-list__row:nth-child(odd) { background: #111; }

/* 文章標題格行背景 */
.b-list-wrap { background: #222; }

/* HOVER 標題背景 */
.b-list__row:hover { background: #000; }

/* 換頁按鈕 */
#BH-pagebtn .no,
#BH-pagebtn .no:hover,
#BH-pagebtn .no:active {
	color: #bbb;
	background-color: #555;
	border-color: #777; }
#BH-pagebtn a {
	border: 1px solid #777;
	color: #bbb;
	background: #555; }

/********** 右側區塊 **********/

/* 新版介面 */
.c-test { display: none; }

/* 快捷選單 */
.c-quicktool { display: none; }

.BH-rbox {
	background-color: rgba(0, 0, 0, 0.05);
	border: 1px solid #555; }
.FM-rbox14-master { border-bottom: 1px solid #555; }
.FM-rbox14 img { border: 1px solid #555; }
.FM-rbox16A { border-bottom: 1px solid #555; }
.FM-rbox15C a {
	background-color: #555;
	border: 1px solid #777; }
.FM-rbox15C a:hover {
	color: #FFFFFF;
	background-color: #148aa4; }
.FM-rbox8 a { color: #47b; }

/* 動漫電玩通 */
.BH-qabox1 a { color: #47b; }

/* 我要出題 */
.BH-qabox1 button { display: none; }

/* 廣場聊天室(Beta) */
.as-mes-wrapper .message-input { border-top: 1px solid #777; }
.as-mes-wrapper .message-log__header { color: #ddd; }
.as-mes-wrapper .message-input__editer {
	color: #ddd;
	background: #333; }
.as-mes-wrapper .message-input__toolbar { background: #111; }

/********** 內文 **********/

/* 作者頭像 */
.c-user__avatar { border: 1px solid #555; }

/* 標題顏色 */
.c-post__header__title { border-bottom: 1px solid #555; }

/* 作者暱稱 
.c-post__header__author .username { color: #bbb; }
*/

/* 文字顏色 
.c-article { color: #ddd; } */

/* 內文背景 */
.c-post { background: #999; }

/* 下拉標題 */
.c-menu.is-scroll .c-menu__scrolldown { background: #111; }
.c-menu__scrolldown .title { color: #ccc; }

/* 刪文 */
.c-disable { background: #322; }

/********** 內文回覆 **********/

/* 回覆文字 */
.c-reply__item .reply-content .reply-content__article { color: #ddd; }

/* 內文回覆背景 */
.c-reply { background: #222; }
.c-reply__item:hover { background: #111; }

/* 樓層 */
.c-post__header__author .floor { color: #555; }

/* 編輯時間 */
.c-post__header__info .edittime { color: #555; }

/* 輸入回覆背景 */
.c-reply__editor .reply-input {
	background: #333;
	border: 1px solid #555;
	border-radius: 4px; }
.c-reply__editor .reply-input .content-edit {
	color: #ddd;
	background-color: #333;}

/********** 文章回覆 **********/
.c-editor__input .content-edit { background: #999; }
.c-editor__input .content-edit .ql-editor { border: 1px solid #777; }
.c-editor__input .option {
	background: #333;
	text-align: right; }

/********** 精華區 **********/
.FM-stb1 {
    border: 1px solid #555;
    background-color: #222; }
.FM-stb1 tr:nth-child(odd) { background-color: #333; }
.FM-stb1 tr:hover { background-color: #111; }
}
