 */

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("die.net") {
  
:root {
  --main-bg-color: #111;
  --secondary-bg-color: #222;
  --odd-bg-color: #222;
  --even-bg-color: #1D1D1D;
  --input-bg-color: #0A0A0A;
  --button-bg-color: #333;
  --highlight-bg-color: #600;
  
  --main-fg-color: #BBB;
  --secondary-fg-color: #DDD;
  --a-fg-color: #588EBB;
  --themed-fg-color: #B64A4F;
  --error-fg-color: #BB585D;
  
  --main-border-color: #444;
  --secondary-border-color: #777;
  --error-border-color: #BB585D;
}

body, table {
  background-color: var(--main-bg-color);
	color: var(--main-fg-color);
}
a {
  color: var(--a-fg-color);
}
input {
  background-color: var(--input-bg-color) !important;
  border: 1px solid var(--main-border-color);
}
#logo a, #menu a {
  color: var(--secondary-fg-color);
}
#logo a:hover, #menu a:hover {
  background-color: #300;
}
#menu dd {
  margin-left: 0;
}
#menu input {
  background-color: var(--input-bg-color);
  border: none;
}
#menu img[alt="Back"] {
  -webkit-filter: brightness(75%);
  filter: brightness(75%);
}
#news {
  background-color: var(--secondary-bg-color);
}
.even {
  background-color: var(--even-bg-color);
}
.odd {
  background-color: var(--odd-bg-color);
}
.sidebar {
  background-color: var(--secondary-bg-color);
}
.highlight {
  background-color: var(--highlight-bg-color);
}
.error {
  border: 1px solid var(--error-border-color);
  color: var(--error-fg-color);
}
.button,
input[type=submit] {
  background-color: var(--button-bg-color);
  border: 1px solid var(--main-border-color);
}
.button:hover,
input[type=submit]:hover {
  background-color: var(--secondary-bg-color);
}

.gsc-above-wrapper-area {
  border-bottom: 1px solid var(--secondary-border-color);
}

.cse .gsc-control-cse,
.gsc-control-cse {
  background-color: var(--secondary-bg-color);
  border: 1px solid var(--secondary-bg-color);
}
.gsc-webResult.gsc-result,
.gsc-results .gsc-imageResult {
  border-color: var(--secondary-bg-color);
  background-color: var(--secondary-bg-color);
}
.gsc-webResult.gsc-result:hover,
.gsc-imageResult:hover {
  border-color: var(--secondary-bg-color);
  background-color: var(--secondary-bg-color);
}
.gsc-webResult.gsc-result.gsc-promotion:hover {
  border-color: var(--secondary-bg-color);
  background-color: var(--secondary-bg-color);
}
.gs-webResult.gs-result a.gs-title:link,
.gs-webResult.gs-result a.gs-title:link b,
.gs-imageResult a.gs-title:link,
.gs-imageResult a.gs-title:link b {
  color: var(--themed-fg-color);
}
.gs-webResult.gs-result a.gs-title:visited,
.gs-webResult.gs-result a.gs-title:visited b,
.gs-imageResult a.gs-title:visited,
.gs-imageResult a.gs-title:visited b {
  color: var(--themed-fg-color);
}
.gs-webResult.gs-result a.gs-title:hover,
.gs-webResult.gs-result a.gs-title:hover b,
.gs-imageResult a.gs-title:hover,
.gs-imageResult a.gs-title:hover b {
  color: var(--themed-fg-color);
}
.gs-webResult.gs-result a.gs-title:active,
.gs-webResult.gs-result a.gs-title:active b,
.gs-imageResult a.gs-title:active,
.gs-imageResult a.gs-title:active b {
  color: var(--themed-fg-color);
}
.gsc-cursor-page {
  color: var(--themed-fg-color);
}
a.gsc-trailing-more-results:link {
  color: var(--themed-fg-color);
}
.gs-webResult .gs-snippet,
.gs-imageResult .gs-snippet,
.gs-fileFormatType {
	color: var(--main-fg-color);
}
.gs-webResult div.gs-visibleUrl,
.gs-imageResult div.gs-visibleUrl {
  color: var(--themed-fg-color);
}
.gs-webResult div.gs-visibleUrl-short {
  color: var(--themed-fg-color);
}
.gsc-cursor-box {
    border-color: var(--main-border-color);
}
.gsc-results .gsc-cursor-box .gsc-cursor-page {
  border-color: var(--main-border-color);
  background-color: var(--secondary-bg-color);
  color: var(--themed-fg-color);
}
.gsc-results .gsc-cursor-box .gsc-cursor-current-page {
  border-color: var(--main-border-color);
  background-color: var(--secondary-bg-color);
  color: var(--themed-fg-color);
}
.gsc-completion-title {
  color: var(--themed-fg-color);
}
  
.gsc-adBlock {
  display: none;
}
}
