css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("comixide.org") {

  header {
    background: #205f8f;
}
  
  .wrapper {
    background: #f3f7fb;
  }
  
  .main {
    color: #000;
}

.main-bg-light {
    background-color: #fff;
}  
  
  .main-bg-dark {
    background-color: #dfe9f5;
}
  
  h3.title-page {
    color: #000;
    text-shadow: 0 2px 6px white;
}

  h3.title-page.second-variant {
    color: #878787;
}
  
  .pagination > li > a {
    color: #555;
}
  .one-book a {
    color: #000;
}
  
  .breadcrumbs {
    background-color: #399ae3;
    color: #e4edf5;
}

  
  .breadcrumbs a {
    color: #e4edf5;
}
  
  .container.book-page {
    background-color: #fff;
    color: #000;
}

  .reviews-block .one-review:nth-child(2n) {
    background-color: #f3f7fb;
}
  
  .up-rating {
    color: #aac8a6;
}
a.down-rating {
    color: #d7a9a9;
}
  
  .delete-book-review {
    background-color: #E0B1B9;
  }
  
  .btn-gray {
    background-color: #fff;
}
  .btn-danger {
    color: #d43f3a;
    background-color: #fff;
    border-color: #d43f3a;
}
  
  .btn-success {
    color: #4cae4c;
    background-color: #fff;
    border-color: #4cae4c;
}
  
  .rates-block {
    background-color: #dfe9f5;
}
  
  .star-rating__ico {
    color: #fff;
}
  
  .other_in_series {
    background-color: #dfe9f5;
}
  
  .dark-bg {
    background-color: #dfe9f5;
}
  
  .reviews-block {

    background-color: #dfe9f5;
}
  
  .tabs {
    color: #000;
}
  
.tabs__caption li.active {
    background-color: #f3f7fb;
}
 
.review-form button.public {
    color: #fff;
}
  
.mce-btn button {
    padding: 4px 8px;
    font-size: 14px;
    line-height: 20px;
    *line-height: 16px;
    cursor: pointer;
    color: #166cdb;
    text-align: center;
    overflow: visible;
    -webkit-appearance: none;
}

  .mce-ico {
    color: #166cdb;
  }
  
  .mce-btn button {
    color: #166cdb;
}
  
a.moderation-link {
    color: #3c91cf !important;
}

  .your-rating .your-rating-text {
    color: #555;
}

  .your-rating span {
    color: #339ae3;
    font-size: 16px;
    font-weight: 600;
}
 
  .readers-rating .rating-average {
    color: white;
}

  .preferences-block > div {
    background-color: #f3f7fb;
}

  .full-bg.rating-list > li:nth-child(2n) {
    background-color: #f3f7fb;
} 
.preferences-block {
    color: #222;
}
 
  .preferences-block .rating-list a {
    color: #337ab7;
}

  .preferences-block .rating-list a:hover {
    color: #82BdEf;
}

  .sellers a {
    color: #337ab7;
}
  .sellers a:hover {
    color: #82BdEf;
}

.best-connected-books {
    background-color: #dfe9f5;
}
 
.rating-list > li:nth-child(2n) {
    background-color: #f3f7fb;
} 
  
  .best-related-books a {
    color: #337ab7;
}
  .best-related-books a:hover {
    color: #82BdEf;
}

  .rating-average.high {
    color: white;
}  
  
  .rating-average.middle {
    
    color: white;
}
 
  .last-reviews-block.dark-bg .one-review:nth-child(2n) {
    background-color: #f3f7fb;
}

  .last-reviews-block.dark-bg .one-review:nth-child(2n+1) {
    background-color: #f3f7fb;
} 
  
  .row.person-connected-books {
    background-color: #dfe9f5;
}
  
  .rating-list {
    color: #333;
}
  
  a.white {
    color: #337ab7;
}

  a.white:hover {
  background-color: #dfe9f5;
  color: #82BdEf;
}
  
.one-review:nth-child(2n) {
    background-color: #f3f7fb;
}

  .topic-name {
    color: #333;
}
  
    .topic-name:hover {
    color: #666;
}

  .thread-icon {
    background: #f3f7fb;
    color: #333;
}

  .thread-icon:hover {
    color: #666;
}
  
  .forum-thread-list tr:nth-child(2n):not(:first-child) {
    background-color: #f3f7fb;
}

  
  .thread-pagination a {
    background-color: #cfd9e5;
}
 
  .message-body {
    border-bottom: 1px dashed #c6d0da;
}

.yellow-link {
    color: #f0ca26;
}  
 
 .send-message .btn-success {
    color: white;
  }
  
  .star-rating__input:checked ~ .star-rating__ico::before {
    color: #f6910e;
  }
 
  .your-rating-table.rating-list > li:nth-child(2n) {
    background-color: #fff;
}
 
  .last-reviews-block.light-bg .one-review:nth-child(2n) {
    background-color: #f3f7fb;
}
  
  .last-reviews-block.light-bg .one-review:nth-child(2n+1) {
    background-color: #dfe9f5;
}

  .profile_page .book_list:nth-child(2n+1) {
    background-color: #dfe9f5;
}
.user-profile__collection {
    background-color: #f3f7fb;
}
 
  .header-mobile {
    background-color: #205f8f;
}

  .search-mobile {
    background: #205f8f;
}  
}
