css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("userstyles.org") {

/* hide Ads */
.ad, #userjs-install, #userjs-ie-install, #opera-css-install, #post-install-ad { display: none !important; }

/*[[theme]]*/
}

style_setting_options.value:
/* Dark Style */
body { background: #313131 !important; color:#6d6d6d !important; }
body a { color:#6d6d6d !important; }
#main-header { background:#1c1c1c !important; color:#cbcbcb !important; }
.author-styles thead { background:#1c1c1c !important; }
#main-header a { color:white !important; }

#install { background: #1c1c1c !important; color:#6d6d6d !important; }
.install-info { background: #1c1c1c !important; color:#6d6d6d !important; }

#related, #search, #left-sidebar nav { background: #1c1c1c !important; color:#6d6d6d !important; }
