css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document regexp('https://t[s|x][0-9]+\\.travian\\.[a-z]+/.*?') {
  
  /* Structure */
  body,
  body div#background div#bodyWrapper,
  div#header,
  div#footer div#pageLinks {
    min-width: 1110px !important;
  }
  body div#background div#bodyWrapper {
    width: 70vw;
  }
  
  /* Fonts */
  div.forum table#posts td.pcontent div.text {
    font-size: 12px;
  }

  /* Sidebars */
  div.sidebar {
    min-width: 215px;
  }
  .sidebar .sidebarBox {
    width: 215px;
  }
  .sidebar .sidebarBox .sidebarBoxInnerBox .header {
    padding-top: 5px;
  }
  div.sidebar div#sidebarBoxHero .heroImageButton {
    left: calc(50% - 94px);
  }
  .sidebar .sidebarBox .sidebarBoxInnerBox .header, 
  .sidebar .sidebarBox .sidebarBoxInnerBox .content, 
  .sidebar .sidebarBox .sidebarBoxInnerBox .footer {
    padding: 10px 30px 10px 28px;
  }
  
  #sidebarBoxVillagelist .sidebarBoxInnerBox .content {
    padding-right: 25px;
    padding-left: 25px;
  }
  
  #sidebarBoxVillagelist div.sidebarBoxInnerBox div.content ul li a {
    padding: 2px 0 2px 10px;
  }
  #sidebarBoxVillagelist .content ul li a img {
    left: -4px;
  }
  .sidebar .sidebarBox.toggleable:not(#sidebarBoxVillagelist) .toggle {
    left: 97px;
  }
}
