css:
@-moz-document regexp("(http|https)://(gelbooru|youhate).(com|us)/.*") {
div#note-container
{
    color:#000 !important;
}

body
{    
	background: #000 !important; 
    color:#fff !important;
}

div#notice:not(.ui-state-error)  {
        background-color: #696969;
    border: 1px solid #ffffff;
    padding: 5px;
    text-align: center;
}

.alert-info {
    background-color: #383838;
    border-color: #ffffff;
    color: #ffffff;
}

.alert-info > a {
    color: #41c0ff;
}

div.quote {
    padding: 15px;
    text-align: justify;
    color: #e0e0e0;
    background: #232323;
    font-size: 11px;
    border: 1px dashed #8c8c8c;
}

table, table.form th {
    color: #ddd !important;
}

div.rounded_footer {
    background: #111;
}

table.highlightable td {
    border: 1px solid #EEE;
}

table.highlightable > tbody > tr:hover {
    background: #111;
}

.secondary_search
{
    background-color:#222 !important;
}

table,table.form th
{
    color:#aaa !important;
}

.thumbnail-preview
{
 	background: #000 !important;
    padding:0px !important;
    border: 1px solid #000 !important;
}

div.header,.navbar
{
    background: linear-gradient(#111,#000) !important;
}

.navbar-default .navbar-nav > .active > a, .navbar-default .navbar-nav > .active > a:hover, .navbar-default .navbar-nav > .active > a:focus,li.current-page a
{
    background:#222 !important;
    color:#fff !important;
}

input,textarea
{
    background-color:#000 !important;
    color:#fff !important;
}

input.search_list
{
    border: 1px solid #222 !important;
}


#submenu
{
    background:#111 !important;
}

.tag-type-copyright > a
{
    color:#c0f;
}

.tag-type-character > a
{
    color:#0c0;
}

.tag-type-artist > a
{
    color:#d00;
}

a
{
    color: #0af;
}

ul#tag-list
{
    border-right: 1px solid #222;
}
}
