css:
@-moz-document domain("www.youtube.com") {
body {
	background: black !important;
    color: #999 !important;
}

iframe {
	display:none;
}

ytd-app, ytd-masthead, ytd-watch {
	background: black !important;
}

#masthead-container.ytd-app {
	background: transparent !important;
}

#container.ytd-masthead {
	background: transparent !important;
}

ytd-browse[page-subtype="channels"] {
	background: transparent !important;
}

#channel-container.ytd-c4-tabbed-header-renderer, #tabs-container.ytd-c4-tabbed-header-renderer {
	background: transparent !important;
}

#channel-header.ytd-c4-tabbed-header-renderer {
	background: transparent !important;
}

#tabs-inner-container.ytd-c4-tabbed-header-renderer {
	background: black !important;
}

ytd-c4-tabbed-header-renderer[has-channel-art] .banner-visible-area.ytd-c4-tabbed-header-renderer {
	height: calc(100vw / 6.2 - 2px);
}

#backgroundFrontLayer.app-header {
	transform: translate3d(0px, 0px, 0px) !important; 
}

ytd-app[guide-hovered] {
	margin-right: 0 !important;
}

#guide-content.ytd-app {
	background: black !important;
}

#guide-icon.ytd-masthead, #guide-icon.ytd-app {
	fill: #999 !important;
}

#logo-icon.ytd-topbar-logo-renderer   #you-path {
	fill: white !important;
}

#logo-icon.ytd-topbar-logo-renderer   #tube-paths {
	fill: red !important;
}

paper-button.ytd-subscribe-button-renderer {
	border-radius: 20px;
    font-size: 10px;
    padding: 5px 10px;
}

ytd-searchbox #search-form.ytd-searchbox, ytd-searchbox #search-form.ytd-searchbox {
	background: transparent !important;
	border: 0 !important;
}

ytd-searchbox #container.ytd-searchbox {
	background: transparent !important;
	border: 0px solid #999 !important;
}

ytd-searchbox  #search-container {
	border: 0px solid #999 !important;
}

ytd-searchbox  #search-input {
	border: 0px solid #999 !important;
}

ytd-searchbox[mode=legacy] #container.ytd-searchbox {
	background: transparent !important;
	border: 1px solid #2d2d2d !important;
    box-shadow: inset 0 1px 2px #2d2d2d !important;
}

ytd-searchbox[mode=legacy] #container.ytd-searchbox, ytd-searchbox[mode=legacy-centered] #container.ytd-searchbox {
    background-color: transparent !important;
    border: 1px solid #2d2d2d !important;
    border-right: none;
    box-shadow: inset 0 1px 2px  #2d2d2d !important;
    padding: 2px 6px;
    border-radius: 2px 0 0 2px;
}

ytd-searchbox  #search-icon-legacy.ytd-searchbox {
	background: #2d2d2d !important;
	border: 0px solid #999 !important;
}

.toggle-button.paper-toggle-button {
	background: #2d2d2d !important;
    cursor: pointer;
}

.title.ytd-video-primary-info-renderer {
	color: #999 !important;
}

yt-icon {
	color: #999 !important;
}

span.yt-view-count-renderer {
	color: #999 !important;
}

#upnext.ytd-compact-autoplay-renderer {
	color: #999 !important;
}

h3.ytd-compact-video-renderer, #video-title.ytd-compact-playlist-renderer {
	color: #999 !important;
}

#byline.ytd-video-meta-block {
	color: #999 !important;
}

#metadata-line.ytd-video-meta-block span.ytd-video-meta-block {
	color: #0094FF !important;
}

paper-button.yt-next-continuation {
	background: #2d2d2d !important;
    border-radius: 20px;
    width: 100px;
}

.badge-style-type-simple.ytd-badge-supported-renderer {
	background: #2d2d2d !important;
    border-radius: 20px;
    padding-left: 10px;
    padding-right: 10px;
    color: #999;
}

.date.ytd-video-secondary-info-renderer {
    color: #0094FF;
}

a.yt-simple-endpoint.yt-formatted-string {
    color: #999;
}

a.yt-simple-endpoint.yt-formatted-string:hover {
    color: #0094FF !important;
}

#name {
    color: #0094FF !important;
}

#vote-count-middle {
	color: #999 !important;
}

#icon > svg {
    color: #999 !important;
    fill: #0094FF !important;
}

yt-formatted-string.content {
    color: #999 !important;
}

g#youtube-paths > path {
    fill: #999 !important;
}

#text {
    color: #999 !important;
}

.content.ytd-video-secondary-info-renderer {
	color: #999;
}

.count-text.ytd-comments-header-renderer {
	color: #999;
}

#author-text.yt-simple-endpoint.ytd-comment-renderer {
    color: #999;
}

#content-text.ytd-comment-renderer {
	color: #999;
}

.more-button.ytd-video-secondary-info-renderer, .less-button.ytd-video-secondary-info-renderer {
	color: #999;
}

#icon-label.yt-dropdown-menu {
	color: #999;
}

yt-icon {
	fill: #999;
}

#simplebox-placeholder.ytd-comment-simplebox-renderer {
	color: #999;
}

ytd-button-renderer #button.ytd-button-renderer {
    color: #0094FF;
}

paper-button.ytd-comment-replies-renderer, yt-next-continuation.ytd-comment-replies-renderer {
	color: #999;
}

.more-button.ytd-comment-renderer, .less-button.ytd-comment-renderer {
	color: #999;
}

#guide-section-title.ytd-guide-section-renderer {
	color: #999;
}

.title.ytd-guide-entry-renderer {
	color: #999;
}

#footer.ytd-guide-renderer  #vat-notice {
	color: #999;
}

#copyright > div {
	color: #999;
}

app-drawer.ytd-app:not([persistent]) #header.ytd-app, #sections.ytd-guide-renderer > *.ytd-guide-renderer:not(:last-child), #footer.ytd-guide-renderer  #guide-links-primary {
	border-color: #999;
}

ytd-searchbox[mode=legacy] #container.ytd-searchbox input.ytd-searchbox, input#search {
	color: #999 !important;
}

#result-count {
	color: #999 !important;
}

#title.ytd-shelf-renderer {
	color: #999 !important;
}

#video-title.yt-simple-endpoint.ytd-grid-video-renderer {
	color: #999 !important;
}

#metadata-line.ytd-grid-video-renderer span.ytd-grid-video-renderer {
	color: #999 !important;
}

#toggle.ytd-grid-renderer {
	color: #999 !important;
}

#channel-title.ytd-c4-tabbed-header-renderer {
	color: #999 !important;
}

a.yt-formatted-string {
    color: #0094FF !important;
}

.tab-content.paper-tab {
	color: #999 !important;
}

yt-formatted-string.content.ytd-channel-video-player-renderer {
	color: #999 !important;
}

#title.ytd-vertical-channel-section-renderer {
	color: #999 !important;
}

.title.ytd-mini-channel-renderer {
	color: #999 !important;
}

ytd-subscribe-button-renderer[button-style="COMPACT_GRAY"] paper-button.ytd-subscribe-button-renderer {
	background: #ff2020 !important;
    color: white !important;
}

paper-tab.iron-selected.ytd-c4-tabbed-header-renderer > div {
	color: #0094FF !important;
}

#selectionBar.paper-tabs {
	background: #0094ff !important;
}

#video-title.ytd-video-renderer {
	color: #999 !important;
}

#label-text.yt-dropdown-menu {
	color: #999 !important;
}

#video-title.ytd-grid-playlist-renderer {
	color: #999 !important;
}

#title.ytd-grid-channel-renderer {
	color: #999 !important;
}

#thumbnail-attribution.ytd-grid-channel-renderer {
	color: #0094FF !important;
}

.subheadline.ytd-channel-about-metadata-renderer {
	color: #999 !important;
}

#description.ytd-channel-about-metadata-renderer {
	color: #999 !important;
}

#details-container > table > tbody > tr > td > ytd-button-renderer.style-default[is-paper-button] {
	background: transparent !important;
}

#details-container > table > tbody > tr > td > ytd-button-renderer > a > paper-button.ytd-button-renderer {
	background: #2d2d2d !important;
    border-radius: 20px;
    padding-left: 10px;
    padding-right: 10px;
    margin-left: 10px;
    margin-right: 10px;
    font-size: 10px;
    height: 26px;
}

#right-column.ytd-channel-about-metadata-renderer > yt-formatted-string.ytd-channel-about-metadata-renderer {
	color: #999;
}

#description-container.ytd-channel-about-metadata-renderer, #details-container.ytd-channel-about-metadata-renderer, #links-container.ytd-channel-about-metadata-renderer {
	border-color: #999;
}

#right-column.ytd-channel-about-metadata-renderer > yt-formatted-string.ytd-channel-about-metadata-renderer  {
	border-color: #999;
}

#yt-masthead-container {
	background: black !important;
    border-color: #999;
}

#masthead-appbar {
	background: black !important;
    border-color: #999;
}

#appbar-nav .epic-nav-item-heading {
	color: #0094FF;
    border-color: #0094FF;
}

#appbar-nav .yt-uix-button.yt-uix-button-epic-nav-item {
	color: #999;
}

#appbar-nav .yt-uix-button.yt-uix-button-epic-nav-item:hover {
    border-color: #0094FF;
}

#upload-btn.yt-uix-button-has-icon {
	background: #999;
    opacity: 0.5;
}

#sb-button-notify, #yt-masthead-notifications-button  {
	background: #999;
    opacity: 0.5;
}

.yt-card {
	background: transparent;
}

#subscription-manager-container .empty-message, #subscription-manager-container .error-message {
	border-color: #999;
}

.yt-uix-button-primary, .yt-uix-button-primary[disabled], .yt-uix-button-primary[disabled]:hover, .yt-uix-button-primary[disabled]:active, .yt-uix-button-primary[disabled]:focus {
	border-radius: 20px;
}

.yt-uix-button-default, .yt-uix-button-default[disabled], .yt-uix-button-default[disabled]:hover, .yt-uix-button-default[disabled]:active, .yt-uix-button-default[disabled]:focus {
	background: #999;
    border-color: #999;
    border-radius: 20px;
    color: #2d2d2d;
}

body #footer-container {
	background: transparent;
    border-color: #999;
}

#footer .yt-picker {
	background: transparent;
}

.yt-picker-header {
    border-color: #999;
}

.yt-picker-header h3.yt {
	color: #999;
}

#masthead-search-terms {
	background: transparent;
    border-color: #999;
    box-shadow: inset 0 1px 2px #2d2d2d !important;
    color: #999;
}

#masthead-search-terms input {
    color: #999;
}

.yt-gb-shelf .category-header .category-title {
    color: #999;
}

.branded-page-base-bold-titles .channel-header .branded-page-header-title .branded-page-header-title-link, .branded-page-base-bold-titles .branded-page-module-title {
	color: #999;
}

.branded-page-related-channels h3 a, .branded-page-related-channels h3 {
	color: #999;
}

.yt-uix-button.yt-uix-button-subscribe-unbranded, .yt-uix-button.yt-uix-button-subscribed-unbranded {
	border-radius: 20px;
    background: #f22;
    border-color: #f22;
    color: white;
}

.yt-uix-button.yt-uix-button-subscribe-branded/*, .yt-uix-button.yt-uix-button-subscribed-branded*/ {
	border-radius: 20px 0px 0px 20px;
    background: #f22;
    border-color: #f22;
    color: white;
}

.yt-uix-button-subscribe-branded+.yt-subscription-button-subscriber-count-branded-horizontal {
	border-radius: 0px 20px 20px 0px;
    background: #f22;
    border-color: #f22;
    color: white;
}

.guide-pinned .guide-pinning-enabled #appbar-guide-menu {
    background: black !important;
    border-color: #999;
}

.guide-item {
    color: #999;
}

#title.ytd-sub-feed-selector-renderer, ytd-sub-feed-option-renderer.ytd-sub-feed-selector-renderer {
    border-color: #999;
    color: #999;
}

yt-formatted-string.ytd-sub-feed-option-renderer {
    color: #999;
}

ytd-toggle-button-renderer #button.ytd-toggle-button-renderer {
    color: #999;
}

ytd-playlist-sidebar-renderer {
	background: black !important;
}

#stats.ytd-playlist-sidebar-primary-info-renderer {
    color: #999;
}

ytd-browse[page-subtype="playlist"] ytd-two-column-browse-results-renderer.ytd-browse {
	background: black !important;
}

#video-title.ytd-playlist-video-renderer {
    color: #999;
}

#index.ytd-playlist-video-renderer {
    color: #999;
}

#content.ytd-playlist-video-renderer {
    border-color: #999;
}

.header.ytd-playlist-panel-renderer {
	background: black !important;
}

#publisher-container.ytd-playlist-panel-renderer {
	color: #999;
}

.index-message.ytd-playlist-panel-renderer {
	color: #999;
}

.playlist-items.ytd-playlist-panel-renderer {
	background: transparent;
}

#unplayableText.ytd-playlist-panel-video-renderer {
	color: #999;
}

#video-title.ytd-playlist-panel-video-renderer {
	color: #999;
}

#byline.ytd-playlist-panel-video-renderer {
	color: #999;
}

#video-title.ytd-grid-movie-renderer {
	color: #999;
}

.badge-style-type-ypc.ytd-badge-supported-renderer {
	background: #2d2d2d;
    border-radius: 20px;
    padding: 5px 20px;
}

#creator-sidebar.single-section .creator-sidebar-section h3, #creator-sidebar.single-section .creator-sidebar-section .creator-sidebar-item-link {
	color: #999;
}

#creator-sidebar .creator-sidebar-section a:hover {
	background: #0094FF;
    color: white !important;
}

.account-container {
	background: black !important;
}

.howto-promo-container {
	background: black !important;
}

.howto-circle {
	color: black;
}

#appbar-guide-menu, .guide-flyout {
	background: black !important;
}

.freedoms__copy {
	color: #999;
}

.hero__angle-container, .news__angle {
	display: none;
}

.page-header {
	background: black !important;
}

.page-header__menu-icon {
	fill: #999;
}

.freedoms__ribbon:after {
	background: #cd2014;
}

.news {
	background: black !important;
}

.news__link-text {
	color: #999;
}

.video-ads {
	display: none;
}

.ytp-sb-subscribe, a.ytp-sb-subscribe {
	border-radius: 20px;
    font-size: 10px;
    /*height: 6px;*/
    padding: 2px 10px;
}
}
