css:
@-moz-document domain("www.songfacts.com") {
.factsullist-sf .inner a {
    text-decoration: underline;
    color: #b66363;
}
.sfdetail .sfdata-holder .album-holder span {
    display: block;
    overflow: hidden;
    color: #6ccead;
}
.transformer-tabs a {
    display: inline-block;
    padding: 0 16px;
    font-weight: normal;
    line-height: 34px;
    text-decoration: none;
    color: #20094f;
    outline: none;
    position: relative;
    text-transform: uppercase;
}
.top-bar-section ul li > a {
    display: block;
    width: 100%;
    color: #191c2e;
    font-weight: normal;
    text-transform: uppercase;
    padding: 12px 15px;
    background: #999;
}
strong,
b {
    font-weight: bold;
    line-height: inherit;
}
em,
i {
    font-style: italic;
    line-height: inherit;
    color: #793c97;
}
.sfdetail-titleholder {
    padding: 10px;
    background: #4e5f78;
    border: 1px solid #fff;
    margin-bottom: 10px;
}
.sfdetail .detailby {
    text-transform: lowercase;
    color: #43fffa;
}
.sfdetail h2 {
    color: #4ec1c1;
    margin-bottom: 15px;
    overflow: hidden;
    font-size: 1em;
    display: block;
}
body,
div.inner,
li {
    background: black;
}
.factsullist-sf li:nth-child(even) .inner {
    background: grey;
    color: #063555;
}
.factsullist-sf li {
    border-left: 10px solid #862bbe;
    margin-bottom: 10px;
    overflow: hidden;
}
.sfdetail-holder {
    padding: 15px;
    background: #f9f9f9;
    border: 1px solid #fff;
    margin-bottom: 10px;
    background-color: #4d4e4e;
}
.blog-comments .comment-holder .commentext,
.artistfacts-comments .comment-holder .commentext,
.sfdetail-comments .comment-holder .commentext {
    display: block;
    color: #c16d6d;
}
/*.sfdetail-comments .comment-holder:nth-child(odd) {
    background: #24295f;
}*/

.sfdetail-comments .comment-holder:nth-child(odd) {
    background: #24295f;
}
.sfdetail-comments span.name {
    border-left: 10px solid #cd047d;
    color: yellowgreen;
}
.blog-comments h2,
.artistfacts-comments h2,
.sfdetail-comments h2 {
    margin-bottom: 15px;
    font-size: 1em;
    color: indianred;
}
}
