css:
@-moz-document url-prefix("https://macmagazine.com.br/") {
body, .compartilhamento, #single #content blockquote {
    background-color: #26272a;
}
body, #nav-posts .nav-selo {
    color: #CECECE;
}
a {
    color: #54baf7;
}
#single h1 {
    color: white;
}
#single .meta {
    color: #AAA;
}
#single hr {
    background-color: #555;
}
#menu-header li a {
    color: #CCC;
}
#single #content code {
    color: #0A0;
}
#single #content blockquote {
    border-left: 6px solid #6A6A6C;
    color: #ACACAD;
}
#single #content .atualizacao {
    background-color: #3C3E43;
    border: 2px solid #555;
}
#single #content .credito {
    border-top: 4px solid #444;
}
tr > th {
    background-color: #45474D !important;
}
tr:nth-child(odd) {
    background-color: #323337 !important;
}
.wp-caption.alignnone .wp-caption-text, .wp-caption.aligncenter .wp-caption-text, .wp-caption.alignfull .wp-caption-text, #content>p.wp-caption-text {
    border-bottom: 1px solid #888;
}
.appbox-details .appbox-devices img {
    -webkit-filter: invert(1) !important;
    filter: invert(1) !important;
}
.appbox-badge img {
    border-radius: 4px;
}
.appbox-badge p.appbox-QRcode img { 
    background-color: white;
    border-radius: 0;
}
.appbox-badge p.appbox-QRcode a {
    color: #888;
}
.appbox-badge p.appbox-price {
    color: #CECECE;
}
.appbox-badge p.appbox-price span.appbox-oldprice {
    color: #C52920;
}
.appbox-icon img.appbox-iconiOS {
    border: 1px solid rgba(0, 0, 0, 0.2);
}
#relato-erro img {
    -webkit-filter: invert(1) !important;
    filter: invert(1) !important;
}
#relato-erro {
    color: #888;
    opacity: 1;
    background-color: rgba(255, 255, 255, 0.2);
    border: 1px solid transparent;
}
#relato-erro:hover {
    background-color: white;
}
#nav-posts, #mais-posts, .bloco-especial.bloco-reviews, .bloco-especial.bloco-videos, .bloco-especial.bloco-podcast, .bloco-especial.bloco-tutoriais {
    background-color: #2D2E32;
}
#nav-posts ul li {
    background-color: #3C3E43;
}
#nav-posts ul li h2 a {
    color: #ECECEC;
}
.lista-posts ul li h2 a,
#jp-relatedposts .jp-relatedposts-items-visual h4.jp-relatedposts-post-title a {
    color: #ECECEC !important;
}
#mais-posts .lista-posts ul li h2 a {
    color: #ECECEC;
}
.lista-posts ul li p {
    color: #CCC;
}
.lista-posts ul li .meta a {
    color: #CCC;
}
.comentarios-regras {
    color: #a5b1c0;
}
#shuffle {
    opacity: 0.5;
}
}

@-moz-document regexp("https://disqus.com/embed/comments/.*macmagazinecombr.*") {
body {
    color: #ECECEC;
}
.nav-tab>a, .dropdown-toggle {
    color: #7E8798 !important;
}
.tab-conversation.active>a {
    color: #ECECEC !important;
}
.textarea-wrapper, .input--text {
    background-color: #26272a;
    border: 2px solid #c2c6cc;
}
.comment__footer .vote-down, .comment__footer a, .comment__header .vote-down, .comment__header a {
    color: #7E8798 !important;
}
.vote-down {
    border-left: 2px solid #c2c6cc;
}
.publisher-anchor-color a {
    color: #54baf7 !important;
}
}

@-moz-document url-prefix("https://w.soundcloud.com/player/?url=https%3A//api.soundcloud.com/users/23497565") {
.g-background-default {
    background-color: #222;
}
.g-shadow-inset {
    box-shadow: none;
}
.widget {
    border: 1px solid #000;
}
.g-text-shadow {
    text-shadow: none;
}
.sc-background-white {
    background-color: #000;
}
a.sc-link-medium {
    color: #888;
}
a.sc-link-dark {
    color: #CECECE;
}
a.sc-link-dark:hover, a.sc-link-medium:hover, a.sc-ministats:hover {
    color: #00a2de;
}
.waveform__layer.waveform__scene > canvas:last-child {
    -webkit-filter: invert(1) !important;
    filter: invert(1) !important;
}
.sc-media-content.soundItem__content {
    color: #AAA;
}
.soundsList__item.g-border-bottom {
    border-bottom: 1px solid #555;
}
.g-scrollbar {
    background-color: rgba(255, 255, 255, 0.5);
    box-shadow: 0 0 1px #fff;
}
.multiSoundDetails {
    background-color: #222;
}
.multiSoundDetails__text {
    color: #888;
}
.sc-border-dark-top {
    border-top: 1px solid #555;
}
.sc-border-dark-bottom {
    border-bottom: 1px solid #555;
}
.sc-button-small:before {
    -webkit-filter: invert(1) !important;
    filter: invert(1) !important;
}
.sc-button {
    border: 1px solid #909090;
    background-color: #222;
    color: #909090;
}
.sc-button-focus, .sc-button:focus, .sc-button:hover {
    color: #CCC;
    border-color: #ccc;
}
.cookiePolicy.sc-background-light {
    color: #CECECE;
    background-color: #000;
}
}
