.ab_button {
    background-color: #414141;
    background-image: -webkit-gradient(linear,left top,left bottom,from(#414141),to(#414141));
    background-image: -webkit-linear-gradient(top,#414141,#414141);
    background-image: linear-gradient(top,#414141,#414141);
    border: 1px solid #dcdcdc;
    border: 1px solid rgba(0,0,0,0.1);
    color: #444;
}

.jhp input[type="submit"], .sbdd_a input, .gbqfba {
    background-image: -webkit-gradient(linear,left top,left bottom,from(#414141),to(#414141));
    background-image: -webkit-linear-gradient(top,#414141,#414141);
    -webkit-border-radius: 2px;
    -webkit-user-select: none;
    background-color: #414141;
    border: 1px solid #414141;
}
.jhp input[type="submit"]:hover, .sbdd_a input:hover, .gbqfba:hover {
    background-image: -webkit-gradient(linear,left top,left bottom,from(#414141),to(#414141));
    background-image: -webkit-linear-gradient(top,#414141,#414141);
    -webkit-box-shadow: 0 1px 1px rgba(0,0,0,0.1);
    background-color: #f8f8f8;
    background-image: linear-gradient(top,#414141,#414141);
    background-image: -o-linear-gradient(top,#414141,#414141);
    border: 1px solid #414141;
    box-shadow: 0 1px 1px rgba(0,0,0,0.1);
    color: #aaa;
}

