css:
@-moz-document domain("yggtorrent.com") {
.navbar-inverse {
    background: linear-gradient(rgb(41, 109, 226),rgb(12, 48, 111));
}

.navbar {

    position: relative;
    min-height: 50px;
}



body {
      background: #e3e9ff;
    }

.content-box, .content-box-large {
    background: #fff;
    }

.table-striped>tbody>tr:nth-child(odd)>td, .table-striped>tbody>tr:nth-child(odd)>th {
    background-color: rgba(197, 203, 208, 0.45);
    }


.ygg-navbar-input {
    border-radius: 10px;
    background: #27262d;
}


.col-md-offset-2 {
    margin-left: 0%;
}

.sidebar .nav>li>a {
    background: linear-gradient(rgb(39, 104, 217),rgb(14, 51, 117))!important;
    }

.sidebar .nav>li>ul>li>a {
    font-size: 13px;
    line-height: 20px;
    padding: 2px;
    color: #060000;
    background: rgb(255, 255, 255);
    display: block;
    text-decoration: none;
    border-top: 0px;
    /* font-weight: bold; */
}    
    
.content-box-header {

    background: #0d3456;
    }

td.tabledata1 {
    background-color: rgb(13, 52, 86);
    padding: 5px 10px;
    border: 1px rgba(223,232,238,0.05) solid;
    color: white;
}

.content-box-large {
    padding: 60px;
    }

.page-content>.row {
    margin-left: 10%!important;
    margin-right: 10%!important;
    margin-top: 10px;
   }  
    
    }
